import React, { useEffect, useState } from 'react';
import TraineeHeader from '../components/TraineeHeader';
import TrainerCard from '../components/TrainerCard';
import VideoCard from '../components/VideoCard';
import UserActivityCard from '../components/UserActivityCard';

// ดึงข้อมูล trainer จริงจาก backend
const defaultTrainer = {
  name: '',
  picture: '',
  members: 0,
  videos: 0
};
const mockVideos = [
  { id: 1, title: 'Plank', image: 'https://images.pexels.com/photos/416778/pexels-photo-416778.jpeg', duration: '30.20', kcal: 20, score: 52, statusBtn: 'Try Again', statusColor: '#a855f7' },
  { id: 2, title: 'Pushups', image: 'https://images.pexels.com/photos/2261482/pexels-photo-2261482.jpeg', duration: '30.20', kcal: 20, score: 95, statusBtn: 'Pass', statusColor: '#fb923c' },
  { id: 3, title: 'Squats', image: 'https://images.pexels.com/photos/1552242/pexels-photo-1552242.jpeg', duration: '30.20', kcal: 20, score: null, statusBtn: 'Not Started', statusColor: '#64748b' },
  { id: 4, title: 'Yoga', image: 'https://images.pexels.com/photos/317157/pexels-photo-317157.jpeg', duration: '30.20', kcal: 20, score: null },
  { id: 5, title: 'Lunges', image: 'https://images.pexels.com/photos/3823039/pexels-photo-3823039.jpeg', duration: '30.20', kcal: 20, score: null },
];
const mockActivity = { views: 0, downloads: 0, shares: 0 };


function TraineePage() {
  const [user, setUser] = useState(null);
  const [trainer, setTrainer] = useState(defaultTrainer);
  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) setUser(JSON.parse(userData));
  }, []);

  useEffect(() => {
    // ดึง trainer ของ trainee จาก backend — ทำเมื่อ user โหลดแล้วเท่านั้น
    if (!user) return;

    const fetchTrainer = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await fetch('http://localhost:8000/my-trainer', {
          method: 'GET',
          headers: {
            'Accept': 'application/json',
            ...(token ? { 'Authorization': `Bearer ${token}` } : {})
          }
        });

        if (!res.ok) {
          console.error("/my-trainer returned non-OK status", res.status, await res.text());
          // clear trainer to default so UI doesn't show stale data
          setTrainer(defaultTrainer);
          return;
        }

        const data = await res.json();
        if (!data) {
          console.warn('/my-trainer returned empty body');
          setTrainer(defaultTrainer);
          return;
        }

        setTrainer({
          name: data.name || '',
          picture: data.profile_image || data.picture || '/user (1).png',
          members: data.members || 0,
          videos: data.videos || 0
        });
      } catch (err) {
        console.error('Failed to fetch /my-trainer', err);
        setTrainer(defaultTrainer);
      }
    };

    fetchTrainer();
  }, [user]);

  if (!user) return (<div className="dashboard-loading"><div className="loading-spinner"></div><p>กำลังโหลด...</p></div>);

  const today = new Date().toLocaleDateString('en-GB', { day: 'numeric', month: 'long', year: 'numeric' });
  return (
    <div className="dashboard" style={{background:'#f8fafc'}}>
      <TraineeHeader user={user} points={200} date={today} />
      <div style={{maxWidth:1200,margin:'0 auto',padding:'1.5rem 0'}}>
        <div style={{fontWeight:700,fontSize:'1.5rem',color:'#334155',marginBottom:'1.25rem'}}>My trainer</div>
        <TrainerCard trainer={trainer} />
        <div style={{display:'flex',gap:'2rem',alignItems:'flex-start',marginBottom:'2rem',flexWrap:'wrap'}}>
          <div style={{flex:'2 1 600px'}}>
            <VideoCard video={mockVideos[0]} onPlay={()=>{}} />
          </div>
          <div style={{flex:'1 1 220px',minWidth:220}}>
            <UserActivityCard activity={mockActivity} />
          </div>
        </div>
        <div style={{fontWeight:700,fontSize:'1.15rem',color:'#334155',marginBottom:'0.5rem'}}>Videolist (5)</div>
        <div style={{marginBottom:'0.5rem',color:'#64748b',fontWeight:600}}>Level 1 (3)</div>
        <div style={{display:'flex',gap:'1.5rem',flexWrap:'wrap',marginBottom:'1.5rem'}}>
          {mockVideos.slice(0,3).map(v => <VideoCard key={v.id} video={v} onPlay={()=>{}} />)}
        </div>
        <div style={{marginBottom:'0.5rem',color:'#64748b',fontWeight:600}}>Level 2 (2)</div>
        <div style={{display:'flex',gap:'1.5rem',flexWrap:'wrap'}}>
          {mockVideos.slice(3).map(v => <VideoCard key={v.id} video={v} onPlay={()=>{}} />)}
        </div>
      </div>
    </div>
  );
}

export default TraineePage;
