    const dy1 = point1.y - point2.y;
    const dx2 = point3.x - point2.x;
    const dy2 = point3.y - point2.y;
    
    const angle1 = Math.atan2(dy1, dx1);
    const angle2 = Math.atan2(dy2, dx2);
    let angle = Math.abs(angle1 - angle2);
    
    if (angle > Math.PI) {
      angle = 2 * Math.PI - angle;
    }
    
    return angle * (180 / Math.PI); // แปลงเป็นองศา
  }

  // เปรียบเทียบท่าด้วย AI API
  async analyzeWithAI(trainerKeypoints, traineeKeypoints) {
