        // แปลงเป็นคะแนน (ยิ่งใกล้ยิ่งคะแนนสูง)
        const maxDistance = 0.3; // ระยะห่างสูงสุดที่ยอมรับได้
        const jointScore = Math.max(0, 100 - (distance / maxDistance) * 100);
        
        totalScore += jointScore;
        validJoints++;
        
        jointComparisons[joint] = {
          distance: distance,
          score: Math.round(jointScore),
          trainerPosition: { x: trainerJoint.x, y: trainerJoint.y },
          traineePosition: { x: traineeJoint.x, y: traineeJoint.y }
