import React, { useRef, useEffect, useState } from 'react';

const RealKeypoints = ({ videoRef, isPlaying, onKeypointsDetected }) => {
  const canvasRef = useRef(null);
  const [isReady, setIsReady] = useState(false);
  const [pose, setPose] = useState(null);

  // MediaPipe Pose connections (33 landmarks)
  const POSE_CONNECTIONS = [
    // Face
    [0, 1], [1, 2], [2, 3], [3, 7], [0, 4], [4, 5], [5, 6], [6, 8],
    // Arms
    [9, 10], [11, 12], [11, 13], [13, 15], [15, 17], [15, 19], [15, 21], [17, 19],
    [12, 14], [14, 16], [16, 18], [16, 20], [16, 22], [18, 20],
    // Body
    [11, 23], [12, 24], [23, 24],
    // Legs
    [23, 25], [24, 26], [25, 27], [26, 28], [27, 29], [28, 30], [29, 31], [30, 32], [27, 31], [28, 32]
  ];

  // ‡πÉ‡∏ä‡πâ TensorFlow.js PoseNet ‡πÅ‡∏ó‡∏ô MediaPipe ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏´‡∏•‡∏µ‡∏Å‡πÄ‡∏•‡∏µ‡πà‡∏¢ CORS issues
  useEffect(() => {
    const initializePoseNet = async () => {
      try {
        console.log('üîÑ Loading TensorFlow.js PoseNet...');
        
        // ‡πÇ‡∏´‡∏•‡∏î TensorFlow.js ‡πÅ‡∏•‡∏∞ PoseNet
        if (!window.tf) {
          const script1 = document.createElement('script');
          script1.src = 'https://cdn.jsdelivr.net/npm/@tensorflow/tfjs@3.21.0/dist/tf.min.js';
          document.head.appendChild(script1);
          
          await new Promise((resolve) => {
            script1.onload = resolve;
          });
        }

        if (!window.posenet) {
          const script2 = document.createElement('script');
          script2.src = 'https://cdn.jsdelivr.net/npm/@tensorflow-models/posenet@2.2.2/dist/posenet.min.js';
          document.head.appendChild(script2);
          
          await new Promise((resolve) => {
            script2.onload = resolve;
          });
        }

        console.log('üì¶ Loading PoseNet model...');
        const net = await window.posenet.load({
          architecture: 'MobileNetV1',
          outputStride: 16,
          inputResolution: { width: 640, height: 480 },
          multiplier: 0.75
        });

        setPoseNet(net);
        setIsReady(true);
        console.log('‚úÖ PoseNet loaded successfully!');

      } catch (error) {
        console.error('‚ùå Failed to load PoseNet:', error);
      }
    };

    initializePoseNet();
  }, []);

  const drawPose = (pose, canvas) => {
    const ctx = canvas.getContext('2d');
    const video = videoRef.current;
    if (!video) return;

    // ‡∏•‡πâ‡∏≤‡∏á‡∏´‡∏ô‡πâ‡∏≤‡∏à‡∏≠
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // ‡∏õ‡∏£‡∏±‡∏ö‡∏Ç‡∏ô‡∏≤‡∏î canvas ‡πÉ‡∏´‡πâ‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö‡∏ß‡∏¥‡∏î‡∏µ‡πÇ‡∏≠
    const rect = video.getBoundingClientRect();
    if (canvas.width !== rect.width || canvas.height !== rect.height) {
      canvas.width = rect.width;
      canvas.height = rect.height;
    }

    const scaleX = rect.width / video.videoWidth;
    const scaleY = rect.height / video.videoHeight;

    // ‡∏ß‡∏≤‡∏î‡∏à‡∏∏‡∏î keypoints
    pose.keypoints.forEach((keypoint, index) => {
      if (keypoint.score > 0.5) {
        const { y, x } = keypoint.position;
        
        // Mirror x-coordinate ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÉ‡∏´‡πâ‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö video ‡∏ó‡∏µ‡πà mirror ‡πÅ‡∏•‡πâ‡∏ß
        const mirroredX = rect.width - (x * scaleX);
        const scaledY = y * scaleY;

        ctx.beginPath();
        ctx.arc(mirroredX, scaledY, 4, 0, 2 * Math.PI);
        
        // ‡∏™‡∏µ‡∏ï‡πà‡∏≤‡∏á‡πÜ ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏™‡πà‡∏ß‡∏ô‡∏ï‡πà‡∏≤‡∏á‡πÜ ‡∏Ç‡∏≠‡∏á‡∏£‡πà‡∏≤‡∏á‡∏Å‡∏≤‡∏¢
        if (index <= 4) {
          ctx.fillStyle = '#FF6B6B'; // ‡∏´‡∏ô‡πâ‡∏≤
        } else if (index <= 10) {
          ctx.fillStyle = '#4ECDC4'; // ‡∏•‡∏≥‡∏ï‡∏±‡∏ß‡∏ö‡∏ô
        } else if (index <= 16) {
          ctx.fillStyle = '#45B7D1'; // ‡πÅ‡∏Ç‡∏ô
        } else {
          ctx.fillStyle = '#96CEB4'; // ‡∏Ç‡∏≤
        }
        
        ctx.fill();
        
        // ‡πÄ‡∏™‡πâ‡∏ô‡∏Ç‡∏≠‡∏ö‡∏™‡∏µ‡∏Ç‡∏≤‡∏ß
        ctx.beginPath();
        ctx.arc(mirroredX, scaledY, 4, 0, 2 * Math.PI);
        ctx.strokeStyle = '#FFFFFF';
        ctx.lineWidth = 1;
        ctx.stroke();
      }
    });

    // ‡∏ß‡∏≤‡∏î‡πÄ‡∏™‡πâ‡∏ô‡πÄ‡∏ä‡∏∑‡πà‡∏≠‡∏° (skeleton)
    const connections = [
      // ‡∏´‡∏ô‡πâ‡∏≤
      [0, 1], [1, 2], [2, 3], [3, 4],
      // ‡∏•‡∏≥‡∏ï‡∏±‡∏ß
      [5, 6], [5, 11], [6, 12], [11, 12],
      // ‡πÅ‡∏Ç‡∏ô‡∏ã‡πâ‡∏≤‡∏¢
      [5, 7], [7, 9],
      // ‡πÅ‡∏Ç‡∏ô‡∏Ç‡∏ß‡∏≤  
      [6, 8], [8, 10],
      // ‡∏Ç‡∏≤‡∏ã‡πâ‡∏≤‡∏¢
      [11, 13], [13, 15],
      // ‡∏Ç‡∏≤‡∏Ç‡∏ß‡∏≤
      [12, 14], [14, 16]
    ];

    connections.forEach(([i, j]) => {
      const kp1 = pose.keypoints[i];
      const kp2 = pose.keypoints[j];
      
      if (kp1 && kp2 && kp1.score > 0.5 && kp2.score > 0.5) {
        const x1 = rect.width - (kp1.position.x * scaleX);
        const y1 = kp1.position.y * scaleY;
        const x2 = rect.width - (kp2.position.x * scaleX);
        const y2 = kp2.position.y * scaleY;

        ctx.beginPath();
        ctx.moveTo(x1, y1);
        ctx.lineTo(x2, y2);
        ctx.strokeStyle = '#00FF00';
        ctx.lineWidth = 2;
        ctx.stroke();
      }
    });

    console.log('üéØ Pose drawn with', pose.keypoints.length, 'keypoints');
  };

  useEffect(() => {
    if (!isReady || !poseNet || !videoRef?.current || !canvasRef.current || !isPlaying) {
      return;
    }

    const video = videoRef.current;
    let animationId;

    const detectPose = async () => {
      if (video.readyState >= 2 && !video.paused) {
        try {
          const pose = await poseNet.estimateSinglePose(video, {
            flipHorizontal: false
          });

          if (pose && pose.keypoints) {
            drawPose(pose, canvasRef.current);
            
            if (onKeypointsDetected) {
              // ‡πÅ‡∏õ‡∏•‡∏á PoseNet format ‡πÄ‡∏õ‡πá‡∏ô MediaPipe format
              const mediaPipeFormat = pose.keypoints.map(kp => ({
                x: kp.position.x / video.videoWidth,
                y: kp.position.y / video.videoHeight,
                visibility: kp.score
              }));
              onKeypointsDetected(mediaPipeFormat);
            }
          }
        } catch (error) {
          console.error('Pose detection error:', error);
        }
      }

      if (isPlaying) {
        animationId = requestAnimationFrame(detectPose);
      }
    };

    detectPose();

    return () => {
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
    };
  }, [isReady, poseNet, isPlaying]);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 10
      }}
    />
  );
};

export default RealKeypoints;
