import React, { useState, useRef, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import KeypointOverlay from '../components/KeypointOverlay';
import RealTimePoseAnalyzer from '../components/RealTimePoseAnalyzer';
import SessionSummary from '../components/SessionSummary';
import { poseComparator } from '../utils/PoseComparator';
import './WorkoutPage.css';

const WorkoutPage = () => {
  const { videoId } = useParams();
  const navigate = useNavigate();
  
  // Refs for video and camera
  const trainerVideoRef = useRef(null);
  const traineeVideoRef = useRef(null);
  const traineeCanvasRef = useRef(null);
  
  // States
  const [isPlaying, setIsPlaying] = useState(false);
  const [videoData, setVideoData] = useState(null);
  const [segments, setSegments] = useState([]);
  const [currentSegment, setCurrentSegment] = useState(0);
  const [loading, setLoading] = useState(true);
  
  // Camera and keypoints states
  const [cameraStream, setCameraStream] = useState(null);
  const [traineeKeypoints, setTraineeKeypoints] = useState(null);
  const [trainerKeypoints, setTrainerKeypoints] = useState(null);
  const [cameraError, setCameraError] = useState(null);
  
  // Scoring states
  const [matchScore, setMatchScore] = useState(0);
  const [feedback, setFeedback] = useState('à¹€à¸£à¸´à¹ˆà¸¡à¸•à¹‰à¸™à¸­à¸­à¸à¸à¸³à¸¥à¸±à¸‡à¸à¸²à¸¢');
  const [isInitialized, setIsInitialized] = useState(false);
  const [bodyPartScores, setBodyPartScores] = useState({});
  const [detailedAnalysis, setDetailedAnalysis] = useState(null);
  const [confidenceLevel, setConfidenceLevel] = useState(0);
  
  // Session tracking states
  const [sessionStartTime, setSessionStartTime] = useState(null);
  const [sessionData, setSessionData] = useState({
    scores: [],
    timestamps: [],
    maxAccuracy: 0,
    bodyPartHistory: {}
  });
  const [showSessionSummary, setShowSessionSummary] = useState(false);

  // Load video data and segments
  useEffect(() => {
    const loadVideoData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:8000/videos/${videoId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        if (!response.ok) throw new Error('Failed to load video');
        
        const data = await response.json();
        console.log('ðŸ“¹ Video data loaded:', data);
        setVideoData(data);
        
        // Load segments
        const segmentsResponse = await fetch(`http://localhost:8000/videos/${videoId}/segments`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        if (segmentsResponse.ok) {
          const segmentsData = await segmentsResponse.json();
          setSegments(segmentsData.segments || []);
        }
        
      } catch (error) {
        console.error('Error loading video data:', error);
        alert('à¹„à¸¡à¹ˆà¸ªà¸²à¸¡à¸²à¸£à¸–à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸§à¸´à¸”à¸µà¹‚à¸­à¹„à¸”à¹‰');
        navigate('/trainee');
      } finally {
        setLoading(false);
      }
    };
    
    loadVideoData();
  }, [videoId, navigate]);

  // Camera permission states  
  const [cameraPermissionState, setCameraPermissionState] = useState('not-requested'); // 'not-requested', 'requesting', 'granted', 'denied'
  const [userRequestedCamera, setUserRequestedCamera] = useState(false);

  // Debug log for states
  useEffect(() => {
    console.log('WorkoutPage State:', {
      userRequestedCamera,
      cameraPermissionState,
      cameraStream: !!cameraStream,
      cameraError,
      isInitialized
    });
  }, [userRequestedCamera, cameraPermissionState, cameraStream, cameraError, isInitialized]);

  // Request camera permission function
  const requestCameraPermission = useCallback(async () => {
    console.log('ðŸŽ¥ requestCameraPermission called');
    setCameraPermissionState('requesting');
    setUserRequestedCamera(true);
    
    try {
      console.log('à¸à¸³à¸¥à¸±à¸‡à¸‚à¸­à¸­à¸™à¸¸à¸à¸²à¸•à¹ƒà¸Šà¹‰à¸à¸¥à¹‰à¸­à¸‡...');
      
      // Check if mediaDevices is supported
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error('Browser à¹„à¸¡à¹ˆà¸£à¸­à¸‡à¸£à¸±à¸šà¸à¸²à¸£à¹€à¸‚à¹‰à¸²à¸–à¸¶à¸‡à¸à¸¥à¹‰à¸­à¸‡');
      }
      
      const constraints = {
        video: { 
          width: { ideal: 640 }, 
          height: { ideal: 480 }, 
          facingMode: 'user'
        },
        audio: false
      };
      
      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      console.log('à¹€à¸›à¸´à¸”à¸à¸¥à¹‰à¸­à¸‡à¸ªà¸³à¹€à¸£à¹‡à¸ˆ:', stream);
      
      setCameraStream(stream);
      setCameraPermissionState('granted');
      setCameraError(null);
      
      console.log('âœ… Camera stream state updated');
      
    } catch (error) {
      console.error('Error accessing camera:', error);
      
      let errorMessage = 'à¹„à¸¡à¹ˆà¸ªà¸²à¸¡à¸²à¸£à¸–à¹€à¸‚à¹‰à¸²à¸–à¸¶à¸‡à¸à¸¥à¹‰à¸­à¸‡à¹„à¸”à¹‰';
      if (error.name === 'NotAllowedError') {
        errorMessage = 'à¸à¸£à¸¸à¸“à¸²à¸­à¸™à¸¸à¸à¸²à¸•à¸à¸²à¸£à¹ƒà¸Šà¹‰à¸‡à¸²à¸™à¸à¸¥à¹‰à¸­à¸‡à¹ƒà¸™à¹€à¸šà¸£à¸²à¸§à¹Œà¹€à¸‹à¸­à¸£à¹Œ';
        setCameraPermissionState('denied');
      } else if (error.name === 'NotFoundError') {
        errorMessage = 'à¹„à¸¡à¹ˆà¸žà¸šà¸à¸¥à¹‰à¸­à¸‡à¹ƒà¸™à¸­à¸¸à¸›à¸à¸£à¸“à¹Œ';
        setCameraPermissionState('denied');
      } else if (error.name === 'NotReadableError') {
        errorMessage = 'à¸à¸¥à¹‰à¸­à¸‡à¸–à¸¹à¸à¹ƒà¸Šà¹‰à¸‡à¸²à¸™à¹‚à¸”à¸¢à¹à¸­à¸›à¸žà¸¥à¸´à¹€à¸„à¸Šà¸±à¸™à¸­à¸·à¹ˆà¸™';
        setCameraPermissionState('denied');
      } else {
        setCameraPermissionState('denied');
      }
      
      setCameraError(errorMessage);
      setIsInitialized(false);
    }
  }, []);

  // Handle camera stream assignment to video element
  useEffect(() => {
    if (cameraStream && traineeVideoRef.current) {
      console.log('ðŸŽ¯ Assigning camera stream to video element');
      const videoElement = traineeVideoRef.current;
      
      // Clear any existing stream first
      if (videoElement.srcObject) {
        const existingStream = videoElement.srcObject;
        if (existingStream !== cameraStream) {
          existingStream.getTracks().forEach(track => track.stop());
        }
      }
      
      // Assign new stream
      videoElement.srcObject = cameraStream;
      
      // Force play
      videoElement.play().then(() => {
        console.log('âœ… Video playing successfully');
        setIsInitialized(true);
      }).catch(err => {
        console.error('âŒ Error playing video:', err);
      });
    }
  }, [cameraStream]);

  // Cleanup camera on unmount
  useEffect(() => {
    return () => {
      if (cameraStream) {
        cameraStream.getTracks().forEach(track => track.stop());
      }
    };
  }, [cameraStream]);

  // Handle play/pause
  const togglePlayPause = () => {
    if (trainerVideoRef.current) {
      if (isPlaying) {
        trainerVideoRef.current.pause();
      } else {
        trainerVideoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  // Handle trainer keypoints detection (would be pre-recorded data)
  const handleTrainerKeypoints = (keypoints) => {
    console.log('ðŸŽ¬ Trainer keypoints detected:', keypoints?.length || 0, 'points');
    setTrainerKeypoints(keypoints);
  };

  // Handle trainee keypoints detection (real-time)
  const handleTraineeKeypoints = (keypoints) => {
    console.log('ðŸŽ¯ Trainee keypoints detected:', keypoints?.length || 0, 'points');
    setTraineeKeypoints(keypoints);
    
    // Compare with trainer keypoints if available using new AI algorithm
    if (trainerKeypoints && keypoints) {
      console.log('ðŸ”„ Comparing poses - Trainer:', trainerKeypoints.length, 'Trainee:', keypoints.length);
      const analysis = poseComparator.getDetailedAnalysis(trainerKeypoints, keypoints);
      
      console.log('ðŸ“Š Analysis result:', {
        accuracy: analysis.accuracy,
        confidence: analysis.confidence,
        bodyParts: Object.keys(analysis.bodyPartScores).length
      });
      
      setMatchScore(analysis.accuracy);
      setFeedback(analysis.feedback);
      setBodyPartScores(analysis.bodyPartScores);
      setDetailedAnalysis(analysis);
      setConfidenceLevel(analysis.confidence);
      
      // Track session data
      trackSessionData(analysis);
    } else {
      console.log('âŒ Cannot compare:', {
        hasTrainer: !!trainerKeypoints,
        hasTrainee: !!keypoints,
        trainerLength: trainerKeypoints?.length || 0,
        traineeLength: keypoints?.length || 0
      });
    }
  };

  // Track session performance data
  const trackSessionData = (analysis) => {
    if (!sessionStartTime) {
      setSessionStartTime(Date.now());
    }

    const currentTime = Date.now();
    setSessionData(prev => ({
      ...prev,
      scores: [...prev.scores, analysis.accuracy],
      timestamps: [...prev.timestamps, currentTime],
      maxAccuracy: Math.max(prev.maxAccuracy, analysis.accuracy),
      bodyPartHistory: {
        ...prev.bodyPartHistory,
        [currentTime]: analysis.bodyPartScores
      }
    }));
  };

  // ðŸš€ Enhanced comparison using AI algorithm
  // Old comparison functions replaced by PoseComparator utility
  // All logic moved to utils/PoseComparator.js for better maintainability

  // End session and show summary
  const endSession = () => {
    if (sessionData.scores.length === 0) {
      alert('No session data to save');
      return;
    }

    const averageAccuracy = sessionData.scores.reduce((a, b) => a + b, 0) / sessionData.scores.length;
    const totalDuration = sessionStartTime ? Math.floor((Date.now() - sessionStartTime) / 1000) : 0;
    
    // Calculate average body part scores
    const avgBodyPartScores = {};
    Object.values(sessionData.bodyPartHistory).forEach(scores => {
      Object.entries(scores).forEach(([part, score]) => {
        if (!avgBodyPartScores[part]) avgBodyPartScores[part] = [];
        avgBodyPartScores[part].push(score);
      });
    });
    
    Object.keys(avgBodyPartScores).forEach(part => {
      const scores = avgBodyPartScores[part];
      avgBodyPartScores[part] = scores.reduce((a, b) => a + b, 0) / scores.length;
    });

    const summaryData = {
      exerciseName: segments.length > 0 ? segments[currentSegment]?.exercise_name : 'Chair-Assisted Squats',
      totalDuration,
      averageAccuracy,
