              </div>
            )}
          </div>
        </div>

        {/* Center: Real-Time Pose Analyzer */}
        <div className="accuracy-section">
          <RealTimePoseAnalyzer
            matchScore={matchScore}
            feedback={feedback}
            bodyPartScores={bodyPartScores}
            detailedAnalysis={detailedAnalysis}
            confidenceLevel={confidenceLevel}
            isActive={isInitialized && cameraStream && trainerKeypoints && traineeKeypoints}
          />
        </div>

        {/* Right: Trainer video */}
        <div className="trainer-panel">
          <div className="video-container trainer-container">
            <div style={{ width: '100%', height: '100%', position: 'relative' }}>
              <video
                ref={trainerVideoRef}
                src={videoData?.s3_url ? 
                  (/^https?:\/\//i.test(videoData.s3_url) 
                    ? videoData.s3_url 
                    : `http://localhost:8000/static/${videoData.s3_url.replace(/^.*[\\\/]/, '')}`
                  ) : (videoData?.image ? 
                    (/^https?:\/\//i.test(videoData.image)
                      ? videoData.image
                      : `http://localhost:8000/static/${videoData.image.replace(/^.*[\\\/]/, '')}`
                    ) : ''
