                onKeypointsDetected={handleTrainerKeypoints}
              />
            </div>
            <div className="section-label playing-label">
              <span className="trainer-indicator">ðŸŽ¯ PLAYING</span>
            </div>
          </div>
          
          {/* Enhanced Feedback box under trainer video */}
          <div className="feedback-box">
            <p className="feedback-text">
              {feedback || 'Follow the movement pattern'}
