            onClick={endSession}
            style={{
              marginLeft: '10px',
              background: 'linear-gradient(135deg, #22c55e, #16a34a)',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: '600'
            }}
          >
            ðŸ“Š End Session
          </button>
        )}
        
        {!userRequestedCamera && (
          <button 
            className="enable-camera-btn-bottom"
            onClick={requestCameraPermission}
            style={{
              marginLeft: '10px',
              background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '8px',
              cursor: 'pointer'
            }}
          >
            ï¿½ à¹€à¸›à¸´à¸”à¸à¸¥à¹‰à¸­à¸‡à¹€à¸žà¸·à¹ˆà¸­à¹€à¸£à¸´à¹ˆà¸¡à¸­à¸­à¸à¸à¸³à¸¥à¸±à¸‡à¸à¸²à¸¢
          </button>
        )}
      </div>

      {/* Session Summary Modal */}
      <SessionSummary
        sessionData={sessionData.summaryData}
        show={showSessionSummary}
        onClose={() => setShowSessionSummary(false)}
        onSaveResults={saveSessionResults}
      />
    </div>
  );
};

export default WorkoutPage;
