/**
 * üìä Session Summary Component
 * ‡πÅ‡∏™‡∏î‡∏á‡∏™‡∏£‡∏∏‡∏õ‡∏ú‡∏•‡∏Å‡∏≤‡∏£‡∏≠‡∏≠‡∏Å‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏Å‡∏≤‡∏¢‡∏´‡∏•‡∏±‡∏á‡∏à‡∏≤‡∏Å‡πÄ‡∏™‡∏£‡πá‡∏à‡∏™‡∏¥‡πâ‡∏ô session
 */

import React, { useState, useEffect } from 'react';
import { poseComparator } from '../utils/PoseComparator';
import './SessionSummary.css';

const SessionSummary = ({ 
  sessionData, 
  onClose, 
  onSaveResults,
  show = false 
}) => {
  const [animationStep, setAnimationStep] = useState(0);
  const [savedSuccessfully, setSavedSuccessfully] = useState(false);

  useEffect(() => {
    if (show) {
      // Animation sequence
      const timer1 = setTimeout(() => setAnimationStep(1), 300);
      const timer2 = setTimeout(() => setAnimationStep(2), 800);
      const timer3 = setTimeout(() => setAnimationStep(3), 1300);
      
      return () => {
        clearTimeout(timer1);
        clearTimeout(timer2);
        clearTimeout(timer3);
      };
    }
  }, [show]);

  if (!show || !sessionData) return null;

  const {
    totalDuration = 0,
    averageAccuracy = 0,
    maxAccuracy = 0,
    exerciseName = 'Unknown Exercise',
    bodyPartScores = {},
    recommendations = [],
    timestamps = []
  } = sessionData;

  const getPerformanceGrade = (accuracy) => {
    if (accuracy >= 90) return { grade: 'A+', color: '#22c55e', emoji: 'üèÜ' };
    if (accuracy >= 80) return { grade: 'A', color: '#22c55e', emoji: 'üéâ' };
    if (accuracy >= 70) return { grade: 'B+', color: '#eab308', emoji: 'üëç' };
    if (accuracy >= 60) return { grade: 'B', color: '#eab308', emoji: '‚ö°' };
    if (accuracy >= 50) return { grade: 'C+', color: '#f97316', emoji: '‚ö†Ô∏è' };
    return { grade: 'C', color: '#ef4444', emoji: 'üéØ' };
  };

  const performance = getPerformanceGrade(averageAccuracy);

  const formatDuration = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleSaveResults = async () => {
    try {
      await onSaveResults(sessionData);
      setSavedSuccessfully(true);
      setTimeout(() => {
        onClose();
      }, 2000);
    } catch (error) {
      console.error('Failed to save results:', error);
      alert('‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏ú‡∏•‡πÑ‡∏î‡πâ ‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏•‡∏≠‡∏á‡πÉ‡∏´‡∏°‡πà‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á');
    }
  };

  return (
    <div className={`session-summary-overlay ${show ? 'show' : ''}`}>
      <div className={`session-summary-modal ${animationStep >= 1 ? 'show' : ''}`}>
        
        {/* Header */}
        <div className={`summary-header ${animationStep >= 1 ? 'animate' : ''}`}>
          <div className="performance-badge">
            <span className="performance-emoji">{performance.emoji}</span>
            <div className="performance-grade" style={{ color: performance.color }}>
              {performance.grade}
            </div>
          </div>
          <h2 className="summary-title">Session Complete!</h2>
          <p className="exercise-name">{exerciseName}</p>
        </div>

        {/* Main Stats */}
        <div className={`main-stats ${animationStep >= 2 ? 'animate' : ''}`}>
          <div className="stat-card main-accuracy">
            <div className="stat-icon">üéØ</div>
            <div className="stat-value" style={{ color: performance.color }}>
              {Math.round(averageAccuracy)}%
            </div>
            <div className="stat-label">Average Accuracy</div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">‚è±Ô∏è</div>
            <div className="stat-value">{formatDuration(totalDuration)}</div>
            <div className="stat-label">Duration</div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">üìà</div>
            <div className="stat-value" style={{ color: '#22c55e' }}>
              {Math.round(maxAccuracy)}%
            </div>
            <div className="stat-label">Best Score</div>
          </div>
        </div>

        {/* Body Parts Breakdown */}
        {Object.keys(bodyPartScores).length > 0 && (
          <div className={`body-parts-breakdown ${animationStep >= 3 ? 'animate' : ''}`}>
            <h3 className="breakdown-title">Body Parts Performance</h3>
            <div className="breakdown-grid">
              {Object.entries(bodyPartScores)
                .sort(([,a], [,b]) => b - a)
                .slice(0, 6)
                .map(([part, score], index) => (
                  <div 
                    key={part} 
                    className="breakdown-item"
                    style={{ animationDelay: `${index * 0.1}s` }}
                  >
                    <div className="breakdown-header">
                      <span className="part-name">
                        {poseComparator.getBodyPartNameThai(part)}
                      </span>
                      <span 
                        className="part-score"
                        style={{ color: poseComparator.getAccuracyColor(score) }}
                      >
                        {Math.round(score)}%
                      </span>
                    </div>
                    <div className="breakdown-bar">
                      <div 
                        className="breakdown-fill"
                        style={{ 
                          width: `${score}%`,
                          backgroundColor: poseComparator.getAccuracyColor(score)
                        }}
                      ></div>
                    </div>
                  </div>
                ))
              }
            </div>
          </div>
        )}

        {/* Recommendations */}
        {recommendations.length > 0 && (
          <div className={`recommendations-section ${animationStep >= 3 ? 'animate' : ''}`}>
            <h3 className="recommendations-title">üí° Recommendations for Next Time</h3>
            <div className="recommendations-list">
              {recommendations.slice(0, 3).map((rec, index) => (
                <div key={index} className="recommendation-card">
                  <div className="rec-icon">üí™</div>
                  <div className="rec-content">
                    <div className="rec-body-part">{rec.bodyPart}</div>
                    <div className="rec-suggestion">{rec.suggestion}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Action Buttons */}
        <div className={`action-buttons ${animationStep >= 3 ? 'animate' : ''}`}>
          {!savedSuccessfully ? (
            <>
              <button 
                className="btn-secondary"
                onClick={onClose}
              >
                Skip Saving
              </button>
              <button 
                className="btn-primary"
                onClick={handleSaveResults}
              >
                Save Results
              </button>
            </>
          ) : (
            <div className="save-success">
              <span className="success-icon">‚úÖ</span>
              <span className="success-text">Results saved successfully!</span>
            </div>
          )}
        </div>

        {/* Close button */}
        <button className="close-btn" onClick={onClose}>
          √ó
        </button>
      </div>
    </div>
  );
};

export default SessionSummary;
