import React, { useRef, useEffect, useState } from 'react';

const KeypointOverlay = ({ videoRef, isPlaying, onKeypointsDetected }) => {
  const canvasRef = useRef(null);
  const [isInitialized, setIsInitialized] = useState(false);
  const [poseNet, setPoseNet] = useState(null);

  // PoseNet skeleton connections (ใช้ TensorFlow.js แทน MediaPipe)
  const POSE_CONNECTIONS = [
    // หัวและคอ
    [0, 1], [0, 2], [1, 3], [2, 4],
    // แขน
    [5, 6], [5, 7], [7, 9], [6, 8], [8, 10],
    // ลำตัว
    [5, 11], [6, 12], [11, 12],
    // ขา
    [11, 13], [12, 14], [13, 15], [14, 16]
  ];

  useEffect(() => {
    const initializePoseNet = async () => {
      try {
        // ใช้ TensorFlow.js PoseNet แทน MediaPipe
        const tf = await import('@tensorflow/tfjs');
        const posenet = await import('@tensorflow-models/posenet');
        
        const net = await posenet.load({
          architecture: 'MobileNetV1',
          outputStride: 16,
          inputResolution: { width: 640, height: 480 },
          multiplier: 0.75
        });
        
        setPoseNet(net);
        setIsInitialized(true);
      } catch (error) {
        console.error('Failed to load PoseNet:', error);
      }
    };

    initializePoseNet();
  }, []);

  useEffect(() => {
    if (!isInitialized || !poseNet || !videoRef?.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    
    const processFrame = async () => {
      if (video.paused || video.ended || !isPlaying) return;
      
      try {
        // Match canvas size to video display size
        const rect = video.getBoundingClientRect();
        canvas.width = rect.width;
        canvas.height = rect.height;
        
        // Estimate pose
        const pose = await poseNet.estimateSinglePose(video, {
          flipHorizontal: false
        });

        if (pose && pose.score > 0.3) {
          drawPose(pose, canvas, video);
          
          if (onKeypointsDetected) {
            onKeypointsDetected(pose.keypoints);
          }
        }
      } catch (error) {
        console.error('Pose detection error:', error);
      }

      if (isPlaying && !video.paused && !video.ended) {
        requestAnimationFrame(processFrame);
      }
    };

    if (isPlaying) {
      if (video.readyState >= 2) {
        processFrame();
      } else {
        video.addEventListener('loadeddata', processFrame, { once: true });
      }
    }

    // Clean up canvas when not playing
    if (!isPlaying) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
  }, [isPlaying, isInitialized, poseNet]);

  const onResults = (results) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (results.poseLandmarks) {
      // Draw skeleton connections
      drawConnectors(ctx, results.poseLandmarks, POSE_CONNECTIONS, {
        color: '#00FF00',
        lineWidth: 2
      });

      // Draw keypoints
      drawLandmarks(ctx, results.poseLandmarks, {
        color: '#FF0000',
        radius: 3
      });

      // Pass keypoints to parent component
      if (onKeypointsDetected) {
        onKeypointsDetected(results.poseLandmarks);
      }
    }
  };

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 10
      }}
    />
  );
};

export default KeypointOverlay;
