import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthLayout from '../components/auth/AuthLayout';
import AuthHeader from '../components/auth/AuthHeader';
import AuthButton from '../components/auth/AuthButton';

const ResetPassword = () => {
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (password !== confirmPassword) {
            setError('รหัสผ่านไม่ตรงกัน');
            return;
        }
        if (password.length < 8) {
            setError('รหัสผ่านต้องมีความยาวอย่างน้อย 8 ตัวอักษร');
            return;
        }

        setError('');
        setIsLoading(true);

        try {
            // API call would go here
            // const response = await api.resetPassword(password);
            
            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 1000));
            
            // Navigate to success page
            navigate('/reset-success');
        } catch (err) {
            setError('เกิดข้อผิดพลาด โปรดลองใหม่อีกครั้ง');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <AuthLayout>
            <AuthHeader 
                title="รีเซ็ตรหัสผ่าน"
                subtitle="กรุณาตั้งรหัสผ่านใหม่ของคุณ"
                linkText="เข้าสู่ระบบ"
                linkPath="/login"
                linkLabel="จำรหัสผ่านได้แล้ว?"
                isLoading={isLoading}
            />

            <form onSubmit={handleSubmit} className="auth-form">
                <div className="form-group">
                    <label htmlFor="password">รหัสผ่านใหม่</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="กรอกรหัสผ่านใหม่ (อย่างน้อย 8 ตัวอักษร)"
                        required
                        disabled={isLoading}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="confirmPassword">ยืนยันรหัสผ่านใหม่</label>
                    <input
                        type="password"
                        id="confirmPassword"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        placeholder="กรอกรหัสผ่านใหม่อีกครั้ง"
                        required
                        disabled={isLoading}
                    />
                </div>

                <div className="auth-actions">
                    <AuthButton
                        type="submit"
                        disabled={isLoading}
                    >
                        {isLoading ? 'กำลังดำเนินการ...' : 'บันทึกรหัสผ่านใหม่'}
                    </AuthButton>
                </div>
            </form>

            {error && <div className="error-message">{error}</div>}
        </AuthLayout>
    );
};

export default ResetPassword;
