import React, { useState, useRef, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import KeypointOverlay from '../components/KeypointOverlay';
import './WorkoutPage.css';

const WorkoutPage = () => {
  const { videoId } = useParams();
