import React from 'react';

import { GoogleLogin } from '@react-oauth/google';
import './GoogleAuthButton.css';

const GoogleAuthButton = ({ onSuccess, onError, isLoading, type = "login" }) => {
  // ข้อความปุ่ม fix เป็น 'ลงชื่อเข้าใช้ด้วย Google' ตามตัวอย่าง
  const buttonText = "ลงชื่อเข้าใช้ด้วย Google";
  return (
    <div className="google-auth-button">
      <GoogleLogin
        onSuccess={credentialResponse => onSuccess(credentialResponse)}
        onError={onError}
        useOneTap
        // ไม่ใช้ theme/shape ของ Google เพื่อใช้ custom ปุ่ม 100%
        render={renderProps => (
          <button
            className="google-auth-btn-custom"
            onClick={renderProps.onClick}
            disabled={isLoading || renderProps.disabled}
            type="button"
          >
            <img src="/google-icon.png" alt="Google" className="google-icon" />
            <span style={{ fontWeight: 700 }}>{buttonText}</span>
          </button>
        )}
      />
    </div>
  );
};

export default GoogleAuthButton;
