// src/pages/UploadVideo.jsx

import React, { useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import TrainerHeader from '../components/TrainerHeader';

const box = (extra = {}) => ({
  background:'#fff',
  borderRadius:16,
  boxShadow:'0 2px 12px rgba(0,0,0,0.06)',
  ...extra
});

export default function UploadVideo() {
  const navigate = useNavigate();
  const fileRef = useRef(null);

  const [file, setFile] = useState(null);
  const [fileURL, setFileURL] = useState('');
  const [name, setName] = useState('');
  const [kcal, setKcal] = useState('');
  const [level, setLevel] = useState('1');
  const [preview, setPreview] = useState(false);

  const onPick = () => fileRef.current?.click();

  const onFileChange = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    setFile(f);
    setFileURL(URL.createObjectURL(f));
  };

  const onDrop = (e) => {
    e.preventDefault();
    const f = e.dataTransfer.files?.[0];
    if (!f) return;
    setFile(f);
    setFileURL(URL.createObjectURL(f));
  };

  const onDragOver = (e) => e.preventDefault();


  const today = new Date().toLocaleDateString('en-GB', { day: 'numeric', month: 'long', year: 'numeric' });
  // ดึง user จริงจาก localStorage เพื่อแสดงโปรไฟล์
  let user = { name: 'Trainer' };
  try {
    const userData = localStorage.getItem('user');
    if (userData) {
      const userObj = JSON.parse(userData);
      user = {
        name: userObj.name || 'Trainer',
        picture: userObj.picture || '/user (1).png',
      };
    }
  } catch {}

  return (
    <div style={{
      minHeight:'100vh',
      background:'linear-gradient(135deg,#f8fafc 0%, #e2e8f0 100%)',
      fontFamily:'Inter, system-ui, sans-serif'
    }}>
  {/* Top bar replaced with TrainerHeader (full width) */}

  <TrainerHeader user={user} date={today} />

  {/* ปุ่มกลับไปหน้า TrainerPage */}
  <div style={{maxWidth:1200, margin:'0 auto', marginTop: '12px', marginBottom: '8px', display:'flex', alignItems:'center'}}>
    <button
      onClick={() => navigate('/trainer')}
      style={{
        display: 'flex',
        alignItems: 'center',
        gap: 8,
        background: 'linear-gradient(90deg,#a855f7 0%, #ff4d8b 100%)',
        color: '#fff',
        fontWeight: 700,
        border: 'none',
        borderRadius: 999,
        padding: '8px 22px 8px 16px',
        fontSize: '1.08rem',
        boxShadow: '0 2px 8px #a855f733',
        cursor: 'pointer',
        transition: 'background 0.18s, color 0.18s',
        outline: 'none',
      }}
    >
      <svg width="20" height="20" fill="none" xmlns="http://www.w3.org/2000/svg" style={{marginRight:4}}><path d="M12.5 15l-5-5 5-5" stroke="#fff" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/></svg>
      กลับหน้าหลัก
    </button>
  </div>

      {/* Main */}
      <div style={{maxWidth:1200, margin:'0 auto', padding:'8px 16px 40px 16px'}}>
        {/* Upload area */}
        <div style={box({padding:24, marginTop:8})}>
          <div
            onDrop={onDrop}
            onDragOver={onDragOver}
            style={{
              border:'2px dashed #d1d5db',
              borderRadius:16,
              padding:'36px 20px',
              textAlign:'center',
              color:'#6b7280',
              background:'#f8fafc'
            }}
          >
            <div style={{fontSize:18, fontWeight:700, color:'#111827', marginBottom:6}}>
              Select video to upload
            </div>
            <div style={{marginBottom:16}}>Or drag and drop it here</div>
            <button
              onClick={onPick}
              style={{
                background:'linear-gradient(90deg,#ff4d8b 0%, #a855f7 100%)',
                color:'#fff', fontWeight:700, border:'none', borderRadius:10,
                padding:'10px 18px', cursor:'pointer'
              }}
            >
              Select video
            </button>
            <input
              ref={fileRef}
              type="file"
              accept="video/*"
              onChange={onFileChange}
              style={{display:'none'}}
            />
            {file && (
              <div style={{marginTop:12, color:'#374151'}}>
                Selected: <b>{file.name}</b> ({Math.round(file.size/1024/1024)} MB)
              </div>
            )}
          </div>
        </div>

        {/* Info row */}
        <div style={{display:'grid',gridTemplateColumns:'repeat(4,1fr)',gap:16, marginTop:14}}>
          {[
            {title:'Size and duration',desc:'Maximum size: 30 GB, video duration: 60 minutes'},
            {title:'File formats',desc:'Recommended: “.mp4”. Other major formats are supported'},
            {title:'Video resolutions',desc:'High-resolution recommended: 1080p, 1440p, 4K.'},
            {title:'Aspect ratios',desc:'Recommended: 16:9 for landscape.'},
          ].map((item,idx)=>(
            <div key={idx} style={box({padding:'14px 16px'})}>
              <div style={{fontWeight:700, color:'#111827', marginBottom:6}}>{item.title}</div>
              <div style={{fontSize:13, color:'#6b7280', lineHeight:1.4}}>{item.desc}</div>
            </div>
          ))}
        </div>

        {/* Details */}
        <div style={{display:'grid', gridTemplateColumns:'1.1fr 1fr', gap:20, marginTop:24}}>
          {/* Left form */}
          <div style={box({padding:20})}>
            <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
              <div style={{fontSize:20,fontWeight:800,color:'#111827'}}>Details</div>
              <button
                onClick={()=>setPreview(p=>!p)}
                style={{
                  border:'1.5px solid #e5e7eb', background:'#fff',
                  color:'#a855f7', borderRadius:999, padding:'8px 14px',
                  fontWeight:700, cursor:'pointer'
                }}
              >
                Preview
              </button>
            </div>

            <div style={{marginTop:18}}>
              <label style={{display:'block',fontWeight:700,marginBottom:6}}>Name *</label>
              <input
                value={name}
                onChange={e=>setName(e.target.value)}
                placeholder="e.g., Pushups – Beginner Form"
                style={{
                  width:'97%', padding:'10px 12px', borderRadius:8,
                  border:'1px solid #e5e7eb', outline:'none'
                }}
              />
            </div>

            <div style={{marginTop:14}}>
              <label style={{display:'block',fontWeight:700,marginBottom:6}}>Kcalories *</label>
              <input
                type="number"
                min="0"
                value={kcal}
                onChange={e=>setKcal(e.target.value)}
                placeholder="e.g., 60"
                style={{
                  width:240, padding:'10px 12px', borderRadius:8,
                  border:'1px solid #e5e7eb', outline:'none'
                }}
              />
            </div>

            <div style={{marginTop:14}}>
              <label style={{display:'block',fontWeight:700,marginBottom:6}}>Level</label>
              <select
                value={level}
                onChange={(e)=>setLevel(e.target.value)}
                style={{
                  width:180, padding:'10px 12px', borderRadius:8,
                  border:'1px solid #e5e7eb', outline:'none', background:'#fff'
                }}
              >
                <option value="1">Level 1</option>
                <option value="2">Level 2</option>
                <option value="3">Level 3</option>
              </select>
            </div>

            {/* Action buttons */}
            <div style={{display:'flex',gap:12,marginTop:20}}>
              <button
                onClick={()=>alert('Public (mock)')}
                style={{
                  background:'linear-gradient(90deg,#ff4d8b 0%, #a855f7 100%)',
                  color:'#fff',fontWeight:700,border:'none',borderRadius:10,
                  padding:'10px 16px',cursor:'pointer', display:'inline-flex',alignItems:'center',gap:8
                }}
              >
                 <img src={process.env.PUBLIC_URL + '/UploadSimple.png'} alt="upload"
                     style={{width:20,height:20,marginRight:6,verticalAlign:'middle'}} />
                Public
              </button>
              <button
                onClick={()=>alert('Saved as draft (mock)')}
                style={{
                  background:'#e5e7eb',color:'#111827',fontWeight:700,border:'none',
                  borderRadius:10,padding:'10px 16px',cursor:'pointer'
                }}
              >
                Save draft
              </button>
              <button
                onClick={()=>navigate(-1)}
                style={{
                  background:'#fff',color:'#6b7280',fontWeight:700,
                  border:'1px solid #e5e7eb',borderRadius:10,padding:'10px 16px',
                  cursor:'pointer'
                }}
              >
                Discard
              </button>
            </div>
          </div>

          {/* Right preview pane */}
          <div style={box({padding:16, minHeight:300, display:'flex',alignItems:'center',justifyContent:'center'})}>
            {!fileURL || !preview ? (
              <div style={{
                width:'100%', height:320, borderRadius:12, background:'#e5e7eb',
                display:'flex',alignItems:'center',justifyContent:'center', color:'#6b7280'
              }}>
                {preview ? 'No video selected' : 'Preview area'}
              </div>
            ) : (
              <video
                src={fileURL}
                controls
                style={{width:'100%', height:320, objectFit:'cover', borderRadius:12}}
              />
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
