// ...existing imports...
import React, { useEffect, useState } from 'react';
import { startPolling } from '../utils/polling';
import '../components/Dashboard.css';
import TrainerHeader from '../components/TrainerHeader';
import { useNavigate } from 'react-router-dom';
import VideoCard from '../components/VideoCard';
import InviteTrainee from '../components/InviteTrainee';
import { useLoading } from '../contexts/LoadingProvider';
import MemberSidebar from '../components/MemberSidebar';
import VideoGrid from '../components/VideoGrid';

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:8000";

const themePurple = {
  background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)',
  minHeight: '100vh',
  fontFamily: 'Inter, sans-serif',
};

function TrainerPage() {
  // ดึง draft video จาก localStorage (เฉพาะ user นี้)
  function getDraftVideos() {
    const draft = localStorage.getItem("uploadVideoDraft");
    if (!draft) return [];
    try {
      const d = JSON.parse(draft);
      // สร้าง object ให้เหมือน video backend
      return [{
        id: "draft-local", // id เฉพาะ
        title: d.name,
        image: "", // ไม่มีไฟล์จริง
        duration: "",
        kcal: d.kcal,
        statusBtn: "Draft",
        isDraft: true,
        // เพิ่ม field อื่นๆ ได้ตามต้องการ
      }];
    } catch { return []; }
  }
  // Polling: ดึงวิดีโอใหม่ทุก 5 วินาที
  useEffect(() => {
    const stop = startPolling(() => fetchVideos(), 5000);
    return stop;
  }, []);
  // ฟังก์ชันลบวิดีโอ
  const handleDeleteVideo = async (videoId) => {
    const token = localStorage.getItem('token');
    if (!token) return;
    try {
      const res = await fetch(`http://localhost:8000/videos/${videoId}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });
      if (!res.ok) throw new Error('ลบวิดีโอไม่สำเร็จ');
      // ลบสำเร็จ: อัปเดตรายการวิดีโอ
      setVideos(videos => videos.filter(v => v.id !== videoId));
    } catch (e) {
      alert('เกิดข้อผิดพลาดในการลบวิดีโอ');
      console.error(e);
    }
  };
  const navigate = useNavigate(); // ✅ ต้องอยู่ใน component

  // State สำหรับวิดีโอจริง (ต้องอยู่บนสุด)
  const [videos, setVideos] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [profile, setProfile] = useState({ name: '', email: '', picture: '' });
  const [trainees, setTrainees] = useState([]); // ✅ รายชื่อลูกเทรนจริง
  const { withLoading, showLoading, hideLoading } = useLoading();

  const [showSidebar, setShowSidebar] = useState(true);

  // ดึงวิดีโอของ trainer (ต้องอยู่บนสุด)
  const fetchVideos = async () => {
  const token = localStorage.getItem('token');
  if (!token) return;
  try {
    const res = await fetch(`${API_BASE}/my-videos`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    if (!res.ok) throw new Error('โหลดวิดีโอไม่สำเร็จ');

    let vids = await res.json();                 // ✅ อ่านครั้งเดียว
    vids = Array.isArray(vids) ? vids.map(v => { // ✅ map รูป (ไม่ filter approved)
      let url = v.s3_url || '';
      if (url && !/^https?:\/\//i.test(url)) {
        url = `${API_BASE}/static/${url.replace(/^.*[\\\/]/, '')}`;
      }
      return { ...v, image: url };
    }) : [];

  // รวม draft video (local) กับ backend
  const allVideos = [...getDraftVideos(), ...vids];
  setVideos(allVideos);
  } catch (e) {
    console.error(e);
  }
};

  // ดึงวิดีโอเมื่อเข้าเพจ (ต้องอยู่บนสุด)
  useEffect(() => {
    fetchVideos();
  }, []);
  const [menuTraineeId, setMenuTraineeId] = useState(null);


  const [menuOpen, setMenuOpen] = useState(false);
  const [selectedTrainee, setSelectedTrainee] = useState(null);
  const [menuPos, setMenuPos] = useState({ top: 0, left: 0 });


  const openMenu = (e, trainee) => {
    e.stopPropagation();
    const r = e.currentTarget.getBoundingClientRect();
    const MENU_W = 180;
    setMenuPos({
      top: r.bottom + 8,
      left: Math.min(window.innerWidth - MENU_W - 12, r.right - MENU_W),
    });
    setSelectedTrainee(trainee);
    setMenuOpen(true);
  };

  const closeMenu = () => setMenuOpen(false);

  useEffect(() => {
    const onEsc = (e) => e.key === 'Escape' && closeMenu();
    window.addEventListener('keydown', onEsc);
    return () => window.removeEventListener('keydown', onEsc);
  }, []);




  // ✅ ดึงลูกเทรนจาก BE (อ่าน token ข้างใน ไม่ต้องรับพารามิเตอร์)
  const fetchTrainees = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;
    try {
      const res = await fetch('http://localhost:8000/my-trainees', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (!res.ok) throw new Error('โหลดรายชื่อสมาชิกไม่สำเร็จ');
      const data = await res.json();
      setTrainees(Array.isArray(data) ? data : []);
    } catch (e) {
      console.error(e);
    }
  };


  useEffect(() => {
    const close = () => setMenuTraineeId(null);
    window.addEventListener('click', close);
    return () => window.removeEventListener('click', close);
  }, []);

  const handleDelete = async (t) => {
    if (!window.confirm(`Remove ${t.name || t.email} from your team?`)) return;
    const token = localStorage.getItem("token");
    try {
      const res = await fetch(`http://localhost:8000/my-trainees/${t.id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` }
      });
      if (!res.ok && res.status !== 204) {
        const data = await res.json().catch(() => ({}));
        throw new Error(data.detail || "Delete failed");
      }
      // เอาออกจาก state ทันที หรือจะเรียก fetchTrainees() ก็ได้
      setTrainees(prev => prev.filter(x => x.id !== t.id));
      setMenuTraineeId(null);
    } catch (e) {
      alert(e.message);
    }
  };


  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoading(true);

    // ดึง user จาก localStorage
    const userData = localStorage.getItem('user');
    let userPic = '';
    let userName = '';
    if (userData) {
      try {
        const userObj = JSON.parse(userData);
        userPic = userObj.picture || '';
        userName = userObj.name || '';
      } catch { }
    }

    // เลือก trainer ของผู้ใช้ และดึงโปรไฟล์ (ตามโค้ดเดิม)
    fetch('http://localhost:8000/trainers', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(trainers => {
        let trainer = null;
        if (Array.isArray(trainers) && trainers.length > 0) {
          if (userData) {
            try {
              const userObj = JSON.parse(userData);
              trainer = trainers.find(t => t.email === userObj.email) || trainers[0];
            } catch {
              trainer = trainers[0];
            }
          } else {
            trainer = trainers[0];
          }
        }
        if (trainer) {
          // Always use backend proxy for profile image
          setProfile({
            id: trainer.id,
            name: userName || trainer.name,
            email: trainer.email,
            picture: `http://localhost:8000/profile-image/${trainer.id}`
          });
          return fetch(`http://localhost:8000/trainers/${trainer.id}`, {
            headers: { Authorization: `Bearer ${token}` }
          });
        } else {
          throw new Error('ไม่พบข้อมูลเทรนเนอร์');
        }
      })
      .then(res => res.json())
      .catch(() => setError('เกิดข้อผิดพลาดในการโหลดข้อมูลเทรนเนอร์'))
      .finally(() => setIsLoading(false));

    // ✅ โหลดลูกเทรนจริงคู่ขนาน
    fetchTrainees();
  }, []);


  if (isLoading) {
    return (
      <div className="dashboard-loading">
        <div className="running-loader">
          <div className="runner">
            <div className="head"></div>
            <div className="torso"></div>
            <div className="arm arm-right"></div>
            <div className="arm arm-left"></div>
            <div className="leg leg-right"></div>
            <div className="leg leg-left"></div>
            <div className="shadow"></div>
          </div>
          <span className="runner-text">LOADING...</span>
        </div>
      </div>

    );
  }
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="dashboard trainer-dashboard-bg" style={themePurple}>
      {/* header */}
      <TrainerHeader
        user={{ id: profile.id, name: profile.name, picture: profile.picture || '/user (1).png' }}
        date={new Date().toLocaleDateString('en-GB', { day: 'numeric', month: 'long', year: 'numeric' })}
      />

      <div style={{
        background: '#f3f4f6',
        borderRadius: 20,
        boxShadow: '0 2px 24px rgba(0,0,0,0.10)',
        maxWidth: 1400,
        minWidth: 340,
        margin: '2rem 4vw',
        padding: '2.5rem 4vw 3rem 4vw',
        minHeight: 800,
        display: 'flex',
        flexDirection: 'column',
        gap: 32
      }}>

        <div style={{ display: 'flex', gap: 40, alignItems: 'flex-start', padding: '0' }}>
          {/* Memberlist */}
          <aside style={{
            background: '#fff',
            borderRadius: 24,
            padding: '2.2rem 2.5rem',
            boxShadow: '0 2px 16px rgba(0,0,0,0.06)',
            minWidth: 340,
            maxWidth: 380,
            flex: '0 0 350px',
            display: 'flex',
            flexDirection: 'column',
            gap: 20,
            marginLeft: '38px',
            marginRight: '38px'
          }}>

            <div style={{ display: 'flex', alignItems: 'center', gap: 18, marginBottom: 10 }}>
              <img src={profile.picture} alt="avatar" style={{ width: 56, height: 56, borderRadius: '50%', objectFit: 'cover', border: '2.5px solid #e5e7eb' }} />
              <div style={{ fontWeight: 800, fontSize: '1.45rem', color: '#23272e' }}>{profile.name}</div>
              <span style={{ width: 14, height: 14, borderRadius: '50%', background: '#22c55e', display: 'inline-block', marginLeft: 8 }}></span>
            </div>

            <hr style={{ border: 'none', borderTop: '2px solid #e5e7eb', margin: '0 0 18px 0' }} />

            <div style={{ fontWeight: 700, fontSize: '1.18rem', marginBottom: 10 }}>
              Memberlist ({trainees.length})
            </div>

            {/* เชิญแล้วรีเฟรชลิสต์ */}
            <InviteTrainee onSuccess={fetchTrainees} />

            <div className="trainer-list__scroll">
              {trainees.length === 0 ? (
                <div style={{ color: '#64748b' }}>ยังไม่มีสมาชิกในทีม</div>
              ) : (
                trainees.map((t) => (
                  <div key={t.id} className="trainee-row">
                    {/* โซนหลักของแถว (คลิก = navigate) */}
                    <button
                      className="trainee-row__main"
                      onClick={() =>
                        navigate(`/trainer/trainees/${t.id}`, { state: { trainee: t } })
                      }
                    >
                      <img className="trainee-row__avatar" src={t.profile_image || "/user (1).png"} alt="avatar" />
                      <div className="trainee-row__info">
                        <div className="trainee-row__name">{t.name || 'Unnamed'}</div>
                        <div className="trainee-row__email">{t.email}</div>
                      </div>
                      <span className="trainee-row__status" />
                    </button>

                    {/* ปุ่ม ⋮ (เปิดเมนู) */}
                    <button
                      className="kebab"
                      aria-haspopup="menu"
                      aria-expanded={menuOpen && selectedTrainee?.id === t.id}
                      onClick={(e) => openMenu(e, t)}
                    >
                      ⋮
                    </button>
                  </div>
                ))
              )}

              {/* Overlay dropdown (นอก map) */}
              {menuOpen && (
                <>
                  <div className="menu-backdrop" onClick={closeMenu} />
                  <div className="row-menu row-menu--fixed" style={{ top: menuPos.top, left: menuPos.left }}>
                    <button
                      className="row-menu__item"
                      onClick={() => {
                        handleDelete(selectedTrainee);  // ใช้ข้อมูลแถวที่เลือกจริง
                        closeMenu();
                      }}
                    >
                      Delete
                    </button>
                    {/* เพิ่มเมนูอื่นได้ เช่น Suspend, View history */}
                  </div>
                </>
              )}
            </div>
          </aside>

          {/* Videolist */}
          <main style={{ flex: 1 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 22 }}>
              <div style={{ fontWeight: 700, fontSize: '1.18rem' }}>Videolist ({videos.length})</div>
              <button
                onClick={() => navigate('/trainer/upload')}
                style={{
                  background: 'linear-gradient(90deg,#ff4d8b 0%, #a855f7 100%)',
                  color: '#fff', fontWeight: 700, border: 'none', borderRadius: 18,
                  padding: '0.6rem 1.7rem', fontSize: '1.05rem', display: 'flex',
                  alignItems: 'center', gap: 9, cursor: 'pointer'
                }}
              >
                <img src={process.env.PUBLIC_URL + '/UploadSimple.png'} alt="upload"
                  style={{ width: 20, height: 20, marginRight: 6, verticalAlign: 'middle' }} />
                Upload
              </button>
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit,minmax(320px,1fr))',
              gap: '64px',
              alignItems: 'stretch',
              justifyItems: 'stretch',
              padding: '0'
            }}>
              {videos.length === 0 ? (
                <div style={{ color: '#64748b' }}>ยังไม่มีวิดีโอ</div>
              ) : (
                videos.map(v => {
                  let videoUrl = v.image || v.s3_url || '';
                  if (videoUrl && !/^https?:\/\//i.test(videoUrl)) {
                    videoUrl = `http://localhost:8000/static/${videoUrl.replace(/^.*[\\\/]/, '')}`;
                  }

                  const videoForEdit = { ...v, image: videoUrl };

                  return (
                    <VideoCard
                      key={v.id}
                      video={{
                        id: v.id,
                        title: v.title,
                        image: videoUrl,
                        duration: v.duration || '',
                        kcal: v.kcal || '',
                        statusBtn: v.statusBtn || (v.approved ? 'Active' : 'Pending'),
                        isDraft: v.isDraft,
                      }}
                      cardHeight="stretch"
                      cardMargin={4}
                      onDelete={handleDeleteVideo}
                      onCardClick={() =>
                        navigate('/trainer/upload', {
                          state: { mode: v.isDraft ? undefined : 'edit', videoId: v.id, video: videoForEdit }
                        })
                      }
                    />
                  );
                })
              )}
            </div>

            <div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: 18 }}>
              <button
                onClick={() => navigate('/trainer/videos')}
                className="trainer-morebar"
                title="Open all videos"
              >
                More
              </button>
            </div>

          </main>
        </div>
      </div>
    </div>
  );
}

export default TrainerPage;
