// ...existing code from Dashboard.js...
import React, { useEffect, useState } from 'react';
import ExternalUserModal from '../components/ExternalUserModal';
import { useNavigate } from 'react-router-dom';
import '../components/Dashboard.css';

const Dashboard = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [showExternalModal, setShowExternalModal] = useState(false);

    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (!userData) {
            navigate('/login');
            return;
        }
        const parsed = JSON.parse(userData);
        setUser(parsed);
        // ตรวจสอบ role
        const validRoles = ['admin', 'trainer', 'trainee'];
        if (!parsed.role || !validRoles.includes(parsed.role)) {
            setShowExternalModal(true);
        }
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem('user');
        navigate('/login');
    };

    if (!user) {
        return (
            <div className="dashboard-loading">
                <div className="loading-spinner"></div>
                <p>กำลังโหลด...</p>
            </div>
        );
    }

    return (
        <>
            <div className="dashboard" style={showExternalModal ? { filter: 'blur(2px)', pointerEvents: 'none', userSelect: 'none' } : {}}>
                <nav className="dashboard-nav">
                    <div className="dashboard-logo">FitAddict</div>
                    <div className="dashboard-user">
                        {user.picture && (
                            <img src={user.picture} alt="Profile" className="user-avatar" />
                        )}
                        <span className="user-name" style={{ fontWeight: 700, fontSize: "1.1rem", color: "#1e293b" }}>
                            {user.name}
                        </span>
                        <button
                            className="logout-btn"
                            style={{
                                marginLeft: "1.5rem",
                                background: "#f1f5f9",
                                color: "#64748b",
                                fontWeight: 500,
                                border: "none",
                                borderRadius: "100px",
                                padding: "0.5rem 1.25rem",
                                cursor: "pointer",
                                fontSize: "0.95rem",
                                transition: "all 0.3s"
                            }}
                            onClick={handleLogout}
                        >
                            ออกจากระบบ
                        </button>
                    </div>
                </nav>
                {/* ...rest of Dashboard code... */}
            </div>
            <ExternalUserModal
                open={showExternalModal}
                onContactAdmin={() => {
                    window.location.href = 'mailto:admin@example.com?subject=ขอสิทธิ์เข้าใช้งานระบบ';
                }}
            />
        </>
    );
};

export default Dashboard;
