import React from 'react';
import { useNavigate } from 'react-router-dom';

const AuthHeader = ({ title, subtitle, linkText, linkPath, linkLabel, isLoading }) => {
    const navigate = useNavigate();

    return (
        <div className="auth-header" style={{display:'flex',flexDirection:'column',alignItems:'center',gap:12}}>
            <img 
                src={process.env.PUBLIC_URL + '/Logo_FitAddicttest.png'} 
                alt="FitAddict Logo" 
                style={{width:72, height:72, objectFit:'contain', marginBottom:8, borderRadius:20, boxShadow:'0 4px 24px 0 #a855f733'}} 
            />
            <h2 style={{fontWeight:800, fontSize:'2rem', color:'#a855f7', marginBottom:4, letterSpacing:0.5}}>{title}</h2>
            <p className="auth-subtitle" style={{color:'#64748b', fontWeight:500, marginBottom:12, textAlign:'center'}}>{subtitle}</p>
            {/* Optional: add top link if needed */}
            {linkText && (
                <div className="auth-top-link" style={{marginTop:8}}>
                    <span>{linkLabel}</span>
                    <button 
                        onClick={() => navigate(linkPath)} 
                        className="auth-link"
                        disabled={isLoading}
                    >
                        {linkText}
                    </button>
                </div>
            )}
        </div>
    );
};

export default AuthHeader;
