import React, { useEffect, useState } from 'react';
import '../components/Dashboard.css';
import ExternalUserModal from '../components/ExternalUserModal';

function TraineePage() {
  const [user, setUser] = useState(null);
  const [showExternalModal, setShowExternalModal] = useState(false);

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      const parsed = JSON.parse(userData);
      setUser(parsed);
      if (parsed.status === 'pending' || parsed.status === 'unverified') {
        setShowExternalModal(true);
      }
    }
  }, []);

  if (!user) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>กำลังโหลด...</p>
      </div>
    );
  }

  if (showExternalModal) {
    return (
      <>
        <div className="dashboard" style={{ minHeight: '100vh', background: '#f6f8fb' }}></div>
        <ExternalUserModal
          open={showExternalModal}
          onContactAdmin={() => {
            window.location.href = 'mailto:admin@example.com?subject=ขอสิทธิ์เข้าใช้งานระบบ';
          }}
        />
      </>
    );
  }

  return (
    <div className="dashboard">
<nav className="dashboard-nav">
        <div className="dashboard-logo">FitAddict</div>
        <div className="dashboard-user">
          {user.picture && (
            <img src={user.picture} alt="Profile" className="user-avatar" />
          )}
          <span className="user-name" style={{ fontWeight: 700, fontSize: "1.1rem", color: "#1e293b" }}>
            {user.name}
          </span>
          <button
            className="logout-btn"
            style={{
              marginLeft: "1.5rem",
              background: "#f1f5f9",
              color: "#64748b",
              fontWeight: 500,
              border: "none",
              borderRadius: "100px",
              padding: "0.5rem 1.25rem",
              cursor: "pointer",
              fontSize: "0.95rem",
              transition: "all 0.3s"
            }}
            onClick={() => {
              localStorage.removeItem('user');
              window.location.href = '/login';
            }}
          >
            ออกจากระบบ
          </button>
        </div>
      </nav>
      <main className="dashboard-main">
        <div className="dashboard-welcome">
          <h1>ยินดีต้อนรับ, {user.name}!</h1>
          <p>นี่คือหน้าสำหรับ Trainee คุณสามารถเริ่มต้นออกกำลังกายและติดตามความก้าวหน้าได้ที่นี่</p>
        </div>
        <div className="dashboard-stats">
          <div className="stat-card">
            <h3>
              <span className="material-icons">flag</span>
              เป้าหมายของวันนี้
            </h3>
            <p>ตั้งเป้าหมายการออกกำลังกายของคุณ และติดตามความสำเร็จ</p>
            <button className="action-btn">ตั้งเป้าหมาย</button>
          </div>
          <div className="stat-card">
            <h3>
              <span className="material-icons">fitness_center</span>
              การออกกำลังกายล่าสุด
            </h3>
            <p>บันทึกการออกกำลังกายของคุณ และดูความก้าวหน้า</p>
            <button className="action-btn">เพิ่มการออกกำลังกาย</button>
          </div>
          <div className="stat-card">
            <h3>
              <span className="material-icons">emoji_events</span>
              ความสำเร็จ
            </h3>
            <p>ดูเหรียญรางวัลและความสำเร็จทั้งหมดของคุณ</p>
            <button className="action-btn">ดูทั้งหมด</button>
          </div>
        </div>
        <div className="quick-actions">
          <h2>เริ่มต้นใช้งาน</h2>
          <div className="action-grid">
            <button className="feature-btn">
              <span className="material-icons">add_task</span>
              <span>บันทึกการออกกำลังกาย</span>
            </button>
            <button className="feature-btn">
              <span className="material-icons">track_changes</span>
              <span>ตั้งเป้าหมายใหม่</span>
            </button>
            <button className="feature-btn">
              <span className="material-icons">bar_chart</span>
              <span>ดูสถิติ</span>
            </button>
            <button className="feature-btn">
              <span className="material-icons">groups</span>
              <span>เข้าร่วมความท้าทาย</span>
            </button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default TraineePage;
