import React, { useRef, useEffect, useState } from 'react';

const KeypointOverlay = ({ videoRef, isPlaying, onKeypointsDetected, autoPlay = true, loop = true, videoFile = null }) => {
  const canvasRef = useRef(null);
  const [isInitialized, setIsInitialized] = useState(false);
  const [pose, setPose] = useState(null);
  const [videoUrl, setVideoUrl] = useState(null);

  // MediaPipe Pose connections (33 landmarks)
  const POSE_CONNECTIONS = [
    // Face
    [0, 1], [1, 2], [2, 3], [3, 7], [0, 4], [4, 5], [5, 6], [6, 8],
    // Arms
    [9, 10], [11, 12], [11, 13], [13, 15], [15, 17], [15, 19], [15, 21], [17, 19],
    [12, 14], [14, 16], [16, 18], [16, 20], [16, 22], [18, 20],
    // Body
    [11, 23], [12, 24], [23, 24],
    // Legs
    [23, 25], [24, 26], [25, 27], [26, 28], [27, 29], [28, 30], [29, 31], [30, 32], [27, 31], [28, 32]
  ];

  useEffect(() => {
    const initializeMediaPipe = async () => {
      try {
        // โหลด MediaPipe จาก CDN
        if (!window.Pose) {
          // โหลด scripts จาก CDN
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/camera_utils/camera_utils.js');
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/control_utils/control_utils.js');
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/drawing_utils/drawing_utils.js');
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/pose/pose.js');
        }

        const poseInstance = new window.Pose({
          locateFile: (file) => {
            return `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`;
          }
        });

        poseInstance.setOptions({
          modelComplexity: 1,
          smoothLandmarks: true,
          enableSegmentation: false,
          smoothSegmentation: true,
          minDetectionConfidence: 0.5,
          minTrackingConfidence: 0.5
        });

        poseInstance.onResults(onResults);
        setPose(poseInstance);
        setIsInitialized(true);
      } catch (error) {
        console.error('Failed to load MediaPipe:', error);
      }
    };

    initializeMediaPipe();
  }, []);

  // Cleanup effect
  useEffect(() => {
    return () => {
      if (pose) {
        pose.close();
      }
    };
  }, [pose]);

  // Helper function to load scripts
  const loadScript = (src) => {
    return new Promise((resolve, reject) => {
      if (document.querySelector(`script[src="${src}"]`)) {
        resolve();
        return;
      }
      const script = document.createElement('script');
      script.src = src;
      script.onload = resolve;
      script.onerror = reject;
      document.head.appendChild(script);
    });
  };

  // Handle video file upload
  useEffect(() => {
    if (videoFile && videoRef?.current) {
      // Create object URL for the uploaded file
      const url = URL.createObjectURL(videoFile);
      setVideoUrl(url);
      
      const video = videoRef.current;
      video.src = url;
      video.load();
      
      // Set video properties immediately
      video.autoplay = autoPlay;
      video.muted = true;
      video.loop = loop;
      
      console.log('Video file loaded:', videoFile.name);
      
      // Cleanup function for this effect
      return () => {
        if (videoUrl) {
          URL.revokeObjectURL(videoUrl);
        }
        URL.revokeObjectURL(url);
      };
    }
  }, [videoFile, autoPlay, loop]);

  useEffect(() => {
    if (!isInitialized || !pose || !videoRef?.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    
    const processFrame = async () => {
      if (video.paused || video.ended || !video.videoWidth || !video.videoHeight) return;
      
      try {
        // Match canvas size to video element's display size exactly
        const rect = video.getBoundingClientRect();
        if (canvas.width !== rect.width || canvas.height !== rect.height) {
          canvas.width = rect.width;
          canvas.height = rect.height;
        }
        
        // Send frame to MediaPipe
        await pose.send({ image: video });
      } catch (error) {
        console.error('Pose detection error:', error);
      }

      // Continue processing frames as long as video is playing
      if (!video.paused && !video.ended) {
        requestAnimationFrame(processFrame);
      }
    };

    // Handle video events for auto-processing
    const handleLoadedMetadata = () => {
      console.log('Video metadata loaded:', video.videoWidth, 'x', video.videoHeight);
      const rect = video.getBoundingClientRect();
      canvas.width = rect.width;
      canvas.height = rect.height;
    };

    const handleLoadedData = () => {
      console.log('Video data loaded, starting playback');
      // Auto-start processing when video data is loaded
      if (autoPlay) {
        video.play().then(() => {
          console.log('Video playing automatically');
          processFrame();
        }).catch(error => {
          console.log('Auto-play was prevented:', error);
          processFrame();
        });
      } else {
        processFrame();
      }
    };

    const handleCanPlay = () => {
      // Start processing as soon as video can play
      if (autoPlay && video.paused) {
        video.play().catch(error => {
          console.log('Auto-play was prevented in canplay:', error);
        });
      }
      processFrame();
    };

    const handlePlay = () => {
      console.log('Video play event triggered');
      processFrame();
    };

    const handleLoadStart = () => {
      console.log('Video load start');
      // Clear canvas when new video starts loading
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    };

    const handleError = (e) => {
      console.error('Video error:', e, video.error);
    };

    // Add event listeners
    video.addEventListener('loadstart', handleLoadStart);
    video.addEventListener('loadedmetadata', handleLoadedMetadata);
    video.addEventListener('loadeddata', handleLoadedData);
    video.addEventListener('canplay', handleCanPlay);
    video.addEventListener('play', handlePlay);
    video.addEventListener('error', handleError);

    // Start processing immediately if video is already ready
    if (video.readyState >= 2 && video.videoWidth && video.videoHeight) {
      console.log('Video already ready, starting immediately');
      const rect = video.getBoundingClientRect();
      canvas.width = rect.width;
      canvas.height = rect.height;
      
      if (autoPlay && video.paused) {
        video.play().catch(error => {
          console.log('Auto-play was prevented on ready:', error);
        });
      }
      processFrame();
    }

    // Cleanup function
    return () => {
      video.removeEventListener('loadstart', handleLoadStart);
      video.removeEventListener('loadedmetadata', handleLoadedMetadata);
      video.removeEventListener('loadeddata', handleLoadedData);
      video.removeEventListener('canplay', handleCanPlay);
      video.removeEventListener('play', handlePlay);
      video.removeEventListener('error', handleError);
    };
  }, [isInitialized, pose, autoPlay, loop, videoUrl]);

  // Watch for video source changes
  useEffect(() => {
    if (!videoRef?.current) return;
    
    const video = videoRef.current;
    
    // Observer to watch for src attribute changes
    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (mutation.type === 'attributes' && mutation.attributeName === 'src') {
          // Video source changed, start processing
          if (video.src && video.readyState >= 1) {
            if (autoPlay) {
              video.play().catch(error => {
                console.log('Auto-play was prevented:', error);
              });
            }
          }
        }
      });
    });

    observer.observe(video, {
      attributes: true,
      attributeFilter: ['src']
    });

    return () => {
      observer.disconnect();
    };
  }, [videoRef, autoPlay]);

  // Additional effect to handle manual play/pause
  useEffect(() => {
    if (!videoRef?.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;

    if (!isPlaying) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
  }, [isPlaying]);

  const onResults = (results) => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    if (!canvas || !video || !results.poseLandmarks) return;

    try {
      drawPoseMediaPipe(results.poseLandmarks, canvas, video);
      
      if (onKeypointsDetected) {
        onKeypointsDetected(results.poseLandmarks);
      }
    } catch (error) {
      console.error('Drawing error:', error);
    }
  };

  const drawPoseMediaPipe = (landmarks, canvas, video) => {
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Check if video has valid dimensions
    if (!video.videoWidth || !video.videoHeight) return;

    // Get actual video dimensions and display dimensions
    const videoRect = video.getBoundingClientRect();
    const videoWidth = video.videoWidth;
    const videoHeight = video.videoHeight;
    const displayWidth = videoRect.width;
    const displayHeight = videoRect.height;

    // Prevent division by zero
    if (displayWidth === 0 || displayHeight === 0) return;

    // Calculate aspect ratios
    const videoAspect = videoWidth / videoHeight;
    const displayAspect = displayWidth / displayHeight;

    // Calculate actual video display area within the video element
    let actualVideoWidth, actualVideoHeight, offsetX = 0, offsetY = 0;

    if (videoAspect > displayAspect) {
      // Video is wider - letterboxed top/bottom
      actualVideoWidth = displayWidth;
      actualVideoHeight = displayWidth / videoAspect;
      offsetY = (displayHeight - actualVideoHeight) / 2;
    } else {
      // Video is taller - letterboxed left/right
      actualVideoHeight = displayHeight;
      actualVideoWidth = displayHeight * videoAspect;
      offsetX = (displayWidth - actualVideoWidth) / 2;
    }

    // MediaPipe landmarks are normalized (0-1), scale to actual video area
    const scaleX = actualVideoWidth;
    const scaleY = actualVideoHeight;

    // Draw skeleton connections
    POSE_CONNECTIONS.forEach(([i, j]) => {
      const kp1 = landmarks[i];
      const kp2 = landmarks[j];
      
      if (kp1 && kp2 && kp1.visibility > 0.5 && kp2.visibility > 0.5) {
        ctx.beginPath();
        ctx.moveTo(
          kp1.x * scaleX + offsetX, 
          kp1.y * scaleY + offsetY
        );
        ctx.lineTo(
          kp2.x * scaleX + offsetX, 
          kp2.y * scaleY + offsetY
        );
        ctx.strokeStyle = '#00FF00';
        ctx.lineWidth = 2;
        ctx.stroke();
      }
    });

    // Draw keypoints with different colors
    landmarks.forEach((landmark, index) => {
      if (landmark && landmark.visibility > 0.5) {
        ctx.beginPath();
        ctx.arc(
          landmark.x * scaleX + offsetX,
          landmark.y * scaleY + offsetY,
          3,
          0,
          2 * Math.PI
        );
        
        // Different colors for different body parts
        if (index <= 10) {
          ctx.fillStyle = '#FF6B6B'; // Face and upper body
        } else if (index <= 16) {
          ctx.fillStyle = '#4ECDC4'; // Arms
        } else if (index <= 22) {
          ctx.fillStyle = '#45B7D1'; // Hands
        } else {
          ctx.fillStyle = '#96CEB4'; // Legs and body
        }
        
        ctx.fill();
        
        // Add white border for better visibility
        ctx.beginPath();
        ctx.arc(
          landmark.x * scaleX + offsetX,
          landmark.y * scaleY + offsetY,
          3,
          0,
          2 * Math.PI
        );
        ctx.strokeStyle = '#FFFFFF';
        ctx.lineWidth = 1;
        ctx.stroke();
      }
    });
  };

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 10
      }}
    />
  );
};

