// WorkoutSessionPage.js - Updated to use real-time pose comparison
import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import RealTimePoseComparison from '../components/RealTimePoseComparison';
import RealTimePoseService from '../services/RealTimePoseService';
import '../styles/WorkoutSessionPage.css';
import '../styles/RealTimePoseComparison.css';

const WorkoutSessionPage = () => {
    const { videoId } = useParams();
    const navigate = useNavigate();
    
    // State management
    const [user, setUser] = useState(null);
    const [video, setVideo] = useState(null);
    const [segments, setSegments] = useState([]);
    const [currentSegment, setCurrentSegment] = useState(0);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isSessionActive, setIsSessionActive] = useState(false);
    const [sessionResults, setSessionResults] = useState([]);
    const [overallScore, setOverallScore] = useState(0);
    const [sessionStartTime, setSessionStartTime] = useState(null);
    const [sessionDuration, setSessionDuration] = useState(0);

    // Refs
    const intervalRef = useRef(null);

    // Load user data from localStorage
    useEffect(() => {
        const userData = localStorage.getItem('user');
        if (userData) {
            const userObj = JSON.parse(userData);
            setUser({
                ...userObj,
                profile_image: userObj.profile_image || userObj.picture || '/user (1).png',
                name: userObj.name || userObj.email || '',
                email: userObj.email || '',
            });
        }
    }, []);

    // Load video and segments data
    useEffect(() => {
        const loadVideoData = async () => {
            try {
                setIsLoading(true);
                
                // Load video information
                const videoResponse = await fetch(`http://localhost:8000/videos/${videoId}`, {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });

                if (!videoResponse.ok) {
                    throw new Error('Failed to load video');
                }

                const videoData = await videoResponse.json();
                setVideo(videoData);

                // Load video segments (without keypoints dependency)
                const segmentsResponse = await fetch(`http://localhost:8000/video-segments/video/${videoId}`, {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });

                if (!segmentsResponse.ok) {
                    throw new Error('Failed to load video segments');
                }

                const segmentsData = await segmentsResponse.json();
                setSegments(segmentsData);

            } catch (err) {
                console.error('Error loading video data:', err);
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        if (videoId) {
            loadVideoData();
        }
    }, [videoId]);

    // Initialize real-time pose service
    useEffect(() => {
        const initializePoseService = async () => {
            try {
                await RealTimePoseService.initialize();
                console.log('‚úÖ Pose service ready for real-time comparison');
            } catch (err) {
                console.error('‚ùå Failed to initialize pose service:', err);
                setError('Failed to initialize pose detection');
            }
        };

        initializePoseService();

        // Cleanup on unmount
        return () => {
            RealTimePoseService.cleanup();
            if (intervalRef.current) {
                clearInterval(intervalRef.current);
            }
        };
    }, []);

    // Handle session timing
    useEffect(() => {
        if (isSessionActive) {
            setSessionStartTime(Date.now());
            intervalRef.current = setInterval(() => {
                setSessionDuration(prev => prev + 1);
            }, 1000);
        } else {
            if (intervalRef.current) {
                clearInterval(intervalRef.current);
                intervalRef.current = null;
            }
        }

        return () => {
            if (intervalRef.current) {
                clearInterval(intervalRef.current);
            }
        };
    }, [isSessionActive]);

    // Start workout session
    const startSession = async () => {
        if (!user) {
            alert('Please log in to start a workout session');
            return;
        }

        try {
            setIsSessionActive(true);
            setSessionResults([]);
            setOverallScore(0);
            setSessionDuration(0);
            setCurrentSegment(0);
            
            console.log('üèÉ‚Äç‚ôÇÔ∏è Starting real-time workout session');
        } catch (err) {
            console.error('Error starting session:', err);
            setError('Failed to start workout session');
            setIsSessionActive(false);
        }
    };

    // Stop workout session
    const stopSession = async () => {
        try {
            setIsSessionActive(false);
            
            // Calculate final results
            const finalScore = sessionResults.length > 0 
                ? sessionResults.reduce((sum, result) => sum + result.similarity, 0) / sessionResults.length
                : 0;
            
            setOverallScore(finalScore);

            // Save session results to backend (optional - simplified without keypoints storage)
            if (user && user.id) {
                await saveSessionResults({
                    video_id: parseInt(videoId),
                    user_id: user.id,
                    overall_score: finalScore,
                    duration: sessionDuration,
                    segments_completed: sessionResults.length,
                    results: sessionResults.slice(-10) // Keep last 10 results
                });
            }

            console.log('‚úÖ Workout session completed');
        } catch (err) {
            console.error('Error stopping session:', err);
        }
    };

    // Save session results
    const saveSessionResults = async (sessionData) => {
        try {
            const response = await fetch('http://localhost:8000/workout-sessions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify(sessionData)
            });

            if (!response.ok) {
                console.warn('Failed to save session results to server');
            }
        } catch (err) {
            console.warn('Could not save session results:', err);
        }
    };

    // Handle pose comparison results
    const handleComparisonResult = (result) => {
        if (!isSessionActive) return;

        // Add result to session results
        const newResult = {
            timestamp: Date.now(),
            segment_id: segments[currentSegment]?.id,
            similarity: result.similarity_score,
            feedback: result.feedback,
            detailed_scores: result.detailed_scores
        };

        setSessionResults(prev => [...prev.slice(-49), newResult]); // Keep last 50 results

        // Update overall score (rolling average of last 10 results)
        setSessionResults(prev => {
            const recent = prev.slice(-10);
            const avgScore = recent.length > 0 
                ? recent.reduce((sum, r) => sum + r.similarity, 0) / recent.length 
                : 0;
            setOverallScore(avgScore);
            return prev;
        });
    };

    // Navigate to next segment
    const nextSegment = () => {
        if (currentSegment < segments.length - 1) {
            setCurrentSegment(prev => prev + 1);
        }
    };

    // Navigate to previous segment
    const previousSegment = () => {
        if (currentSegment > 0) {
            setCurrentSegment(prev => prev - 1);
        }
    };

    // Format duration
    const formatDuration = (seconds) => {
        const mins = Math.floor(seconds / 60);
        const secs = seconds % 60;
        return `${mins}:${secs.toString().padStart(2, '0')}`;
    };

    // Get current segment video URL
    const getCurrentSegmentVideoUrl = () => {
        const currentSeg = segments[currentSegment];
        if (!currentSeg || !video) return null;

        // Construct video URL (adjust based on your video storage setup)
        return `http://localhost:8000/static/${video.s3_url?.split('/').pop() || `video_${video.id}.mp4`}`;
    };

    if (isLoading) {
        return (
            <div className="workout-session-loading">
                <div className="loading-spinner"></div>
                <h2>Loading workout session...</h2>
            </div>
        );
    }

    if (error) {
        return (
            <div className="workout-session-error">
                <h2>Error</h2>
                <p>{error}</p>
                <button onClick={() => navigate('/videos')} className="back-button">
                    Back to Videos
                </button>
            </div>
        );
    }

    if (!video || segments.length === 0) {
        return (
            <div className="workout-session-error">
                <h2>No workout data found</h2>
                <p>This video doesn't have any exercise segments configured.</p>
                <button onClick={() => navigate('/videos')} className="back-button">
                    Back to Videos
                </button>
            </div>
        );
    }

    const currentSeg = segments[currentSegment];

    return (
        <div className="workout-session-page">
            {/* Top Header Bar */}
            <div className="top-header">
                <h1 className="exercise-title">{currentSeg?.exercise_name || video.title}</h1>
                <div className="exercise-status">Excellent!</div>
                <div className="update-button">Update Exercise UI</div>
            </div>
            
            {/* Session Timer and Progress */}
            <div className="session-timer">
                <div className="timer-display">
                    <span className="time-current">{formatDuration(sessionDuration)}:</span>
                    <span className="time-total">{currentSeg ? Math.ceil(currentSeg.end_time - currentSeg.start_time) : 0}</span>
                </div>
                <div className="progress-indicator">
                    <div className="progress-bar-main">
                        <div className="progress-fill-main" style={{
                            width: `${(sessionDuration / (currentSeg ? currentSeg.end_time - currentSeg.start_time : 1)) * 100}%`
                        }}></div>
                    </div>
                    <span className="progress-fraction">9/15</span>
                </div>
            </div>

            {/* Main Workout Area - ‡πÄ‡∏´‡∏°‡∏∑‡∏≠‡∏ô‡πÉ‡∏ô‡∏£‡∏π‡∏õ */}
            <div className="main-workout-container">
                <RealTimePoseComparison
                    trainerVideoUrl={getCurrentSegmentVideoUrl()}
                    onComparisonResult={handleComparisonResult}
                    isActive={isSessionActive}
                    showTrainerPose={true}
                />
                
                {/* Control Bar */}
                <div className="workout-controls">
                    <div className="control-left">
                        {!isSessionActive ? (
                            <button onClick={startSession} className="control-button start-btn">
                                ‚ñ∂Ô∏è Start
                            </button>
                        ) : (
                            <button onClick={stopSession} className="control-button pause-btn">
                                ‚è∏Ô∏è Pause
                            </button>
                        )}
                    </div>
                    <div className="control-center">
                        <span className="exercise-name">{currentSeg?.exercise_name || 'Chair-Assisted Squats'}</span>
                    </div>
                    <div className="control-right">
                        <button onClick={() => navigate('/videos')} className="control-button back-btn">
                            ‚Üê Back
                        </button>
                    </div>
                </div>
            </div>

            {/* Segment Navigation */}
            <div className="segment-navigation">
                <button 
                    onClick={previousSegment}
                    disabled={currentSegment === 0}
                    className="nav-button prev-button"
                >
                    ‚Üê Previous
                </button>
                
                <div className="segment-indicator">
                    {segments.map((_, index) => (
                        <div
                            key={index}
                            className={`segment-dot ${index === currentSegment ? 'active' : ''}`}
                            onClick={() => setCurrentSegment(index)}
                        />
                    ))}
                </div>
                
                <button 
                    onClick={nextSegment}
                    disabled={currentSegment === segments.length - 1}
                    className="nav-button next-button"
                >
                    Next ‚Üí
                </button>
            </div>

            {/* Session Results Summary */}
            {sessionResults.length > 0 && (
                <div className="session-results-summary">
                    <h3>üìà Session Progress</h3>
                    <div className="results-stats">
                        <div className="stat">
                            <span className="stat-label">Average Score</span>
                            <span className="stat-value">{(overallScore * 100).toFixed(1)}%</span>
                        </div>
                        <div className="stat">
                            <span className="stat-label">Total Measurements</span>
                            <span className="stat-value">{sessionResults.length}</span>
                        </div>
                        <div className="stat">
                            <span className="stat-label">Best Score</span>
                            <span className="stat-value">
                                {Math.max(...sessionResults.map(r => r.similarity * 100)).toFixed(1)}%
                            </span>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default WorkoutSessionPage;
