  // Initialize camera function
  const initCamera = async () => {
    try {
      console.log('üé• ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÄ‡∏õ‡∏¥‡∏î‡∏Å‡∏•‡πâ‡∏≠‡∏á...');
