      console.log('üìã Requesting camera permission...');
      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      console.log('‚úÖ ‡πÄ‡∏õ‡∏¥‡∏î‡∏Å‡∏•‡πâ‡∏≠‡∏á‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à!', stream);
      
