        traineeVideoRef.current.srcObject = stream;
        traineeVideoRef.current.muted = true;
        traineeVideoRef.current.autoplay = true;
        traineeVideoRef.current.playsInline = true;
        
        traineeVideoRef.current.onloadedmetadata = () => {
          traineeVideoRef.current.play().then(() => {
            console.log('üé¨ Camera video playing successfully');
            setIsInitialized(true);
          }).catch(err => {
            console.error('‚ùå Error playing camera stream:', err);
          });
        };
      }
      
    } catch (error) {
      console.error('‚ùå Camera error:', error);
      
      let errorMessage = '‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÄ‡∏Ç‡πâ‡∏≤‡∏ñ‡∏∂‡∏á‡∏Å‡∏•‡πâ‡∏≠‡∏á‡πÑ‡∏î‡πâ';
      if (error.name === 'NotAllowedError') {
        errorMessage = '‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏≠‡∏ô‡∏∏‡∏ç‡∏≤‡∏ï‡∏Å‡∏≤‡∏£‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô‡∏Å‡∏•‡πâ‡∏≠‡∏á ‡πÅ‡∏•‡πâ‡∏ß‡∏•‡∏≠‡∏á‡πÉ‡∏´‡∏°‡πà';
      } else if (error.name === 'NotFoundError') {
        errorMessage = '‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Å‡∏•‡πâ‡∏≠‡∏á‡πÉ‡∏ô‡∏≠‡∏∏‡∏õ‡∏Å‡∏£‡∏ì‡πå';
      } else if (error.name === 'NotReadableError') {
