  const generateFeedback = (trainerPoints, traineePoints, score) => {
    if (score >= 85) {
      return "üéâ ‡∏¢‡∏≠‡∏î‡πÄ‡∏¢‡∏µ‡πà‡∏¢‡∏°! ‡∏ó‡πà‡∏≤‡∏≠‡∏≠‡∏Å‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏Å‡∏≤‡∏¢‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á‡∏°‡∏≤‡∏Å";
    } else if (score >= 70) {
      return "üëç ‡∏î‡∏µ‡∏°‡∏≤‡∏Å! ‡∏õ‡∏£‡∏±‡∏ö‡∏ó‡πà‡∏≤‡πÄ‡∏•‡πá‡∏Å‡∏ô‡πâ‡∏≠‡∏¢‡πÉ‡∏´‡πâ‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö‡πÇ‡∏Ñ‡πâ‡∏ä";
    } else if (score >= 50) {
      return "‚ö†Ô∏è ‡∏õ‡∏£‡∏±‡∏ö‡∏ó‡πà‡∏≤‡πÉ‡∏´‡πâ‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö‡πÇ‡∏Ñ‡πâ‡∏ä‡∏°‡∏≤‡∏Å‡∏Ç‡∏∂‡πâ‡∏ô";
    } else {
      return "‚ùå ‡∏•‡∏≠‡∏á‡∏î‡∏π‡∏ó‡πà‡∏≤‡∏Ç‡∏≠‡∏á‡πÇ‡∏Ñ‡πâ‡∏ä‡πÅ‡∏•‡∏∞‡∏õ‡∏£‡∏±‡∏ö‡∏ï‡∏≤‡∏°‡πÉ‡∏´‡∏°‡πà";
    }
  };

  if (loading) {
    return (
      <div className="workout-loading">
        <div className="loading-spinner">‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î...</div>
      </div>
    );
  }

  return (
    <div className="workout-page">
