import React, { useState, useRef, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import KeypointOverlay from '../components/KeypointOverlay';
import KeypointsService from '../services/KeypointsService';
import './WorkoutPage.css';

const WorkoutPage = () => {
  const { videoId } = useParams();
  const navigate = useNavigate();
  
  // Refs for video and camera
  const trainerVideoRef = useRef(null);
  const traineeVideoRef = useRef(null);
  const traineeCanvasRef = useRef(null);
  
  // States
  const [isPlaying, setIsPlaying] = useState(false);
  const [videoData, setVideoData] = useState(null);
  const [segments, setSegments] = useState([]);
  const [currentSegment, setCurrentSegment] = useState(0);
  const [loading, setLoading] = useState(true);
  
  // Camera and keypoints states
  const [cameraStream, setCameraStream] = useState(null);
  const [traineeKeypoints, setTraineeKeypoints] = useState(null);
  const [trainerKeypoints, setTrainerKeypoints] = useState(null);
  const [cameraError, setCameraError] = useState(null);
  
  // Scoring states
  const [matchScore, setMatchScore] = useState(0);
  const [feedback, setFeedback] = useState('');
  const [isInitialized, setIsInitialized] = useState(false);

  // ðŸŽ¯ KeypointsService instance à¸ªà¸³à¸«à¸£à¸±à¸šà¹€à¸›à¸£à¸µà¸¢à¸šà¹€à¸—à¸µà¸¢à¸š pose
  const keypointsService = useRef(new KeypointsService());
  
  // Real-time comparison states
  const [currentAccuracy, setCurrentAccuracy] = useState(0);
  const [realTimeFeedback, setRealTimeFeedback] = useState(null);
  const [isComparing, setIsComparing] = useState(false);

  // Load video data and segments
  useEffect(() => {
    const loadVideoData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:8000/videos/${videoId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        if (!response.ok) throw new Error('Failed to load video');
        
        const data = await response.json();
        setVideoData(data);
        
        // Load segments
        const segmentsResponse = await fetch(`http://localhost:8000/videos/${videoId}/segments`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        if (segmentsResponse.ok) {
          const segmentsData = await segmentsResponse.json();
          setSegments(segmentsData.segments || []);
        }
        
      } catch (error) {
        console.error('Error loading video data:', error);
        alert('à¹„à¸¡à¹ˆà¸ªà¸²à¸¡à¸²à¸£à¸–à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸§à¸´à¸”à¸µà¹‚à¸­à¹„à¸”à¹‰');
        navigate('/trainee');
      } finally {
        setLoading(false);
      }
    };
    
    loadVideoData();
  }, [videoId, navigate]);

  // Camera permission states  
  const [cameraPermissionState, setCameraPermissionState] = useState('not-requested'); // 'not-requested', 'requesting', 'granted', 'denied'
  const [userRequestedCamera, setUserRequestedCamera] = useState(false);

  // Debug log for states
  useEffect(() => {
    console.log('WorkoutPage State:', {
      userRequestedCamera,
      cameraPermissionState,
      cameraStream: !!cameraStream,
      cameraError,
      isInitialized
    });
  }, [userRequestedCamera, cameraPermissionState, cameraStream, cameraError, isInitialized]);

  // Request camera permission function
  const requestCameraPermission = useCallback(async () => {
    console.log('ðŸŽ¥ requestCameraPermission called');
    setCameraPermissionState('requesting');
    setUserRequestedCamera(true);
    
    try {
      console.log('à¸à¸³à¸¥à¸±à¸‡à¸‚à¸­à¸­à¸™à¸¸à¸à¸²à¸•à¹ƒà¸Šà¹‰à¸à¸¥à¹‰à¸­à¸‡...');
      
      // Check if mediaDevices is supported
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error('Browser à¹„à¸¡à¹ˆà¸£à¸­à¸‡à¸£à¸±à¸šà¸à¸²à¸£à¹€à¸‚à¹‰à¸²à¸–à¸¶à¸‡à¸à¸¥à¹‰à¸­à¸‡');
      }
      
      const constraints = {
        video: { 
          width: { ideal: 640 }, 
          height: { ideal: 480 }, 
          facingMode: 'user'
        },
        audio: false
      };
      
      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      console.log('à¹€à¸›à¸´à¸”à¸à¸¥à¹‰à¸­à¸‡à¸ªà¸³à¹€à¸£à¹‡à¸ˆ:', stream);
      
      setCameraStream(stream);
      setCameraPermissionState('granted');
      setCameraError(null);
      
      console.log('âœ… Camera stream state updated');
      
    } catch (error) {
      console.error('Error accessing camera:', error);
      
      let errorMessage = 'à¹„à¸¡à¹ˆà¸ªà¸²à¸¡à¸²à¸£à¸–à¹€à¸‚à¹‰à¸²à¸–à¸¶à¸‡à¸à¸¥à¹‰à¸­à¸‡à¹„à¸”à¹‰';
      if (error.name === 'NotAllowedError') {
        errorMessage = 'à¸à¸£à¸¸à¸“à¸²à¸­à¸™à¸¸à¸à¸²à¸•à¸à¸²à¸£à¹ƒà¸Šà¹‰à¸‡à¸²à¸™à¸à¸¥à¹‰à¸­à¸‡à¹ƒà¸™à¹€à¸šà¸£à¸²à¸§à¹Œà¹€à¸‹à¸­à¸£à¹Œ';
        setCameraPermissionState('denied');
      } else if (error.name === 'NotFoundError') {
        errorMessage = 'à¹„à¸¡à¹ˆà¸žà¸šà¸à¸¥à¹‰à¸­à¸‡à¹ƒà¸™à¸­à¸¸à¸›à¸à¸£à¸“à¹Œ';
        setCameraPermissionState('denied');
