  const compareKeypoints = (trainerPoints, traineePoints) => {
    if (!trainerPoints || !traineePoints || 
        trainerPoints.length !== traineePoints.length) {
      return 0;
    }

    let totalScore = 0;
    const keyPointWeights = {
      // Higher weights for important body parts
      11: 1.5, 12: 1.5, // Shoulders
      13: 1.3, 14: 1.3, // Elbows  
      15: 1.2, 16: 1.2, // Wrists
      23: 1.4, 24: 1.4, // Hips
      25: 1.3, 26: 1.3, // Knees
      27: 1.1, 28: 1.1, // Ankles
    };

    for (let i = 0; i < trainerPoints.length; i++) {
      const trainerPoint = trainerPoints[i];
      const traineePoint = traineePoints[i];
      
      if (trainerPoint.visibility > 0.5 && traineePoint.visibility > 0.5) {
        // Calculate Euclidean distance
        const distance = Math.sqrt(
          Math.pow(trainerPoint.x - traineePoint.x, 2) + 
          Math.pow(trainerPoint.y - traineePoint.y, 2)
        );
        
        // Convert distance to similarity score (closer = higher score)
        const similarity = Math.max(0, 1 - distance * 2);
        const weight = keyPointWeights[i] || 1.0;
        
        totalScore += similarity * weight;
      }
    }

    return Math.round((totalScore / trainerPoints.length) * 100);
  };

