import React, { useEffect, useState } from 'react';
import TraineeHeader from '../components/TraineeHeader';
import TrainerCard from '../components/TrainerCard';
import VideoCard from '../components/VideoCard';
import UserActivityCard from '../components/UserActivityCard';

const defaultTrainer = {
  name: '',
  picture: '',
  members: 0,
  videos: 0,
  id: null
};

function TraineePage() {
  // ฟังก์ชันลบวิดีโอ
  const handleDeleteVideo = async (videoId) => {
    const token = localStorage.getItem('token');
    if (!token) return;
    try {
      const res = await fetch(`http://localhost:8000/videos/${videoId}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });
      if (!res.ok) throw new Error('ลบวิดีโอไม่สำเร็จ');
      setVideos(videos => videos.filter(v => v.id !== videoId));
    } catch (e) {
      alert('เกิดข้อผิดพลาดในการลบวิดีโอ');
      console.error(e);
    }
  };
  const [user, setUser] = useState(null);
  const [trainer, setTrainer] = useState(defaultTrainer);
  const [videos, setVideos] = useState([]);
  const [userLevel, setUserLevel] = useState(1); // สมมติว่ามี field level ใน user หรือดึงจาก backend

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      const userObj = JSON.parse(userData);
      setUser(userObj);
      // สมมติว่ามี field level ใน userObj
      setUserLevel(userObj.level || 1);
    }
  }, []);

  useEffect(() => {
    // ดึง trainer ของ trainee จาก backend
    const fetchTrainerAndVideos = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await fetch('http://localhost:8000/my-trainer', {
          headers: token ? { 'Authorization': `Bearer ${token}` } : {}
        });
        if (res.ok) {
          const data = await res.json();
          setTrainer({
            name: data.name,
            picture: data.profile_image || data.picture || '/user (1).png',
            members: data.members || 0,
            videos: data.videos || 0,
            id: data.id
          });
          // ดึงวิดีโอของ trainer
          if (data.id) {
            const res2 = await fetch(`http://localhost:8000/trainers/${data.id}/videos`, {
              headers: token ? { 'Authorization': `Bearer ${token}` } : {}
            });
            if (res2.ok) {
              const vids = await res2.json();
              setVideos(Array.isArray(vids) ? vids : []);
            }
          }
        }
      } catch {}
    };
    fetchTrainerAndVideos();
  }, []);

  if (!user) return (<div className="dashboard-loading"><div className="loading-spinner"></div><p>กำลังโหลด...</p></div>);

  // แบ่งวิดีโอตาม level (สมมติแต่ละ video มี field level)
  const videosByLevel = {};
  videos.forEach(v => {
    const lvl = v.level || 1;
    if (!videosByLevel[lvl]) videosByLevel[lvl] = [];
    videosByLevel[lvl].push(v);
  });
  const sortedLevels = Object.keys(videosByLevel).sort((a,b)=>a-b);

  const today = new Date().toLocaleDateString('en-GB', { day: 'numeric', month: 'long', year: 'numeric' });
  return (
    <div className="dashboard" style={{background:'#f8fafc'}}>
      <TraineeHeader user={user} points={200} date={today} />
      <div style={{maxWidth:1200,margin:'0 auto',padding:'1.5rem 0'}}>
        <div style={{fontWeight:700,fontSize:'1.5rem',color:'#334155',marginBottom:'1.25rem'}}>My trainer</div>
  <TrainerCard trainer={{...trainer, videos: videos.length}} />
        {/* วิดีโอแบ่งตาม level */}
        <div style={{fontWeight:700,fontSize:'1.15rem',color:'#334155',marginBottom:'0.5rem'}}>Videolist ({videos.length})</div>
        {sortedLevels.map(lvl => (
          <div key={lvl}>
            <div style={{marginBottom:'0.5rem',color:'#64748b',fontWeight:600}}>
              Level {lvl} ({videosByLevel[lvl].length})
            </div>
            <div style={{
              display:'grid',
              gridTemplateColumns:'repeat(auto-fit,minmax(320px,1fr))',
              gap:'32px',
              alignItems:'stretch',
              justifyItems:'stretch',
              marginBottom:'1.5rem',
              padding:'0'
            }}>
              {videosByLevel[lvl].map(v => (
                (() => {
                  // เฉพาะสถานะที่ต้องการให้แสดง
                  let status = (parseInt(lvl) > userLevel) ? 'Locked' : (v.statusBtn || v.status || 'Not Started');
                  if (!["Pass", "Try Again", "Not Started", "Locked"].includes(status)) return null;
                  return (
                    <VideoCard
                      key={v.id}
                      video={{
                        ...v,
                        image: v.s3_url ?
                          (/^https?:\/\//i.test(v.s3_url)
                            ? v.s3_url
                            : `http://localhost:8000/static/${v.s3_url.replace(/^.*[\\\/]/, '')}`)
                          : '',
                        statusBtn: status
                      }}
                      cardHeight="stretch"
                      cardMargin={0}
                      onPlay={()=>{}}
                      onDelete={handleDeleteVideo}
                    />
                  );
                })()
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TraineePage;
