import React, { useRef, useEffect, useState } from 'react';
import { Pose } from '@mediapipe/pose';
import { Camera } from '@mediapipe/camera_utils';
import { drawConnectors, drawLandmarks } from '@mediapipe/drawing_utils';

const KeypointOverlay = ({ videoRef, isPlaying, onKeypointsDetected }) => {
  const canvasRef = useRef(null);
  const poseRef = useRef(null);
  const [isInitialized, setIsInitialized] = useState(false);

  // MediaPipe Pose connections (skeleton lines)
  const POSE_CONNECTIONS = [
    [11, 12], [11, 13], [13, 15], [15, 17], [15, 19], [15, 21], [17, 19],
    [12, 14], [14, 16], [16, 18], [16, 20], [16, 22], [18, 20],
    [11, 23], [12, 24], [23, 24], [23, 25], [24, 26], [25, 27], [26, 28],
    [27, 29], [28, 30], [29, 31], [30, 32], [27, 31], [28, 32]
  ];

  useEffect(() => {
    if (!videoRef?.current || !canvasRef.current) return;

    // Initialize MediaPipe Pose
    const pose = new Pose({
      locateFile: (file) => {
        return `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`;
      }
    });

    pose.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      enableSegmentation: false,
      smoothSegmentation: true,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5
    });

    pose.onResults(onResults);
    poseRef.current = pose;
    setIsInitialized(true);

    return () => {
      if (poseRef.current) {
        poseRef.current.close();
      }
    };
  }, [videoRef]);

  useEffect(() => {
    if (!isInitialized || !videoRef?.current || !canvasRef.current || !isPlaying) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    
    // Match canvas size to video
    canvas.width = video.videoWidth || video.clientWidth;
    canvas.height = video.videoHeight || video.clientHeight;

    const processFrame = async () => {
      if (video.paused || video.ended) return;
      
      try {
        await poseRef.current.send({ image: video });
      } catch (error) {
        console.error('Pose detection error:', error);
      }

      if (isPlaying) {
        requestAnimationFrame(processFrame);
      }
    };

    if (video.readyState >= 2) {
      processFrame();
    } else {
      video.addEventListener('loadeddata', processFrame, { once: true });
    }
  }, [isPlaying, isInitialized]);

  const onResults = (results) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (results.poseLandmarks) {
      // Draw skeleton connections
      drawConnectors(ctx, results.poseLandmarks, POSE_CONNECTIONS, {
        color: '#00FF00',
        lineWidth: 2
      });

      // Draw keypoints
      drawLandmarks(ctx, results.poseLandmarks, {
        color: '#FF0000',
        radius: 3
      });

      // Pass keypoints to parent component
      if (onKeypointsDetected) {
        onKeypointsDetected(results.poseLandmarks);
      }
    }
  };

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 10
      }}
    />
  );
};

export default KeypointOverlay;
