// PoseAccuracyDisplay.js - ‡πÅ‡∏™‡∏î‡∏á‡∏ú‡∏•‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡πÅ‡∏•‡∏∞ feedback ‡πÅ‡∏ö‡∏ö‡πÄ‡∏£‡∏µ‡∏¢‡∏•‡πÑ‡∏ó‡∏°‡πå

import React from 'react';
import './PoseAccuracyDisplay.css';

const PoseAccuracyDisplay = ({ 
    accuracy, 
    feedback, 
    isComparing, 
    sessionHistory, 
    weakPoints,
    currentSessionScore 
}) => {
    // üé® ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏£‡∏∞‡∏î‡∏±‡∏ö‡∏™‡∏µ ‡πÅ‡∏ö‡∏ö 3 ‡∏£‡∏∞‡∏î‡∏±‡∏ö (‡πÅ‡∏î‡∏á/‡πÄ‡∏´‡∏•‡∏∑‡∏≠‡∏á/‡πÄ‡∏Ç‡∏µ‡∏¢‡∏ß)
    const getAccuracyColor = (score) => {
        if (score <= 50) return '#ef4444'; // üî¥ Red
        if (score <= 70) return '#eab308'; // üü° Yellow
        return '#22c55e'; // üü¢ Green
    };

    const getAccuracyBackgroundColor = (score) => {
        if (score <= 50) return 'rgba(239, 68, 68, 0.1)'; // üî¥ Red background
        if (score <= 70) return 'rgba(234, 179, 8, 0.1)'; // üü° Yellow background
        return 'rgba(34, 197, 94, 0.1)'; // üü¢ Green background
    };

    // ‡πÅ‡∏™‡∏î‡∏á progress bar ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÅ‡∏ï‡πà‡∏•‡∏∞‡∏™‡πà‡∏ß‡∏ô‡∏Ç‡∏≠‡∏á‡∏£‡πà‡∏≤‡∏á‡∏Å‡∏≤‡∏¢
    const renderWeakPointsAnalysis = () => {
        if (!weakPoints || Object.keys(weakPoints).length === 0) return null;

        return (
            <div className="weak-points-analysis">
                <h4>üìä Body Parts Analysis</h4>
                <div className="body-parts-grid">
                    {Object.entries(weakPoints).map(([partName, data]) => (
                        <div key={partName} className="body-part-item">
                            <div className="body-part-header">
                                <span className="part-name">{partName}</span>
                                <span className="part-score">{Math.round(data.accuracy)}%</span>
                            </div>
                            <div className="part-progress-bar">
                                <div 
                                    className="part-progress-fill"
                                    style={{ 
                                        width: `${data.accuracy}%`,
                                        backgroundColor: getAccuracyColor(data.accuracy)
                                    }}
                                />
                            </div>
                            {data.needsImprovement && (
                                <div className="improvement-hint">‚ö†Ô∏è Needs attention</div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        );
    };

    // ‡πÅ‡∏™‡∏î‡∏á mini chart ‡∏Ç‡∏≠‡∏á accuracy history
    const renderAccuracyChart = () => {
        if (!sessionHistory || sessionHistory.length < 2) return null;

        const maxPoints = 30; // ‡πÅ‡∏™‡∏î‡∏á‡πÄ‡∏â‡∏û‡∏≤‡∏∞ 30 ‡∏à‡∏∏‡∏î‡∏•‡πà‡∏≤‡∏™‡∏∏‡∏î
        const displayHistory = sessionHistory.slice(-maxPoints);
        
        return (
            <div className="accuracy-chart">
                <h4>üìà Performance Trend</h4>
                <div className="chart-container">
                    <svg width="100%" height="60" viewBox="0 0 300 60">
                        {displayHistory.map((point, index) => {
                            if (index === 0) return null;
                            
                            const prevPoint = displayHistory[index - 1];
                            const x1 = (index - 1) * (300 / (displayHistory.length - 1));
                            const y1 = 50 - (prevPoint.accuracy * 40 / 100);
                            const x2 = index * (300 / (displayHistory.length - 1));
                            const y2 = 50 - (point.accuracy * 40 / 100);
                            
                            return (
                                <line
                                    key={index}
                                    x1={x1}
                                    y1={y1}
                                    x2={x2}
                                    y2={y2}
                                    stroke={getAccuracyColor(point.accuracy)}
                                    strokeWidth="2"
                                />
                            );
                        })}
                    </svg>
                </div>
            </div>
        );
    };

    if (!isComparing) {
        return (
            <div className="pose-accuracy-display inactive">
                <div className="status-message">
                    üéØ Ready to start pose comparison
                </div>
            </div>
        );
    }

    return (
        <div className="pose-accuracy-display active">
            {/* Main Accuracy Display */}
            <div className="main-accuracy">
                <div className="accuracy-circle">
                    <div 
                        className="accuracy-progress"
                        style={{ 
                            background: `conic-gradient(${getAccuracyColor(accuracy)} ${accuracy * 3.6}deg, #e0e0e0 0deg)`,
                            boxShadow: `0 0 20px ${getAccuracyColor(accuracy)}40`
                        }}
                    >
                        <div className="accuracy-inner">
                            <div className="accuracy-value">{accuracy}%</div>
                            <div className="accuracy-label">Accuracy</div>
                        </div>
                    </div>
                </div>
                
                {/* Feedback Message */}
                {feedback && (
                    <div 
                        className="feedback-message"
                        style={{ color: feedback.color }}
                    >
                        <div className="feedback-text">{feedback.message}</div>
                        <div className="feedback-level">{feedback.level.toUpperCase()}</div>
                    </div>
                )}
            </div>

            {/* Session Score */}
            {currentSessionScore > 0 && (
                <div className="session-score">
                    <span className="session-label">Session Avg:</span>
                    <span 
                        className="session-value"
                        style={{ color: getAccuracyColor(currentSessionScore) }}
                    >
                        {currentSessionScore}%
                    </span>
                </div>
            )}

            {/* Performance Chart */}
            {renderAccuracyChart()}

            {/* Weak Points Analysis */}
            {renderWeakPointsAnalysis()}

            {/* Quick Stats */}
            <div className="quick-stats">
                <div className="stat-item">
                    <span className="stat-label">Frames:</span>
                    <span className="stat-value">{sessionHistory.length}</span>
                </div>
                <div className="stat-item">
                    <span className="stat-label">Max:</span>
                    <span className="stat-value">
                        {Math.max(...sessionHistory.map(h => h.accuracy), 0)}%
                    </span>
                </div>
                <div className="stat-item">
                    <span className="stat-label">Status:</span>
                    <span className="stat-value status-active">‚óè LIVE</span>
                </div>
            </div>
        </div>
    );
};

export default PoseAccuracyDisplay;
