import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import TrainerHeader from '../components/TrainerHeader';  // ใช้คอมโพเนนต์ที่มีอยู่
import VideoCard from '../components/VideoCard';  // ใช้คอมโพเนนต์ที่มีอยู่
import InviteTrainee from '../components/InviteTrainee';  // ใช้คอมโพเนนต์ที่มีอยู่
import { useLoading } from '../contexts/LoadingProvider';
import '../styles/TrainerPageNew';  // นำเข้าไฟล์ CSS

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:8000";

const themePurple = {
  background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)',
  minHeight: '100vh',
  fontFamily: 'Inter, sans-serif',
};

function TrainerPageNew() {
  const [videos, setVideos] = useState([]);
  const [trainees, setTrainees] = useState([]);
  const [profile, setProfile] = useState({ name: '', email: '', picture: '' });
  const { showLoading, hideLoading } = useLoading();
  const navigate = useNavigate();

  // ฟังก์ชันดึงข้อมูลวิดีโอ
  const fetchVideos = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;
    try {
      const res = await fetch(`${API_BASE}/my-videos`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error('โหลดวิดีโอไม่สำเร็จ');
      const data = await res.json();
      setVideos(data);
    } catch (e) {
      console.error(e);
    }
  };

  // ฟังก์ชันดึงข้อมูลสมาชิก
  const fetchTrainees = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;
    try {
      const res = await fetch(`${API_BASE}/my-trainees`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error('โหลดรายชื่อสมาชิกไม่สำเร็จ');
      const data = await res.json();
      setTrainees(data);
    } catch (e) {
      console.error(e);
    }
  };

  useEffect(() => {
    fetchVideos();
    fetchTrainees();
  }, []);

  // ฟังก์ชันลบวิดีโอ
  const handleDeleteVideo = async (videoId) => {
    const token = localStorage.getItem('token');
    if (!token) return;
    try {
      const res = await fetch(`${API_BASE}/videos/${videoId}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error('ลบวิดีโอไม่สำเร็จ');
      setVideos(videos.filter((v) => v.id !== videoId));
    } catch (e) {
      alert('เกิดข้อผิดพลาดในการลบวิดีโอ');
      console.error(e);
    }
  };

  return (
    <div className="trainer-page" style={themePurple}>
      {/* Header */}
      <TrainerHeader
        user={{
          id: profile.id,
          name: profile.name,
          picture: profile.picture || '/user (1).png',
        }}
        date={new Date().toLocaleDateString('en-GB', { day: 'numeric', month: 'long', year: 'numeric' })}
      />

      {/* Main Content */}
      <div className="trainer-main-content" style={{ display: 'flex', gap: '20px', padding: '20px' }}>
        {/* Memberlist */}
        <aside className="trainer-memberlist">
          <div className="member-header">
            <img src={profile.picture} alt="avatar" className="avatar" />
            <div className="profile-info">
              <div>{profile.name}</div>
              <div>{profile.email}</div>
            </div>
            <span className="status-dot"></span>
          </div>

          <div className="memberlist-title">
            Memberlist ({trainees.length})
          </div>
          <InviteTrainee onSuccess={fetchTrainees} />

          <div className="trainee-list">
            {trainees.length === 0 ? (
              <div style={{ color: '#64748b' }}>ยังไม่มีสมาชิกในทีม</div>
            ) : (
              trainees.map((trainee) => (
                <div key={trainee.id} className="trainee-card">
                  <img src={trainee.profile_image || '/user (1).png'} alt="avatar" className="trainee-avatar" />
                  <div>
                    <div>{trainee.name || 'Unnamed'}</div>
                    <div>{trainee.email}</div>
                  </div>
                  <button
                    onClick={() => navigate(`/trainer/trainees/${trainee.id}`)}
                    className="view-button"
                  >
                    View
                  </button>
                </div>
              ))
            )}
          </div>
        </aside>

        {/* Video Grid */}
        <main className="trainer-videolist">
          <div className="videolist-header">
            <div className="videolist-title">Videolist ({videos.length})</div>
            <button
              onClick={() => navigate('/trainer/upload')}
              className="upload-button"
            >
              <img src={process.env.PUBLIC_URL + '/UploadSimple.png'} alt="upload" className="upload-icon" />
              Upload
            </button>
          </div>

          {/* Video Cards */}
          <div className="video-cards">
            {videos.length === 0 ? (
              <div style={{ color: '#64748b' }}>ยังไม่มีวิดีโอ</div>
            ) : (
              videos.map((video) => (
                <VideoCard
                  key={video.id}
                  video={video}
                  onDelete={handleDeleteVideo}
                  onCardClick={() => navigate('/trainer/upload', { state: { mode: 'edit', videoId: video.id, video: video } })}
                />
              ))
            )}
          </div>
        </main>
      </div>
    </div>
  );
}

export default TrainerPageNew;
