import React from 'react';

const VideoCard = ({ video, onPlay, cardHeight }) => (
  <div style={{
    background:'#fff',
    borderRadius:18,
    boxShadow:'0 2px 8px -2px #a855f722',
    padding:'1.25rem',
    display:'flex',
    flexDirection:'column',
    alignItems:'center',
    minWidth:260,
    maxWidth: '100%',
    margin:'0.5rem',
    height: cardHeight === 'stretch' ? '100%' : undefined
  }}>
    <img src={video.image} alt={video.title} style={{width:'100%',height:120,objectFit:'cover',borderRadius:12,marginBottom:'1rem'}} />
    <div style={{fontWeight:700,fontSize:'1.1rem',color:'#334155',marginBottom:'0.5rem'}}>{video.title}</div>
    <div style={{color:'#7c3aed',fontSize:'0.95rem',marginBottom:'0.25rem'}}>ðŸ•’ {video.duration} à¸™. &nbsp; ðŸ”¥ {video.kcal} Kcal</div>
    <div style={{color:'#64748b',fontSize:'0.95rem',marginBottom:'0.25rem'}}>Score: {video.score ?? '-'}%</div>
    {video.statusBtn && <span style={{display:'inline-block',background:video.statusColor,color:'#fff',fontWeight:600,borderRadius:8,padding:'0.15rem 0.75rem',fontSize:'0.95rem',marginBottom:'0.5rem'}}>{video.statusBtn}</span>}
  <button onClick={onPlay} style={{width:'100%',background:'linear-gradient(90deg,#fb6600 0%, #ff4d8b 50%, #a855f7 80%, #4f46e5 100%)',color:'#fff',fontWeight:700,border:'none',borderRadius:12,padding:'0.5rem 0',fontSize:'1.1rem',marginTop:'0.5rem',cursor:'pointer'}}>
      â–¶ Play
    </button>
  </div>
);

export default VideoCard;
