import React from 'react';


import './GoogleAuthButton.css';

const GoogleAuthButton = ({ onSuccess, onError, isLoading }) => {
  const buttonText = "Continue with Google";

  const handleGoogleLogin = () => {
    if (!window.google || !window.google.accounts || !window.google.accounts.oauth2) {
      alert('Google API not loaded');
      return;
    }
    window.google.accounts.oauth2.initTokenClient({
      client_id: process.env.REACT_APP_GOOGLE_CLIENT_ID || 'YOUR_GOOGLE_CLIENT_ID',
      scope: 'profile email openid',
      callback: (response) => {
        if (response && response.access_token) {
          onSuccess({ credential: response.access_token });
        } else {
          onError();
        }
      },
    }).requestAccessToken();
  };

  return (
    <div className="google-auth-button">
      <button
        className="google-auth-btn"
        onClick={handleGoogleLogin}
        disabled={isLoading}
        type="button"
        aria-label="Continue with Google"
      >
        <img src="/googlelogo.png" alt="Google" className="google-icon" />
        <span className="google-text">{buttonText}</span>
      </button>
    </div>
  );
};

export default GoogleAuthButton;
