import InviteTrainee from '../pages/InviteTrainee';
import VideoCard from './VideoCard';
import React, { useEffect, useState } from 'react';
import TrainerHeader from './TrainerHeader';
import './Dashboard.css';
// เพิ่มสไตล์ธีมม่วงแบบหน้า trainee
const themePurple = {
  background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)',
  minHeight: '100vh',
  fontFamily: 'Inter, sans-serif',
};

const TrainerDashboard = () => {
  const [videos, setVideos] = useState([]);
  const [summary, setSummary] = useState(null);
  const [traineeReport, setTraineeReport] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [profile, setProfile] = useState({ name: '', email: '', picture: '' });

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoading(true);
    // Fetch all trainers (simulate as trainer's own data for now)
    fetch('http://localhost:8000/trainers', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(trainers => {
        // Assume the first trainer is the logged-in trainer (for demo)
        const trainer = Array.isArray(trainers) && trainers.length > 0 ? trainers[0] : null;
        if (trainer) {
          setProfile({
            name: trainer.name,
            email: trainer.email,
            picture: '/user (1).png'
          });
          // Fetch trainer profile for more details
          fetch(`http://localhost:8000/trainers/${trainer.id}`, {
            headers: { Authorization: `Bearer ${token}` }
          })
            .then(res => res.json())
            .then(profileData => {
              // Simulate summary and report from profileData
              setSummary({
                most_popular_title: 'N/A',
                most_popular_count: 0,
                total_usage: 0
              });
              setTraineeReport([]); // No trainee report in backend yet
              setVideos([]); // No videos endpoint in backend yet
            })
            .catch(() => setError('เกิดข้อผิดพลาดในการโหลดข้อมูลโปรไฟล์เทรนเนอร์'))
            .finally(() => setIsLoading(false));
        } else {
          setError('ไม่พบข้อมูลเทรนเนอร์');
          setIsLoading(false);
        }
      })
      .catch(() => {
        setError('เกิดข้อผิดพลาดในการโหลดข้อมูลเทรนเนอร์');
        setIsLoading(false);
      });
  }, []);

  if (isLoading) return <div className="dashboard-loading"><div className="loading-spinner"></div><p>กำลังโหลดข้อมูล...</p></div>;
  if (error) return <div className="error-message">{error}</div>;

  // mock data แบบ trainee
  const mockVideos = [
    { id: 1, title: 'Pushups', image: 'https://images.pexels.com/photos/2261482/pexels-photo-2261482.jpeg', duration: '30.20', kcal: 20, score: 95, statusBtn: 'Active' },
    { id: 2, title: 'Pushups', image: 'https://images.pexels.com/photos/2261482/pexels-photo-2261482.jpeg', duration: '30.20', kcal: 20, score: 95, statusBtn: 'Active' },
    { id: 3, title: 'Pushups', image: 'https://images.pexels.com/photos/2261482/pexels-photo-2261482.jpeg', duration: '30.20', kcal: 20, score: 95, statusBtn: 'Active' },
    { id: 4, title: 'Pushups', image: 'https://images.pexels.com/photos/2261482/pexels-photo-2261482.jpeg', duration: '30.20', kcal: 20, score: 95, statusBtn: 'Active' },
  ];
  return (
    <div className="dashboard trainer-dashboard-bg" style={themePurple}>
      {/* Header (TrainerHeader ไม่มี pts) */}
      <TrainerHeader
        user={{
          name: profile.name,
          picture: profile.picture || '/user (1).png',
        }}
        date={new Date().toLocaleDateString('en-GB', { day: 'numeric', month: 'long', year: 'numeric' })}
      />
      <div style={{
        background: '#f3f4f6',
        borderRadius: 20,
        boxShadow: '0 2px 24px rgba(0,0,0,0.10)',
        maxWidth: 1400,
        margin: '2rem auto',
        padding: '2.5rem 3rem 3rem 3rem',
        minHeight: 800,
        display: 'flex',
        flexDirection: 'column',
        gap: 32
      }}>
        {/* Layout: Memberlist + Videolist */}
        <div style={{display:'flex',gap:40,alignItems:'flex-start',padding:'0'}}>
          {/* Memberlist */}
          <aside style={{background:'#fff',borderRadius:24,padding:'2.2rem 1.7rem',boxShadow:'0 2px 16px rgba(0,0,0,0.06)',minWidth:340,maxWidth:380,flex:'0 0 350px',display:'flex',flexDirection:'column',gap:20}}>
            <div style={{fontWeight:700,fontSize:'1.18rem',marginBottom:10}}>Memberlist (12)</div>
            <div style={{display:'flex',gap:10,marginBottom:18}}>
              <input type="email" placeholder="Type an email to Invite" style={{flex:1,padding:'0.7rem 1.1rem',borderRadius:18,border:'1px solid #e5e7eb',fontSize:'1.05rem'}} />
              <button style={{background:'linear-gradient(90deg,#ff4d8b 0%, #a855f7 100%)',color:'#fff',fontWeight:700,border:'none',borderRadius:18,padding:'0.6rem 1.4rem',fontSize:'1.05rem',display:'flex',alignItems:'center',gap:7,cursor:'pointer'}}>
                <span style={{fontSize:'1.4rem',marginRight:2}}>+</span> Invite
              </button>
            </div>
            {/* Member avatars list (mockup) */}
            <div style={{flex:1,overflowY:'auto',maxHeight:520}}>
              {[...Array(8)].map((_,i)=>(
                <div key={i} style={{display:'flex',alignItems:'center',gap:14,marginBottom:14}}>
                  <img src={i%2===0 ? 'https://randomuser.me/api/portraits/men/32.jpg' : 'https://randomuser.me/api/portraits/men/31.jpg'} alt="avatar" style={{width:40,height:40,borderRadius:'50%',objectFit:'cover',border:'2px solid #e5e7eb'}} />
                  <div style={{fontWeight:600,fontSize:'1.05rem'}}>Plume FitAddict</div>
                  <span style={{width:12,height:12,borderRadius:'50%',background:'#22c55e',display:'inline-block',marginLeft:'auto',marginRight:10}}></span>
                  <span style={{fontSize:24,color:'#64748b',cursor:'pointer'}}>⋮</span>
                </div>
              ))}
            </div>
          </aside>
          {/* Videolist */}
          <main style={{flex:1}}>
            <div style={{display:'flex',justifyContent:'space-between',alignItems:'center',marginBottom:22}}>
              <div style={{fontWeight:700,fontSize:'1.18rem'}}>Videolist (4)</div>
              <button style={{background:'linear-gradient(90deg,#ff4d8b 0%, #a855f7 100%)',color:'#fff',fontWeight:700,border:'none',borderRadius:18,padding:'0.6rem 1.7rem',fontSize:'1.05rem',display:'flex',alignItems:'center',gap:9,cursor:'pointer'}}>
                <span style={{fontSize:'1.3rem',fontWeight:900}}>⬆</span> Upload
              </button>
            </div>
            {/* Video cards */}
            <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fit,minmax(320px,1fr))',gap:28}}>
              {mockVideos.map(v => <VideoCard key={v.id} video={v} />)}
            </div>
          </main>
        </div>
      </div>
    </div>
  );
}

export default TrainerDashboard;
