import React, { useEffect, useState } from 'react';
import './Dashboard.css';
// เพิ่มสไตล์ธีมม่วงแบบหน้า trainee
const themePurple = {
  background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)',
  minHeight: '100vh',
  fontFamily: 'Inter, sans-serif',
};

const TrainerDashboard = () => {
  const [videos, setVideos] = useState([]);
  const [summary, setSummary] = useState(null);
  const [traineeReport, setTraineeReport] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [profile, setProfile] = useState({ name: '', email: '', picture: '' });

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoading(true);
    Promise.all([
      fetch('http://localhost:8000/trainer/videos', {
        headers: { Authorization: `Bearer ${token}` }
      }).then(res => res.json()),
      fetch('http://localhost:8000/trainer/video-summary', {
        headers: { Authorization: `Bearer ${token}` }
      }).then(res => res.json()),
      fetch('http://localhost:8000/trainer/trainee-report', {
        headers: { Authorization: `Bearer ${token}` }
      }).then(res => res.json()),
      fetch('http://localhost:8000/me', {
        headers: { Authorization: `Bearer ${token}` }
      }).then(res => res.json()),
    ])
      .then(([videosData, summaryData, traineeReportData, profileData]) => {
        setVideos(Array.isArray(videosData) ? videosData : []);
        setSummary(summaryData);
        setTraineeReport(Array.isArray(traineeReportData) ? traineeReportData : []);
        if (profileData && profileData.name) {
          // ตรวจสอบว่า profileData.picture เป็น URL จริงหรือไม่
          let pictureUrl = profileData.picture;
          if (!pictureUrl || typeof pictureUrl !== 'string' || pictureUrl.startsWith('http') === false) {
            pictureUrl = '/user (1).png'; // ใช้รูป user (1).png ถ้าไม่มีรูปจริง
          }
          setProfile({
            name: profileData.name,
            email: profileData.email,
            picture: pictureUrl
          });
        }
      })
      .catch(err => setError('เกิดข้อผิดพลาดในการโหลดข้อมูล'))
      .finally(() => setIsLoading(false));
  }, []);

  if (isLoading) return <div className="dashboard-loading"><div className="loading-spinner"></div><p>กำลังโหลดข้อมูล...</p></div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="dashboard trainer-dashboard-bg" style={themePurple}>
      {/* Header */}
      <header style={{
        display: 'flex', justifyContent: 'space-between', alignItems: 'center',
        padding: '1.5rem 2.5rem 1rem 2.5rem', background: 'white', boxShadow: '0 2px 8px -2px #a855f722', borderRadius: '0 0 32px 32px', marginBottom: '1.5rem'
      }}>
        <div style={{display: 'flex', alignItems: 'center', gap: '1rem'}}>
          <span style={{fontWeight: 900, fontSize: '2rem', color: '#a855f7', letterSpacing: '-0.03em'}}>FitAddict</span>
        </div>
        <div style={{display: 'flex', alignItems: 'center', gap: '1.25rem'}}>
          {/* Google profile badge */}
          <div style={{display: 'flex', alignItems: 'center', gap: '0.75rem'}}>
            <img src={profile.picture} alt="avatar" style={{width: 40, height: 40, borderRadius: '50%', objectFit: 'cover', background: '#ede9fe'}} onError={e => { e.target.onerror = null; e.target.src = '/user (1).png'; }} />
            <div style={{display: 'flex', flexDirection: 'column'}}>
              <span style={{fontWeight: 600, color: '#334155'}}>{profile.name}</span>
              <span style={{fontSize: '0.95rem', color: '#64748b'}}>{profile.email}</span>
            </div>
          </div>
          <button style={{background: 'linear-gradient(90deg,#a855f7,#6366f1)', color: 'white', fontWeight: 700, border: 'none', borderRadius: 12, padding: '0.5rem 1.25rem', cursor: 'pointer'}}>ออกจากระบบ</button>
        </div>
      </header>

      {/* Tab Bar */}
      <nav className="trainer-tabbar" style={{
        display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '2rem',
        background: 'rgba(255,255,255,0.85)',
        boxShadow: '0 2px 8px -2px #a855f722',
        borderRadius: '0 0 24px 24px',
        padding: '1rem 0', marginBottom: '2rem', position: 'sticky', top: 0, zIndex: 10
      }}>
        <button className="tab-btn" style={{background: 'none', border: 'none', color: '#7c3aed', fontWeight: 700, fontSize: '1.1rem', cursor: 'pointer'}}>Dashboard</button>
        <button className="tab-btn" style={{background: 'none', border: 'none', color: '#6366f1', fontWeight: 700, fontSize: '1.1rem', cursor: 'pointer'}}>อัพวิดีโอ</button>
        <button className="tab-btn" style={{background: 'none', border: 'none', color: '#a855f7', fontWeight: 700, fontSize: '1.1rem', cursor: 'pointer'}}>Invite Trainee</button>
        <button className="tab-btn" style={{background: 'none', border: 'none', color: '#64748b', fontWeight: 700, fontSize: '1.1rem', cursor: 'pointer'}}>Profile</button>
      </nav>

      {/* Welcome Section */}
      <section style={{textAlign: 'center', marginBottom: '2.5rem'}}>
        <div style={{background: 'white', borderRadius: 24, boxShadow: '0 2px 12px -2px #a855f722', padding: '2rem 1rem', maxWidth: 900, margin: '0 auto 2rem auto'}}>
          <h2 style={{fontSize: '2rem', fontWeight: 800, color: '#334155', marginBottom: '0.5rem'}}>ยินดีต้อนรับ, preeyanut chitkot!</h2>
          <p style={{color: '#64748b', fontSize: '1.15rem'}}>นี่คือหน้าสำหรับ Trainer คุณสามารถดูสรุปและจัดการระบบของคุณได้ที่นี่</p>
        </div>
      </section>

      {/* Card Section */}
      <section style={{display: 'flex', justifyContent: 'center', gap: '2rem', marginBottom: '2.5rem', flexWrap: 'wrap'}}>
        <div style={{background: 'white', borderRadius: 18, boxShadow: '0 4px 12px -2px #a855f722', minWidth: 280, padding: '1.5rem 1.25rem', flex: '1 1 280px', maxWidth: 350}}>
          <h3 style={{fontWeight: 700, color: '#334155', marginBottom: '0.5rem'}}>วิดีโอของคุณ</h3>
          <p style={{color: '#64748b', marginBottom: '1rem'}}>ดูและจัดการวิดีโอที่คุณอัพโหลด</p>
          <button style={{background: 'linear-gradient(90deg,#a855f7,#6366f1)', color: 'white', fontWeight: 700, border: 'none', borderRadius: 12, padding: '0.5rem 1.25rem', cursor: 'pointer'}}>ดูวิดีโอทั้งหมด</button>
        </div>
        <div style={{background: 'white', borderRadius: 18, boxShadow: '0 4px 12px -2px #a855f722', minWidth: 280, padding: '1.5rem 1.25rem', flex: '1 1 280px', maxWidth: 350}}>
          <h3 style={{fontWeight: 700, color: '#334155', marginBottom: '0.5rem'}}>Invite Trainee</h3>
          <p style={{color: '#64748b', marginBottom: '1rem'}}>เชิญผู้ใช้ใหม่เข้าระบบของคุณ</p>
          <button style={{background: 'linear-gradient(90deg,#a855f7,#6366f1)', color: 'white', fontWeight: 700, border: 'none', borderRadius: 12, padding: '0.5rem 1.25rem', cursor: 'pointer'}}>เชิญผู้ใช้</button>
        </div>
        <div style={{background: 'white', borderRadius: 18, boxShadow: '0 4px 12px -2px #a855f722', minWidth: 280, padding: '1.5rem 1.25rem', flex: '1 1 280px', maxWidth: 350}}>
          <h3 style={{fontWeight: 700, color: '#334155', marginBottom: '0.5rem'}}>Trainee</h3>
          <p style={{color: '#64748b', marginBottom: '1rem'}}>ดูรายงานผู้เข้าอบรมของคุณ</p>
          <button style={{background: 'linear-gradient(90deg,#a855f7,#6366f1)', color: 'white', fontWeight: 700, border: 'none', borderRadius: 12, padding: '0.5rem 1.25rem', cursor: 'pointer'}}>ดูรายงานผู้เข้าอบรม</button>
        </div>
      </section>

      {/* Dashboard analytics and trainee report sections */}
      <>
        {/* Row 1: Video + Analytics */}
        <section className="trainer-section" style={{marginBottom: '2.5rem'}}>
          <div className="dashboard-row" style={{display: 'flex', justifyContent: 'center', alignItems: 'flex-start', gap: '1.5rem', maxWidth: 1100, margin: '0 auto'}}>
            {/* Video card */}
            {videos.length === 0 ? (
              <div className="stat-card" style={{background: 'rgba(255,255,255,0.95)', border: '2px solid #e9d5ff', boxShadow: '0 4px 12px -2px #a855f722', borderRadius: 18, minWidth: 280, maxWidth: 350, flex: '1 1 280px'}}>
                <h2 className="trainer-section-title" style={{color: '#6366f1', fontSize: '1.25rem', marginBottom: '1rem'}}>วิดีโอของคุณ (รายละเอียด)</h2>
                <h3 style={{color: '#7c3aed', fontWeight: 700}}>ตัวอย่างวิดีโอ</h3>
                <div style={{display: 'flex', alignItems: 'center', gap: '0.5rem'}}>
                  <p style={{color: '#64748b', marginBottom: 0}}>ถูกใช้ 12 ครั้ง</p>
                  <span className="popular-badge" style={{background: 'linear-gradient(90deg,#a855f7,#6366f1)', color: 'white', padding: '0.25rem 0.75rem', borderRadius: 12, fontWeight: 600, fontSize: '0.95rem'}}>🔥 Popular</span>
                </div>
              </div>
            ) : (
              videos.map(v => (
                <div className="stat-card video-card" key={v.id} style={{background: 'rgba(255,255,255,0.95)', border: '2px solid #e9d5ff', boxShadow: '0 4px 12px -2px #a855f722', borderRadius: 18, minWidth: 280, maxWidth: 350, flex: '1 1 280px'}}>
                  <h2 className="trainer-section-title" style={{color: '#6366f1', fontSize: '1.25rem', marginBottom: '1rem'}}>วิดีโอของคุณ (รายละเอียด)</h2>
                  <h3 style={{color: '#7c3aed', fontWeight: 700}}>{v.title}</h3>
                  <p style={{color: '#64748b'}}>ถูกใช้ {v.usage_count} ครั้ง</p>
                  {v.is_popular && <span className="popular-badge" style={{background: 'linear-gradient(90deg,#a855f7,#6366f1)', color: 'white', padding: '0.25rem 0.75rem', borderRadius: 12, fontWeight: 600, fontSize: '0.95rem'}}>🔥 Popular</span>}
                </div>
              ))
            )}
            {/* Analytics cards */}
            {summary ? (
              <>
                <div className="stat-card" style={{background: 'rgba(255,255,255,0.95)', border: '2px solid #e9d5ff', boxShadow: '0 4px 12px -2px #a855f722', borderRadius: 18, minWidth: 280, maxWidth: 350, flex: '1 1 280px'}}>
                  <h2 className="trainer-section-title" style={{color: '#6366f1', fontSize: '1.25rem', marginBottom: '1rem'}}>สรุปการใช้งานวิดีโอ</h2>
                  <h3 style={{color: '#7c3aed', fontWeight: 700}}>วิดีโอที่ถูกใช้มากที่สุด</h3>
                  <p style={{color: '#64748b'}}>{summary.most_popular_title} ({summary.most_popular_count} ครั้ง)</p>
                </div>
                <div className="stat-card" style={{background: 'rgba(255,255,255,0.95)', border: '2px solid #e9d5ff', boxShadow: '0 4px 12px -2px #a855f722', borderRadius: 18, minWidth: 280, maxWidth: 350, flex: '1 1 280px'}}>
                  <h2 className="trainer-section-title" style={{color: '#6366f1', fontSize: '1.25rem', marginBottom: '1rem'}}>สรุปการใช้งานวิดีโอ</h2>
                  <h3 style={{color: '#7c3aed', fontWeight: 700}}>จำนวนการใช้งานรวม</h3>
                  <p style={{color: '#64748b'}}>{summary.total_usage} ครั้ง</p>
                </div>
              </>
            ) : (
              <>
                <div className="stat-card" style={{minWidth: 280, maxWidth: 350, flex: '1 1 280px'}}>
                  <h2 className="trainer-section-title" style={{color: '#6366f1', fontSize: '1.25rem', marginBottom: '1rem'}}>สรุปการใช้งานวิดีโอ</h2>
                  <h3 style={{color: '#7c3aed', fontWeight: 700}}>วิดีโอที่ถูกใช้มากที่สุด</h3>
                  <p style={{color: '#64748b'}}>ตัวอย่างวิดีโอ (8 ครั้ง)</p>
                </div>
                <div className="stat-card" style={{minWidth: 280, maxWidth: 350, flex: '1 1 280px'}}>
                  <h2 className="trainer-section-title" style={{color: '#6366f1', fontSize: '1.25rem', marginBottom: '1rem'}}>สรุปการใช้งานวิดีโอ</h2>
                  <h3 style={{color: '#7c3aed', fontWeight: 700}}>จำนวนการใช้งานรวม</h3>
                  <p style={{color: '#64748b'}}>20 ครั้ง</p>
                </div>
              </>
            )}
          </div>
        </section>
        {/* Row 2: Trainee report full width */}
        <section className="trainer-section">
          <div className="trainer-card-list" style={{display: 'flex', justifyContent: 'center', alignItems: 'flex-start', gap: '1.5rem', flexWrap: 'wrap', maxWidth: 1100, margin: '0 auto'}}>
            {traineeReport.length === 0 ? (
              <div className="stat-card" style={{minWidth: 250, flex: '3 1 750px', maxWidth: '100%'}}>
                <h2 className="trainer-section-title" style={{color: '#6366f1', fontSize: '1.25rem', marginBottom: '1rem'}}>รายงานผู้ใช้ (Trainee)</h2>
                <h3 style={{color: '#7c3aed', fontWeight: 700}}>ตัวอย่างผู้ใช้ 1</h3>
                <p style={{color: '#64748b'}}>ออกกำลังกาย 5 ครั้ง</p>
                <p style={{color: '#64748b'}}>Accuracy เฉลี่ย: <span className="accuracy-badge" style={{background: '#ede9fe', color: '#7c3aed', padding: '0.25rem 0.75rem', borderRadius: 12, fontWeight: 600}}>92.5</span></p>
                <button className="action-btn" style={{background: 'linear-gradient(90deg,#a855f7,#6366f1)', color: 'white', fontWeight: 600, border: 'none', borderRadius: 12, padding: '0.5rem 1.25rem', marginTop: '0.5rem', cursor: 'pointer'}}>Subscribe</button>
              </div>
            ) : (
              <div className="stat-card trainee-card" style={{background: 'rgba(255,255,255,0.95)', border: '2px solid #e9d5ff', boxShadow: '0 4px 12px -2px #a855f722', borderRadius: 18, minWidth: 250, flex: '3 1 750px', maxWidth: '100%'}}>
                <h2 className="trainer-section-title" style={{color: '#6366f1', fontSize: '1.25rem', marginBottom: '1rem'}}>รายงานผู้ใช้ (Trainee)</h2>
                {traineeReport.map(t => (
                  <div key={t.id} style={{marginBottom: '1.5rem'}}>
                    <h3 style={{color: '#7c3aed', fontWeight: 700}}>{t.name}</h3>
                    <p style={{color: '#64748b'}}>ออกกำลังกาย {t.workout_count} ครั้ง</p>
                    <p style={{color: '#64748b'}}>Accuracy เฉลี่ย: <span className="accuracy-badge" style={{background: '#ede9fe', color: '#7c3aed', padding: '0.25rem 0.75rem', borderRadius: 12, fontWeight: 600}}>{t.avg_accuracy ? t.avg_accuracy.toFixed(2) : '-'}</span></p>
                    <button className="action-btn" style={{background: 'linear-gradient(90deg,#a855f7,#6366f1)', color: 'white', fontWeight: 600, border: 'none', borderRadius: 12, padding: '0.5rem 1.25rem', marginTop: '0.5rem', cursor: 'pointer'}}>Subscribe</button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </section>
      </>
    </div>
  );
}

export default TrainerDashboard;
