// ...existing code from TrainerPage.js...
import React, { useEffect, useState } from 'react';
import '../components/Dashboard.css';
import TrainerHeader from '../components/TrainerHeader';
import VideoCard from '../components/VideoCard';

import InviteTrainee from '../components/InviteTrainee';

const themePurple = {
  background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)',
  minHeight: '100vh',
  fontFamily: 'Inter, sans-serif',
};

function TrainerPage() {
  const [videos, setVideos] = useState([]);
  const [summary, setSummary] = useState(null);
  const [traineeReport, setTraineeReport] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [profile, setProfile] = useState({ name: '', email: '', picture: '' });


  // ✅ เพิ่มรายการลูกเทรนจริง
  const [trainees, setTrainees] = useState([]);

  // ✅ ดึงลูกเทรนจาก BE
  const fetchTrainees = async (token) => {
    try {
      const res = await fetch('http://localhost:8000/my-trainees', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (!res.ok) throw new Error('โหลดรายชื่อสมาชิกไม่สำเร็จ');
      const data = await res.json();
      setTrainees(Array.isArray(data) ? data : []);
    } catch (e) {
      console.error(e);
    }
  };




  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoading(true);
    // ดึง user จาก localStorage (เหมือน trainee)
    const userData = localStorage.getItem('user');
    let userPic = '';
    let userName = '';
    if (userData) {
      try {
        const userObj = JSON.parse(userData);
        userPic = userObj.picture || '';
        userName = userObj.name || '';
      } catch {}
    }
    // Fetch all trainers (เลือก trainer ที่ email ตรงกับ user ที่ login)
   fetch('http://localhost:8000/trainers', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(trainers => {
        let trainer = null;
        if (Array.isArray(trainers) && trainers.length > 0) {
          if (userData) {
            try {
              const userObj = JSON.parse(userData);
              trainer = trainers.find(t => t.email === userObj.email) || trainers[0];
            } catch {
              trainer = trainers[0];
            }
          } else {
            trainer = trainers[0];
          }
        }
        if (trainer) {
          setProfile({
            name: userName || trainer.name,
            email: trainer.email,
            picture: userPic || '/user (1).png'
          });
          return fetch(`http://localhost:8000/trainers/${trainer.id}`, {
            headers: { Authorization: `Bearer ${token}` }
          });
        } else {
          throw new Error('ไม่พบข้อมูลเทรนเนอร์');
        }
      })
      .then(res => res.json())
      .then(profileData => {
        setSummary({ most_popular_title: 'N/A', most_popular_count: 0, total_usage: 0 });
        setTraineeReport([]);
        setVideos([]);
      })
      .catch(() => setError('เกิดข้อผิดพลาดในการโหลดข้อมูลเทรนเนอร์'))
      .finally(() => setIsLoading(false));

    // ✅ โหลดลูกเทรนจริงคู่ขนาน
    if (token) fetchTrainees(token);
  }, []);



  if (isLoading) return <div className="dashboard-loading"><div className="loading-spinner"></div><p>กำลังโหลดข้อมูล...</p></div>;
  if (error) return <div className="error-message">{error}</div>;

  // mock data แบบ trainee
  const mockVideos = [
    { id: 1, title: 'Pushups', image: 'https://images.pexels.com/photos/2261482/pexels-photo-2261482.jpeg', duration: '30.20', kcal: 20, score: 95, statusBtn: 'Active' },
    { id: 2, title: 'Pushups', image: 'https://images.pexels.com/photos/2261482/pexels-photo-2261482.jpeg', duration: '30.20', kcal: 20, score: 95, statusBtn: 'Active' },
    { id: 3, title: 'Pushups', image: 'https://images.pexels.com/photos/2261482/pexels-photo-2261482.jpeg', duration: '30.20', kcal: 20, score: 95, statusBtn: 'Active' },
    { id: 4, title: 'Pushups', image: 'https://images.pexels.com/photos/2261482/pexels-photo-2261482.jpeg', duration: '30.20', kcal: 20, score: 95, statusBtn: 'Active' },
  ];
  return (
    <div className="dashboard trainer-dashboard-bg" style={themePurple}>
      {/* Header (TrainerHeader ไม่มี pts) */}
      <TrainerHeader
        user={{
          name: profile.name,
          picture: profile.picture || '/user (1).png',
        }}
        date={new Date().toLocaleDateString('en-GB', { day: 'numeric', month: 'long', year: 'numeric' })}
      />
      <div style={{
        background: '#f3f4f6',
        borderRadius: 20,
        boxShadow: '0 2px 24px rgba(0,0,0,0.10)',
        maxWidth: 1400,
        margin: '2rem auto',
        padding: '2.5rem 3rem 3rem 3rem',
        minHeight: 800,
        display: 'flex',
        flexDirection: 'column',
        gap: 32
      }}>






        {/* Layout: Memberlist + Videolist */}
        <div style={{display:'flex',gap:40,alignItems:'flex-start',padding:'0'}}>
          {/* Memberlist */}
         {/* === Memberlist === */}
          <aside style={{background:'#fff',borderRadius:24,padding:'2.2rem 1.7rem',boxShadow:'0 2px 16px rgba(0,0,0,0.06)',minWidth:340,maxWidth:380,flex:'0 0 350px',display:'flex',flexDirection:'column',gap:20}}>
            {/* Trainer info (avatar + name) */}
            <div style={{display:'flex',alignItems:'center',gap:18,marginBottom:10}}>
              <img src={profile.picture} alt="avatar" style={{width:56,height:56,borderRadius:'50%',objectFit:'cover',border:'2.5px solid #e5e7eb'}} />
              <div style={{fontWeight:800,fontSize:'1.45rem',color:'#23272e'}}>{profile.name}</div>
              <span style={{width:14,height:14,borderRadius:'50%',background:'#22c55e',display:'inline-block',marginLeft:8}}></span>
            </div>

            <hr style={{border:'none',borderTop:'2px solid #e5e7eb',margin:'0 0 18px 0'}} />

            {/* นับจำนวนจากข้อมูลจริง */}
            <div style={{fontWeight:700,fontSize:'1.18rem',marginBottom:10}}>
              Memberlist ({trainees.length})
            </div>

            {/* กล่องเชิญ + รีเฟรชลิสต์หลังเชิญสำเร็จ */}
            <InviteTrainee onSuccess={fetchTrainees} />

            {/* รายชื่อลูกเทรนจริง */}
            <div style={{flex:1,overflowY:'auto',maxHeight:520}}>
              {trainees.length === 0 ? (
                <div style={{color:'#64748b'}}>ยังไม่มีสมาชิกในทีม</div>
              ) : (
                trainees.map((t) => (
                  <div key={t.id} style={{display:'flex',alignItems:'center',gap:14,marginBottom:14}}>
                    <img
                      src={'/user (1).png'}
                      alt="avatar"
                      style={{width:40,height:40,borderRadius:'50%',objectFit:'cover',border:'2px solid #e5e7eb'}}
                    />
                    <div style={{display:'flex',flexDirection:'column'}}>
                      <div style={{fontWeight:600,fontSize:'1.05rem'}}>{t.name || 'Unnamed'}</div>
                      <div style={{fontSize:'0.92rem',color:'#6b7280'}}>{t.email}</div>
                    </div>
                    <span style={{width:12,height:12,borderRadius:'50%',background:'#22c55e',display:'inline-block',marginLeft:'auto',marginRight:10}} />
                    <span style={{fontSize:24,color:'#64748b',cursor:'pointer'}}>⋮</span>
                  </div>
                ))
              )}
            </div>
          </aside>












          {/* Videolist */}
          <main style={{flex:1}}>
            <div style={{display:'flex',justifyContent:'space-between',alignItems:'center',marginBottom:22}}>
              <div style={{fontWeight:700,fontSize:'1.18rem'}}>Videolist (4)</div>
              <button style={{background:'linear-gradient(90deg,#ff4d8b 0%, #a855f7 100%)',color:'#fff',fontWeight:700,border:'none',borderRadius:18,padding:'0.6rem 1.7rem',fontSize:'1.05rem',display:'flex',alignItems:'center',gap:9,cursor:'pointer'}}>
                <img src={process.env.PUBLIC_URL + '/UploadSimple.png'} alt="upload" style={{width:20,height:20,marginRight:6,verticalAlign:'middle'}} /> Upload
              </button>
            </div>
            {/* Video cards */}
            <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fit,minmax(320px,1fr))',gap:28}}>
              {mockVideos.map(v => <VideoCard key={v.id} video={v} />)}
            </div>
          </main>
        </div>
      </div>
    </div>
  );
}

export default TrainerPage;
