import React, { useRef, useEffect, useState } from 'react';
import { Pose } from '@mediapipe/pose';
import { Camera } from '@mediapipe/camera_utils';

const KeypointOverlay = ({ videoRef, isPlaying, onKeypointsDetected }) => {
  const canvasRef = useRef(null);
  const [isInitialized, setIsInitialized] = useState(false);
  const [pose, setPose] = useState(null);

  // MediaPipe Pose connections (33 landmarks)
  const POSE_CONNECTIONS = [
    // Face
    [0, 1], [1, 2], [2, 3], [3, 7], [0, 4], [4, 5], [5, 6], [6, 8],
    // Arms
    [9, 10], [11, 12], [11, 13], [13, 15], [15, 17], [15, 19], [15, 21], [17, 19],
    [12, 14], [14, 16], [16, 18], [16, 20], [16, 22], [18, 20],
    // Body
    [11, 23], [12, 24], [23, 24],
    // Legs
    [23, 25], [24, 26], [25, 27], [26, 28], [27, 29], [28, 30], [29, 31], [30, 32], [27, 31], [28, 32]
  ];

  useEffect(() => {
    const initializeMediaPipe = async () => {
      try {
        console.log('Initializing MediaPipe Pose...');
        
        // สร้าง Pose instance จาก imported package
        const poseInstance = new Pose({
          locateFile: (file) => {
            // ใช้ path ที่ถูกต้องสำหรับ local package
            return `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`;
          }
        });

        // ตั้งค่า options
        poseInstance.setOptions({
          modelComplexity: 1,
          smoothLandmarks: true,
          enableSegmentation: false,
          smoothSegmentation: true,
          minDetectionConfidence: 0.5,
          minTrackingConfidence: 0.5
        });

        // ตั้งค่า callback สำหรับผลลัพธ์
        poseInstance.onResults(onResults);
        
        setPose(poseInstance);
        setIsInitialized(true);
        console.log('MediaPipe Pose initialized successfully');
        
      } catch (error) {
        console.error('Failed to initialize MediaPipe:', error);
        
        // ลองใช้วิธีทางเลือกด้วย window object
        try {
          await initializeFallbackMediaPipe();
        } catch (fallbackError) {
          console.error('Fallback MediaPipe initialization also failed:', fallbackError);
        }
      }
    };

    const initializeFallbackMediaPipe = async () => {
      console.log('Trying fallback MediaPipe initialization...');
      
      // ลองใช้ dynamic import
      try {
        const mediapipePose = await import('@mediapipe/pose');
        const poseInstance = new mediapipePose.Pose({
          locateFile: (file) => {
            return `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`;
          }
        });

        poseInstance.setOptions({
          modelComplexity: 1,
          smoothLandmarks: true,
          enableSegmentation: false,
          smoothSegmentation: true,
          minDetectionConfidence: 0.5,
          minTrackingConfidence: 0.5
        });

        poseInstance.onResults(onResults);
        setPose(poseInstance);
        setIsInitialized(true);
        console.log('MediaPipe Pose initialized via dynamic import');
        
      } catch (importError) {
        console.error('Dynamic import failed:', importError);
        throw importError;
      }
    };

    initializeMediaPipe();
  }, []);

  // Cleanup effect
  useEffect(() => {
    return () => {
      if (pose) {
        pose.close();
      }
    };
  }, [pose]);

  // Helper function to load scripts
  const loadScript = (src) => {
    return new Promise((resolve, reject) => {
      const existingScript = document.querySelector(`script[src="${src}"]`);
      if (existingScript) {
        // ถ้า script โหลดแล้วให้ resolve ทันที
        if (existingScript.dataset.loaded === 'true') {
          resolve();
        } else {
          // ถ้ายังโหลดไม่เสร็จให้รอ
          existingScript.addEventListener('load', resolve);
          existingScript.addEventListener('error', reject);
        }
        return;
      }
      
      const script = document.createElement('script');
      script.src = src;
      script.crossOrigin = 'anonymous';
      
      script.onload = () => {
        script.dataset.loaded = 'true';
        console.log(`Script loaded: ${src}`);
        resolve();
      };
      
      script.onerror = (error) => {
        console.error(`Failed to load script: ${src}`, error);
        reject(new Error(`Failed to load script: ${src}`));
      };
      
      document.head.appendChild(script);
    });
  };

  useEffect(() => {
    if (!isInitialized || !pose || !videoRef?.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    
    const processFrame = async () => {
      if (video.paused || video.ended || !isPlaying) return;
      
      try {
        // Match canvas size to video element's display size exactly
        const rect = video.getBoundingClientRect();
        if (canvas.width !== rect.width || canvas.height !== rect.height) {
          canvas.width = rect.width;
          canvas.height = rect.height;
        }
        
        // Send frame to MediaPipe
        await pose.send({ image: video });
      } catch (error) {
        console.error('Pose detection error:', error);
      }

      if (isPlaying && !video.paused && !video.ended) {
        requestAnimationFrame(processFrame);
      }
    };

    if (isPlaying) {
      if (video.readyState >= 2) {
        processFrame();
      } else {
        video.addEventListener('loadeddata', processFrame, { once: true });
      }
    }

    // Clean up canvas when not playing
    if (!isPlaying) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
  }, [isPlaying, isInitialized, pose]);

  const onResults = (results) => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    if (!canvas || !video || !results.poseLandmarks) return;

    try {
      drawPoseMediaPipe(results.poseLandmarks, canvas, video);
      
      if (onKeypointsDetected) {
        onKeypointsDetected(results.poseLandmarks);
      }
    } catch (error) {
      console.error('Drawing error:', error);
    }
  };

  const drawPoseMediaPipe = (landmarks, canvas, video) => {
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Get actual video dimensions and display dimensions
    const videoRect = video.getBoundingClientRect();
    const videoWidth = video.videoWidth;
    const videoHeight = video.videoHeight;
    const displayWidth = videoRect.width;
    const displayHeight = videoRect.height;

    // Calculate aspect ratios
    const videoAspect = videoWidth / videoHeight;
    const displayAspect = displayWidth / displayHeight;

    // Calculate actual video display area within the video element
    let actualVideoWidth, actualVideoHeight, offsetX = 0, offsetY = 0;

    if (videoAspect > displayAspect) {
      // Video is wider - letterboxed top/bottom
      actualVideoWidth = displayWidth;
      actualVideoHeight = displayWidth / videoAspect;
      offsetY = (displayHeight - actualVideoHeight) / 2;
    } else {
      // Video is taller - letterboxed left/right
      actualVideoHeight = displayHeight;
      actualVideoWidth = displayHeight * videoAspect;
      offsetX = (displayWidth - actualVideoWidth) / 2;
    }

    // MediaPipe landmarks are normalized (0-1), scale to actual video area
    const scaleX = actualVideoWidth;
    const scaleY = actualVideoHeight;

    // Draw skeleton connections
    POSE_CONNECTIONS.forEach(([i, j]) => {
      const kp1 = landmarks[i];
      const kp2 = landmarks[j];
      
      if (kp1 && kp2 && kp1.visibility > 0.5 && kp2.visibility > 0.5) {
        ctx.beginPath();
        // Mirror x-coordinates to match the mirrored video
        const x1 = actualVideoWidth - (kp1.x * scaleX) + offsetX;
        const y1 = kp1.y * scaleY + offsetY;
        const x2 = actualVideoWidth - (kp2.x * scaleX) + offsetX;
        const y2 = kp2.y * scaleY + offsetY;
        
        ctx.moveTo(x1, y1);
        ctx.lineTo(x2, y2);
        ctx.strokeStyle = '#00FF00';
        ctx.lineWidth = 2;
        ctx.stroke();
      }
    });

    // Draw keypoints with different colors
    landmarks.forEach((landmark, index) => {
      if (landmark && landmark.visibility > 0.5) {
        // Mirror x-coordinates to match the mirrored video
        const x = actualVideoWidth - (landmark.x * scaleX) + offsetX;
        const y = landmark.y * scaleY + offsetY;
        
        ctx.beginPath();
        ctx.arc(x, y, 3, 0, 2 * Math.PI);
        
        // Different colors for different body parts
        if (index <= 10) {
          ctx.fillStyle = '#FF6B6B'; // Face and upper body
        } else if (index <= 16) {
          ctx.fillStyle = '#4ECDC4'; // Arms
        } else if (index <= 22) {
          ctx.fillStyle = '#45B7D1'; // Hands
        } else {
          ctx.fillStyle = '#96CEB4'; // Legs and body
        }
        
        ctx.fill();
        
        // Add white border for better visibility
        ctx.beginPath();
        ctx.arc(x, y, 3, 0, 2 * Math.PI);
        ctx.strokeStyle = '#FFFFFF';
        ctx.lineWidth = 1;
        ctx.stroke();
      }
    });
  };

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 10
      }}
    />
  );
};

export default KeypointOverlay;
