import React, { useEffect, useState } from 'react';
import './Dashboard.css';
// เพิ่มสไตล์ธีมม่วงแบบหน้า trainee
const themePurple = {
  background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)',
  minHeight: '100vh',
  fontFamily: 'Inter, sans-serif',
};

const TrainerDashboard = () => {
  // Removed videos, summary, and traineeReport state as requested
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [profile, setProfile] = useState({ name: '', email: '', picture: '' });

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoading(true);
  const apiUrl = process.env.REACT_APP_BACKEND_URL || 'http://localhost:8000';
  fetch(`${apiUrl}/me`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(profileData => {
        if (profileData && profileData.name) {
          let pictureUrl = profileData.picture;
          if (!pictureUrl || typeof pictureUrl !== 'string' || pictureUrl.startsWith('http') === false) {
            pictureUrl = '/user (1).png';
          }
          setProfile({
            name: profileData.name,
            email: profileData.email,
            picture: pictureUrl
          });
        }
      })
      .catch(err => setError('เกิดข้อผิดพลาดในการโหลดข้อมูล'))
      .finally(() => setIsLoading(false));
  }, []);

  if (isLoading) return <div className="dashboard-loading"><div className="loading-spinner"></div><p>กำลังโหลดข้อมูล...</p></div>;
  if (error) return <div className="error-message">{error}</div>;

  // ฟังก์ชัน logout
  const handleLogout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <div className="dashboard trainer-dashboard-bg" style={themePurple}>
      {/* Header */}
      <nav className="dashboard-nav">
        <div className="dashboard-logo">FitAddict</div>
        <div className="dashboard-user">
          <img src={profile.picture} alt="avatar" className="user-avatar" style={{width: 40, height: 40, borderRadius: '50%', objectFit: 'cover', background: '#ede9fe'}} onError={e => { e.target.onerror = null; e.target.src = '/user (1).png'; }} />
          <span className="user-name" style={{ fontWeight: 700, fontSize: "1.1rem", color: "#1e293b" }}>{profile.name}</span>
          <button className="logout-btn" style={{ marginLeft: "1.5rem", background: "#f1f5f9", color: "#64748b", fontWeight: 500, border: "none", borderRadius: "100px", padding: "0.5rem 1.25rem", cursor: "pointer", fontSize: "0.95rem", transition: "all 0.3s" }} onClick={handleLogout}>ออกจากระบบ</button>
        </div>
      </nav>

      {/* Welcome Section */}
      <main className="dashboard-main">
        <div className="dashboard-welcome">
          <h1>ยินดีต้อนรับ, {profile.name}!</h1>
          <p>นี่คือหน้าสำหรับ Trainer คุณสามารถดูสรุปและจัดการระบบของคุณได้ที่นี่</p>
        </div>

        {/* Card Section (removed video and trainee-report cards as requested) */}
        <div className="dashboard-stats">
          <div className="stat-card">
            <h3>
              <span className="material-icons">person_add</span>
              Invite Trainee
            </h3>
            <p>เชิญผู้ใช้ใหม่เข้าระบบของคุณ</p>
            <button className="action-btn">เชิญผู้ใช้</button>
          </div>
          <div className="stat-card">
            <h3>
              <span className="material-icons">groups</span>
              Trainee
            </h3>
            <p>ดูรายงานผู้เข้าอบรมของคุณ</p>
            <button className="action-btn">ดูรายงานผู้เข้าอบรม</button>
          </div>
        </div>

      
      </main>

 
    </div>
  );
}

export default TrainerDashboard;
