// (moved into component below)
// src/pages/UploadVideo.jsx

import React, { useEffect, useRef, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import TrainerHeader from "../components/TrainerHeader";

const box = (extra = {}) => ({
  background: "#fff",
  borderRadius: 16,
  boxShadow: "0 2px 12px rgba(0,0,0,0.06)",
  ...extra,
});

export default function UploadVideo() {
  const navigate = useNavigate();
  const location = useLocation();
  const fileRef = useRef(null);

  // NEW: แยกโหมดแก้ไขจาก state หรือ query (?id=)
  const editId = location.state?.videoId || new URLSearchParams(location.search).get("id");
  const isEdit = !!editId;

  // Restore draft from localStorage if exists (only if not editing)
  useEffect(() => {
    if (isEdit) return;
    const draft = localStorage.getItem("uploadVideoDraft");
    if (draft) {
      try {
        const d = JSON.parse(draft);
        setName(d.name || "");
        setKcal(d.kcal || "");
        setLevel(d.level || "1");
        // ไม่ restore file จริง (security)
      } catch {}
    }
  }, [isEdit]);

  // ฟอร์ม
  const [file, setFile] = useState(null);
  const [fileURL, setFileURL] = useState(""); // ใช้ preview (ทั้งไฟล์ใหม่/ของเดิม)
  const [name, setName] = useState("");
  const [kcal, setKcal] = useState("");
  const [level, setLevel] = useState("1");
  const [preview, setPreview] = useState(false);

  // NEW: กันกดซ้ำ + แสดงสถานะตอนกำลังส่ง
  const [submitting, setSubmitting] = useState(false);

  const onPick = () => fileRef.current?.click();
  const onFileChange = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    setFile(f);
    setFileURL(URL.createObjectURL(f));
  };
  const onDrop = (e) => {
    e.preventDefault();
    const f = e.dataTransfer.files?.[0];
    if (!f) return;
    setFile(f);
    setFileURL(URL.createObjectURL(f));
  };
  const onDragOver = (e) => e.preventDefault();

  // NEW: Prefill เมื่อเป็นโหมดแก้ไข (อ่านจาก state ก่อน, ไม่มีก็ค่อย GET /videos/:id)
  useEffect(() => {
    if (!isEdit) return;

    // ถ้ามีข้อมูลมากับ state ก็เติมเลย (เร็วกว่า)
    if (location.state?.video) {
      const v = location.state.video;
      setName(v.title || "");
      setLevel(String(v.difficulty || v.level || "1"));
      setKcal(
        v.kcal ??
          (typeof v.description === "string"
            ? (v.description.match(/kcal:(\d+)/)?.[1] ?? "")
            : "")
      );
      const url =
        v.image ||
        (v.s3_url
          ? /^https?:\/\//i.test(v.s3_url)
            ? v.s3_url
            : `http://localhost:8000/static/${v.s3_url.replace(/^.*[\\\/]/, "")}`
          : "");
      setFileURL(url);
      return;
    }

    // ไม่มีใน state -> ดึงจาก BE
    (async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) return; // ไม่ล็อกอินจะดึงไม่ได้
        const res = await fetch(`http://localhost:8000/videos/${editId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!res.ok) return;
        const v = await res.json();
        setName(v.title || "");
        setLevel(String(v.difficulty || v.level || "1"));
        setKcal(
          v.kcal ??
            (typeof v.description === "string"
              ? (v.description.match(/kcal:(\d+)/)?.[1] ?? "")
              : "")
        );
        const url =
          v.s3_url &&
          (!/^https?:\/\//i.test(v.s3_url)
            ? `http://localhost:8000/static/${v.s3_url.replace(/^.*[\\\/]/, "")}`
            : v.s3_url);
        setFileURL(url || "");
      } catch {}
    })();
  }, [isEdit, editId, location.state]);

  // CHANGED: อัปโหลดใหม่ (POST /videos) + เช็ค token + กันกดซ้ำ + แสดง error ชัดเจน
  const handleCreate = async () => {
    if (!file || !name) {
      alert("กรุณาเลือกไฟล์และกรอกชื่อวิดีโอ");
      return;
    }
    const token = localStorage.getItem("token");
    if (!token) {
      alert("ยังไม่ได้ล็อกอิน หรือ token หายไป (โปรดล็อกอินด้วยบัญชี Trainer)");
      return;
    }
    if (submitting) return;
    setSubmitting(true);

    const formData = new FormData();
    formData.append("file", file);
    formData.append("title", name);
    formData.append("difficulty", level);
    formData.append("description", `kcal:${kcal}`);

    try {
      const res = await fetch("http://localhost:8000/videos", {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` }, // NEW: ส่ง Bearer token
        body: formData,
      });
      if (!res.ok) {
        let detail = "";
        try {
          const data = await res.json();
          detail = data.detail || "";
        } catch {}
        if (res.status === 401) {
          alert(`อัพโหลดไม่สำเร็จ (401 Unauthorized)\n${detail || "Token ไม่ถูกต้องหรือไม่มีการส่ง"}`);
        } else if (res.status === 403) {
          alert("อัพโหลดไม่สำเร็จ (403) — เฉพาะ Trainer เท่านั้น");
        } else {
          alert(`อัพโหลดไม่สำเร็จ (${res.status}) ${detail}`);
        }
        return;
      }
      alert("อัพโหลดสำเร็จ!");
      navigate("/trainer");
    } catch (e) {
      alert("เกิดข้อผิดพลาดในการอัพโหลด: " + (e?.message || e));
    } finally {
      setSubmitting(false);
    }
  };

  // NEW: โหมดแก้ไข (PUT /videos/:id + ออปชัน PUT /videos/:id/file ถ้าเลือกไฟล์ใหม่)
  const handleUpdate = async () => {
    if (!name) {
      alert("กรุณากรอกชื่อวิดีโอ");
      return;
    }
    const token = localStorage.getItem("token");
    if (!token) {
      alert("ยังไม่ได้ล็อกอิน หรือ token หายไป (โปรดล็อกอินด้วยบัญชี Trainer)");
      return;
    }
    if (submitting) return;
    setSubmitting(true);

    // 1) อัปเดต metadata
    const metaRes = await fetch(`http://localhost:8000/videos/${editId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`, // NEW: ส่ง Bearer token
      },
      body: JSON.stringify({
        title: name,
        difficulty: level, // ถ้า BE ใช้ int: Number(level)
        description: `kcal:${kcal}`,
      }),
    });
    if (!metaRes.ok) {
      let detail = "";
      try {
        const data = await metaRes.json();
        detail = data.detail || "";
      } catch {}
      alert(`อัปเดตไม่สำเร็จ (${metaRes.status}) ${detail}`);
      setSubmitting(false);
      return;
    }

    // 2) ถ้าเลือกไฟล์ใหม่ -> อัปเดตไฟล์
    if (file) {
      const fd = new FormData();
      fd.append("file", file);
      const fileRes = await fetch(`http://localhost:8000/videos/${editId}/file`, {
        method: "PUT",
        headers: { Authorization: `Bearer ${token}` }, // NEW: ส่ง Bearer token
        body: fd,
      });
      if (!fileRes.ok) {
        let detail = "";
        try {
          const data = await fileRes.json();
          detail = data.detail || "";
        } catch {}
        alert(`อัปเดตไฟล์ไม่สำเร็จ (${fileRes.status}) ${detail}`);
        setSubmitting(false);
        return;
      }
    }

    alert("อัปเดตเรียบร้อย");
    setSubmitting(false);
    navigate("/trainer");
  };

  // header user
  const today = new Date().toLocaleDateString("en-GB", {
    day: "numeric",
    month: "long",
    year: "numeric",
  });
  let user = { name: "Trainer" };
  try {
    const userData = localStorage.getItem("user");
    if (userData) {
      const userObj = JSON.parse(userData);
      user = {
        name: userObj.name || "Trainer",
        picture: userObj.picture || "/user (1).png",
      };
    }
  } catch {}

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(135deg,#f8fafc 0%, #e2e8f0 100%)",
        fontFamily: "Inter, system-ui, sans-serif",
      }}
    >
      <TrainerHeader user={user} date={today} />

      {/* back button */}
      <div
        style={{
          maxWidth: 1200,
          margin: "0 auto",
          marginTop: "12px",
          marginBottom: "8px",
          display: "flex",
          alignItems: "center",
        }}
      >
        <button
          onClick={() => navigate("/trainer")}
          style={{
            display: "flex",
            alignItems: "center",
            gap: 8,
            background: "linear-gradient(90deg,#a855f7 0%, #ff4d8b 100%)",
            color: "#fff",
            fontWeight: 700,
            border: "none",
            borderRadius: 999,
            padding: "8px 22px 8px 16px",
            fontSize: "1.08rem",
            boxShadow: "0 2px 8px #a855f733",
            cursor: "pointer",
          }}
        >
          <svg
            width="20"
            height="20"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            style={{ marginRight: 4 }}
          >
            {/* ...svg content here... */}
          </svg>
        </button>
        <div style={box({ padding: 24, marginTop: 8 })}>
          <div
            onDrop={onDrop}
            onDragOver={onDragOver}
            style={{
              border: "2px dashed #d1d5db",
              borderRadius: 16,
              padding: "36px 20px",
              textAlign: "center",
              color: "#6b7280",
              background: "#f8fafc",
            }}
          >
            <div
              style={{
                fontSize: 18,
                fontWeight: 700,
                color: "#111827",
                marginBottom: 6,
              }}
            >
              {isEdit ? "Replace video (optional)" : "Select video to upload"}
            </div>
            <div style={{ marginBottom: 16 }}>Or drag and drop it here</div>
            <button
              onClick={onPick}
              style={{
                background: "linear-gradient(90deg,#ff4d8b 0%, #a855f7 100%)",
                color: "#fff",
                fontWeight: 700,
                border: "none",
                borderRadius: 10,
                padding: "10px 18px",
                cursor: "pointer",
              }}
            >
              Select video
            </button>
            <input
              ref={fileRef}
              type="file"
              accept="video/*"
              onChange={onFileChange}
              style={{ display: "none" }}
            />
            {file && (
              <div style={{ marginTop: 12, color: "#374151" }}>
                Selected: <b>{file.name}</b> ({Math.round(file.size / 1024 / 1024)} MB)
              </div>
            )}
          </div>
        </div>

        {/* Info row */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(4,1fr)",
            gap: 16,
            marginTop: 14,
          }}
        >
          {[
            { title: "Size and duration", desc: "Maximum size: 30 GB, video duration: 60 minutes" },
            { title: "File formats", desc: "Recommended: .mp4 (others supported)" },
            { title: "Video resolutions", desc: "Better: 1080p/1440p/4K" },
            { title: "Aspect ratios", desc: "Recommended: 16:9" },
          ].map((item, idx) => (
            <div key={idx} style={box({ padding: "14px 16px" })}>
              <div style={{ fontWeight: 700, color: "#111827", marginBottom: 6 }}>
                {item.title}
              </div>
              <div style={{ fontSize: 13, color: "#6b7280", lineHeight: 1.4 }}>
                {item.desc}
              </div>
            </div>
          ))}
        </div>

        {/* Details */}
        <div style={{ display: "grid", gridTemplateColumns: "1.1fr 1fr", gap: 20, marginTop: 24 }}>
          {/* Left form */}
          <div style={box({ padding: 20 })}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
              <div style={{ fontSize: 20, fontWeight: 800, color: "#111827" }}>
                {isEdit ? "Edit details" : "Details"}
              </div>
              <button
                onClick={() => setPreview((p) => !p)}
                style={{
                  border: "1.5px solid #e5e7eb",
                  background: "#fff",
                  color: "#a855f7",
                  borderRadius: 999,
                  padding: "8px 14px",
                  fontWeight: 700,
                  cursor: "pointer",
                }}
              >
                Preview
              </button>
            </div>

            <div style={{ marginTop: 18 }}>
              <label style={{ display: "block", fontWeight: 700, marginBottom: 6 }}>
                Name *
              </label>
              <input
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="e.g., Pushups – Beginner Form"
                style={{
                  width: "97%",
                  padding: "10px 12px",
                  borderRadius: 8,
                  border: "1px solid #e5e7eb",
                  outline: "none",
                }}
              />
            </div>

            <div style={{ marginTop: 14 }}>
              <label style={{ display: "block", fontWeight: 700, marginBottom: 6 }}>
                Kcalories *
              </label>
              <input
                type="number"
                min="0"
                value={kcal}
                onChange={(e) => setKcal(e.target.value)}
                placeholder="e.g., 60"
                style={{
                  width: 240,
                  padding: "10px 12px",
                  borderRadius: 8,
                  border: "1px solid #e5e7eb",
                  outline: "none",
                }}
              />
            </div>

            <div style={{ marginTop: 14 }}>
              <label style={{ display: "block", fontWeight: 700, marginBottom: 6 }}>
                Level
              </label>
              <select
                value={level}
                onChange={(e) => setLevel(e.target.value)}
                style={{
                  width: 180,
                  padding: "10px 12px",
                  borderRadius: 8,
                  border: "1px solid #e5e7eb",
                  outline: "none",
                  background: "#fff",
                }}
              >
                <option value="1">Level 1</option>
                <option value="2">Level 2</option>
                <option value="3">Level 3</option>
              </select>
            </div>

            {/* Action buttons */}
            <div style={{ display: "flex", gap: 12, marginTop: 20 }}>
              {isEdit ? (
                <button
                  onClick={handleUpdate}
                  disabled={submitting}                       // NEW: กันกดซ้ำ
                  style={{
                    opacity: submitting ? 0.7 : 1,            // NEW: ฟีดแบ็กกำลังส่ง
                    background: "linear-gradient(90deg,#ff4d8b 0%, #a855f7 100%)",
                    color: "#fff",
                    fontWeight: 700,
                    border: "none",
                    borderRadius: 10,
                    padding: "10px 16px",
                    cursor: submitting ? "not-allowed" : "pointer",
                  }}
                >
                  {submitting ? "Updating..." : "Update"}
                </button>
              ) : (
                <button
                  onClick={() => {
                    if (window.confirm("ต้องรอ admin อนุมัติคลิปก่อนจึงจะเป็น Active\nกดยืนยันเพื่อส่งคลิปเข้าสู่สถานะ Pending")) {
                      localStorage.removeItem("uploadVideoDraft");
                      handleCreate();
                    }
                  }}
                  disabled={submitting}
                  style={{
                    opacity: submitting ? 0.7 : 1,
                    background: "linear-gradient(90deg,#ff4d8b 0%, #a855f7 100%)",
                    color: "#fff",
                    fontWeight: 700,
                    border: "none",
                    borderRadius: 10,
                    padding: "10px 16px",
                    cursor: submitting ? "not-allowed" : "pointer",
                    display: "inline-flex",
                    alignItems: "center",
                    gap: 8,
                  }}
                >
                  <img
                    src={process.env.PUBLIC_URL + "/UploadSimple.png"}
                    alt="upload"
                    style={{
                      width: 20,
                      height: 20,
                      marginRight: 6,
                      verticalAlign: "middle",
                    }}
                  />
                  {submitting ? "Uploading..." : "Public"}
                </button>
              )}
              <button
                onClick={() => {
                  localStorage.setItem(
                    "uploadVideoDraft",
                    JSON.stringify({ name, kcal, level })
                  );
                  alert("บันทึกเป็น draft แล้ว (ยังไม่ส่งขึ้นระบบ)");
                }}
                disabled={submitting}
                style={{
                  background: "#e5e7eb",
                  color: "#111827",
                  fontWeight: 700,
                  border: "none",
                  borderRadius: 10,
                  padding: "10px 16px",
                  cursor: submitting ? "not-allowed" : "pointer",
                }}
              >
                Save draft
              </button>
              <button
                onClick={() => navigate(-1)}
                disabled={submitting}
                style={{
                  background: "#fff",
                  color: "#6b7280",
                  fontWeight: 700,
                  border: "1px solid #e5e7eb",
                  borderRadius: 10,
                  padding: "10px 16px",
                  cursor: submitting ? "not-allowed" : "pointer",
                }}
              >
                Discard
              </button>
            </div>
          </div>

          {/* Right preview pane */}
          <div
            style={box({
              padding: 16,
              minHeight: 300,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            })}
          >
            {!fileURL || !preview ? (
              <div
                style={{
                  width: "100%",
                  height: 320,
                  borderRadius: 12,
                  background: "#e5e7eb",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  color: "#6b7280",
                }}
              >
                {preview ? "No video selected" : "Preview area"}
              </div>
            ) : (
              <video
                src={fileURL}
                controls
                style={{ width: "100%", height: 320, objectFit: "cover", borderRadius: 12 }}
              />
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
