import React, { useState } from 'react';

const AdminLogin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const res = await fetch('http://localhost:8000/admin/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
        body: new URLSearchParams({ username, password })
      });
      const data = await res.json();
      if (res.ok && data.access_token) {
        localStorage.setItem('admin_token', data.access_token);
        window.location.href = '/admin';
      } else {
        setError(data.detail || 'Login failed');
      }
    } catch {
      setError('Network error');
    }
    setLoading(false);
  };

  return (
    <div style={{maxWidth:400,margin:'60px auto',padding:'2rem',background:'#fff',borderRadius:16,boxShadow:'0 2px 16px #a855f733'}}>
      <h2 style={{textAlign:'center',color:'#a855f7',fontWeight:800,marginBottom:'2rem'}}>Admin Login</h2>
      <form onSubmit={handleSubmit}>
        <div style={{marginBottom:18}}>
          <label style={{fontWeight:600}}>Username</label>
          <input type="text" value={username} onChange={e=>setUsername(e.target.value)} required style={{width:'100%',padding:'0.7rem',borderRadius:8,border:'1px solid #e5e7eb',marginTop:6}} />
        </div>
        <div style={{marginBottom:18}}>
          <label style={{fontWeight:600}}>Password</label>
          <input type="password" value={password} onChange={e=>setPassword(e.target.value)} required style={{width:'100%',padding:'0.7rem',borderRadius:8,border:'1px solid #e5e7eb',marginTop:6}} />
        </div>
        {error && <div style={{color:'#ef4444',marginBottom:12}}>{error}</div>}
        <button type="submit" disabled={loading} style={{width:'100%',background:'#a855f7',color:'#fff',fontWeight:700,padding:'0.8rem',border:'none',borderRadius:8,fontSize:'1.08rem',cursor:'pointer'}}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
      </form>
    </div>
  );
};

export default AdminLogin;
