import React, { useRef, useEffect, useState } from 'react';

const KeypointOverlay = ({ 
  videoRef, 
  isPlaying, 
  onKeypointsDetected,
  keypointColor = '#22c55e',
  connectionColor = '#16a34a',
  keypointSize = 6,
  connectionWidth = 3,
  showConnections = true,
  showKeypoints = true
}) => {
  const canvasRef = useRef(null);
  const [isInitialized, setIsInitialized] = useState(false);
  const [pose, setPose] = useState(null);
  const [previousLandmarks, setPreviousLandmarks] = useState(null);
  const [smoothedLandmarks, setSmoothedLandmarks] = useState(null);

  // MediaPipe Pose connections (33 landmarks)
  const POSE_CONNECTIONS = [
    // Face
    [0, 1], [1, 2], [2, 3], [3, 7], [0, 4], [4, 5], [5, 6], [6, 8],
    // Arms
    [9, 10], [11, 12], [11, 13], [13, 15], [15, 17], [15, 19], [15, 21], [17, 19],
    [12, 14], [14, 16], [16, 18], [16, 20], [16, 22], [18, 20],
    // Body
    [11, 23], [12, 24], [23, 24],
    // Legs
    [23, 25], [24, 26], [25, 27], [26, 28], [27, 29], [28, 30], [29, 31], [30, 32], [27, 31], [28, 32]
  ];

  useEffect(() => {
    const initializeMediaPipe = async () => {
      try {
        // โหลด MediaPipe จาก CDN
        if (!window.Pose) {
          // โหลด scripts จาก CDN
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/camera_utils/camera_utils.js');
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/control_utils/control_utils.js');
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/drawing_utils/drawing_utils.js');
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/pose/pose.js');
        }

        const poseInstance = new window.Pose({
          locateFile: (file) => {
            return `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`;
          }
        });

        poseInstance.setOptions({
          modelComplexity: 2, // เพิ่มความแม่นยำ
          smoothLandmarks: true,
          enableSegmentation: false,
          smoothSegmentation: true,
          minDetectionConfidence: 0.7, // เพิ่มความแม่นยำในการตรวจจับ
          minTrackingConfidence: 0.7 // เพิ่มความแม่นยำในการติดตาม
        });

        poseInstance.onResults(onResults);
        setPose(poseInstance);
        setIsInitialized(true);
      } catch (error) {
        console.error('Failed to load MediaPipe:', error);
      }
    };

    initializeMediaPipe();
  }, []);

  // Cleanup effect
  useEffect(() => {
    return () => {
      if (pose) {
        pose.close();
      }
    };
  }, [pose]);

  // Helper function to load scripts
  const loadScript = (src) => {
    return new Promise((resolve, reject) => {
      if (document.querySelector(`script[src="${src}"]`)) {
        resolve();
        return;
      }
      const script = document.createElement('script');
      script.src = src;
      script.onload = resolve;
      script.onerror = reject;
      document.head.appendChild(script);
    });
  };

  useEffect(() => {
    if (!isInitialized || !pose || !videoRef?.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    
    const processFrame = async () => {
      if (video.paused || video.ended || !isPlaying) return;
      
      try {
        // Ensure canvas size matches video element exactly
        const rect = video.getBoundingClientRect();
        const currentCanvasWidth = canvas.width;
        const currentCanvasHeight = canvas.height;
        
        if (currentCanvasWidth !== Math.floor(rect.width) || currentCanvasHeight !== Math.floor(rect.height)) {
          canvas.width = Math.floor(rect.width);
          canvas.height = Math.floor(rect.height);
          console.log('Canvas resized to:', canvas.width, 'x', canvas.height);
        }
        
        // Make sure video is ready before processing
        if (video.readyState >= video.HAVE_CURRENT_DATA && video.videoWidth > 0 && video.videoHeight > 0) {
          await pose.send({ image: video });
        }
      } catch (error) {
        console.error('Pose detection error:', error);
      }

      if (isPlaying && !video.paused && !video.ended) {
        requestAnimationFrame(processFrame);
      }
    };

    if (isPlaying) {
      if (video.readyState >= 2) {
        processFrame();
      } else {
        video.addEventListener('loadeddata', processFrame, { once: true });
      }
    }

    // Clean up canvas when not playing
    if (!isPlaying) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
  }, [isPlaying, isInitialized, pose]);

  // Temporal smoothing function
  const smoothLandmarks = (currentLandmarks, previousLandmarks, alpha = 0.3) => {
    if (!previousLandmarks) return currentLandmarks;
    
    return currentLandmarks.map((landmark, index) => {
      const prev = previousLandmarks[index];
      if (!prev || landmark.visibility < 0.5) return landmark;
      
      return {
        x: alpha * landmark.x + (1 - alpha) * prev.x,
        y: alpha * landmark.y + (1 - alpha) * prev.y,
        z: alpha * landmark.z + (1 - alpha) * prev.z,
        visibility: Math.max(landmark.visibility, prev.visibility * 0.9)
      };
    });
  };

  const onResults = (results) => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    if (!canvas || !video || !results.poseLandmarks) return;

    try {
      // Apply temporal smoothing
      const smoothed = smoothLandmarks(results.poseLandmarks, previousLandmarks);
      setSmoothedLandmarks(smoothed);
      setPreviousLandmarks(smoothed);
      
      drawPoseMediaPipe(smoothed, canvas, video);
      
      if (onKeypointsDetected) {
        onKeypointsDetected(smoothed);
      }
    } catch (error) {
      console.error('Drawing error:', error);
    }
  };

  const drawPoseMediaPipe = (landmarks, canvas, video) => {
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Make sure canvas matches video element size
    const videoRect = video.getBoundingClientRect();
    canvas.width = videoRect.width;
    canvas.height = videoRect.height;

    // Get video dimensions
    const videoWidth = video.videoWidth || 640;
    const videoHeight = video.videoHeight || 480;
    const canvasWidth = canvas.width;
    const canvasHeight = canvas.height;

    console.log('Video dimensions:', videoWidth, 'x', videoHeight);
    console.log('Canvas dimensions:', canvasWidth, 'x', canvasHeight);

    // Calculate scale factors for proper normalization
    // MediaPipe landmarks are normalized (0-1) relative to video dimensions
    let scaleX, scaleY, offsetX = 0, offsetY = 0;

    if (video.style.objectFit === 'contain' || video.style.objectFit === '') {
      // For object-fit: contain, we need to calculate the actual video display area
      const videoAspect = videoWidth / videoHeight;
      const canvasAspect = canvasWidth / canvasHeight;

      if (videoAspect > canvasAspect) {
        // Video is wider - fit to width, letterbox top/bottom
        scaleX = canvasWidth;
        scaleY = canvasWidth / videoAspect;
        offsetY = (canvasHeight - scaleY) / 2;
      } else {
        // Video is taller - fit to height, letterbox left/right  
        scaleY = canvasHeight;
        scaleX = canvasHeight * videoAspect;
        offsetX = (canvasWidth - scaleX) / 2;
      }
    } else {
      // For object-fit: cover or other modes, scale to full canvas
      scaleX = canvasWidth;
      scaleY = canvasHeight;
    }

    console.log('Scale factors:', scaleX, scaleY, 'Offsets:', offsetX, offsetY);

    // Draw skeleton connections with better visibility
    if (showConnections) {
      // Shadow/glow effect for better visibility
      ctx.shadowColor = 'rgba(34, 197, 94, 0.5)';
      ctx.shadowBlur = 5;
      
      POSE_CONNECTIONS.forEach(([i, j]) => {
        const kp1 = landmarks[i];
        const kp2 = landmarks[j];
        
        if (kp1 && kp2 && kp1.visibility > 0.6 && kp2.visibility > 0.6) { // เพิ่ม threshold
          // MediaPipe coordinates are normalized (0-1), need to scale properly
          const x1 = kp1.x * scaleX + offsetX;
          const y1 = kp1.y * scaleY + offsetY;
          const x2 = kp2.x * scaleX + offsetX;
          const y2 = kp2.y * scaleY + offsetY;
          
          ctx.beginPath();
          ctx.moveTo(x1, y1);
          ctx.lineTo(x2, y2);
          
          // Different green shades for different body parts
          if (i <= 10 || j <= 10) {
            ctx.strokeStyle = '#15803d'; // Dark green for face/head
          } else if ((i >= 11 && i <= 16) || (j >= 11 && j <= 16)) {
            ctx.strokeStyle = '#16a34a'; // Medium green for arms
          } else {
            ctx.strokeStyle = '#22c55e'; // Light green for body/legs
          }
          
          ctx.lineWidth = connectionWidth;
          ctx.lineCap = 'round';
          ctx.stroke();
        }
      });
      
      // Reset shadow
      ctx.shadowColor = 'transparent';
      ctx.shadowBlur = 0;
    }

    // Draw keypoints with green theme
    if (showKeypoints) {
      landmarks.forEach((landmark, index) => {
        if (landmark && landmark.visibility > 0.6) { // เพิ่ม threshold สำหรับความแม่นยำ
          // Ensure proper normalization - MediaPipe gives 0-1 coordinates
          const normalizedX = Math.max(0, Math.min(1, landmark.x));
          const normalizedY = Math.max(0, Math.min(1, landmark.y));
          
          const x = normalizedX * scaleX + offsetX;
          const y = normalizedY * scaleY + offsetY;
          
          // Shadow/glow effect
          ctx.shadowColor = 'rgba(34, 197, 94, 0.8)';
          ctx.shadowBlur = 8;
          
          // Main keypoint circle
          ctx.beginPath();
          ctx.arc(x, y, keypointSize, 0, 2 * Math.PI);
          
          // Different green shades for different body parts
          if (index <= 10) {
            ctx.fillStyle = '#15803d'; // Dark green for face/head
          } else if (index >= 11 && index <= 16) {
            ctx.fillStyle = '#16a34a'; // Medium green for arms
          } else if (index >= 17 && index <= 22) {
            ctx.fillStyle = '#22c55e'; // Light green for hands
          } else {
            ctx.fillStyle = '#4ade80'; // Bright green for body/legs
          }
          
          ctx.fill();
          
          // Reset shadow
          ctx.shadowColor = 'transparent';
          ctx.shadowBlur = 0;
          
          // White border for better contrast
          ctx.beginPath();
          ctx.arc(x, y, keypointSize, 0, 2 * Math.PI);
          ctx.strokeStyle = '#ffffff';
          ctx.lineWidth = 2;
          ctx.stroke();
          
          // Inner bright dot for core joints
          if ([11, 12, 23, 24, 13, 14, 15, 16, 25, 26, 27, 28].includes(index)) {
            ctx.beginPath();
            ctx.arc(x, y, 2, 0, 2 * Math.PI);
            ctx.fillStyle = '#dcfce7'; // Very light green
            ctx.fill();
          }
        }
      });
    }
  };

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 10
      }}
    />
  );
};

export default KeypointOverlay;
