import React, { useEffect, useRef } from 'react';
import './GoogleAuthButton.css';

const GoogleAuthButton = ({ onSuccess, onError, isLoading }) => {
  const clientInitialized = useRef(false);


  useEffect(() => {
    if (!window.google || !window.google.accounts || !window.google.accounts.id) return;
    if (clientInitialized.current) return;
    window.google.accounts.id.initialize({
      client_id: process.env.REACT_APP_GOOGLE_CLIENT_ID || 'YOUR_GOOGLE_CLIENT_ID',
      callback: (response) => {
        if (response && response.credential) {
          onSuccess({ credential: response.credential });
        } else {
          onError && onError();
        }
      },
      ux_mode: 'popup',
      login_uri: window.location.origin + '/google-login-popup', // fallback page if popup blocked
    });
    clientInitialized.current = true;
  }, [onSuccess, onError]);

  const handleCustomGoogleLogin = () => {
    if (!window.google || !window.google.accounts || !window.google.accounts.id) {
      alert('Google API not loaded');
      return;
    }
    window.google.accounts.id.prompt();
    // fallback: open Google login in new window if prompt doesn't show
    setTimeout(() => {
      const popupShown = document.querySelector('iframe[title^="Sign in with Google"]');
      if (!popupShown) {
        window.open(
          `https://accounts.google.com/gsi/select?client_id=${encodeURIComponent(process.env.REACT_APP_GOOGLE_CLIENT_ID || 'YOUR_GOOGLE_CLIENT_ID')}&ux_mode=popup`,
          '_blank',
          'width=500,height=600'
        );
      }
    }, 1200);
  };

  return (
    <div className="google-auth-button">
      <button
        className="google-auth-btn"
        onClick={handleCustomGoogleLogin}
        disabled={isLoading}
        type="button"
        aria-label="Continue with Google"
      >
        <img src="/googlelogo.png" alt="Google" className="google-icon" />
        <span className="google-text">Continue with Google</span>
      </button>
    </div>
  );
};

export default GoogleAuthButton;
