import React from 'react';
import { GoogleLogin } from '@react-oauth/google';
import './GoogleAuthButton.css';

const GoogleAuthButton = ({ onSuccess, onError, isLoading, type = "login" }) => {
  return (
    <div className="google-auth-button">
      <GoogleLogin
        onSuccess={credentialResponse => onSuccess(credentialResponse)}
        onError={onError}
        useOneTap
        text="signin_with"
        shape="pill"
        theme="filled_blue"
        width={320}
        locale="th"
        logo_alignment="center"
        render={renderProps => (
          <button
            className="google-auth-btn-custom"
            onClick={renderProps.onClick}
            disabled={isLoading || renderProps.disabled}
          >
            <img src="/google-icon.png" alt="Google" style={{width: 24, height: 24, marginRight: 12, verticalAlign: 'middle'}} />
            ลงชื่อเข้าใช้ด้วย Google
          </button>
        )}
      />
    </div>
  );
};

export default GoogleAuthButton;
