import React, { useRef, useEffect, useState } from 'react';

const KeypointOverlay = ({ videoRef, isPlaying, onKeypointsDetected }) => {
  const canvasRef = useRef(null);
  const [isInitialized, setIsInitialized] = useState(false);
  const [pose, setPose] = useState(null);

  // MediaPipe Pose connections (33 landmarks)
  const POSE_CONNECTIONS = [
    // Face
    [0, 1], [1, 2], [2, 3], [3, 7], [0, 4], [4, 5], [5, 6], [6, 8],
    // Arms
    [9, 10], [11, 12], [11, 13], [13, 15], [15, 17], [15, 19], [15, 21], [17, 19],
    [12, 14], [14, 16], [16, 18], [16, 20], [16, 22], [18, 20],
    // Body
    [11, 23], [12, 24], [23, 24],
    // Legs
    [23, 25], [24, 26], [25, 27], [26, 28], [27, 29], [28, 30], [29, 31], [30, 32], [27, 31], [28, 32]
  ];

  useEffect(() => {
    const initializeMediaPipe = async () => {
      try {
        // ใช้ MediaPipe จาก CDN ที่รองรับ UMD format
        if (!window.Pose) {
          // โหลด MediaPipe scripts แบบ UMD
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/camera_utils@0.3.1640029074/camera_utils.js');
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/control_utils@0.6.1629159505/control_utils.js');
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/drawing_utils@0.3.1620248257/drawing_utils.js');
          await loadScript('https://cdn.jsdelivr.net/npm/@mediapipe/pose@0.5.1635989137/pose.js');
        }

        const poseInstance = new window.Pose({
          locateFile: (file) => {
            return `https://unpkg.com/@mediapipe/pose/${file}`;
          }
        });

        poseInstance.setOptions({
          modelComplexity: 1,
          smoothLandmarks: true,
          enableSegmentation: false,
          smoothSegmentation: true,
          minDetectionConfidence: 0.5,
          minTrackingConfidence: 0.5
        });

        poseInstance.onResults(onResults);
        
        // Test if MediaPipe is working properly
        await new Promise((resolve, reject) => {
          const timeout = setTimeout(() => reject(new Error('MediaPipe initialization timeout')), 5000);
          
          // Set up a test callback
          const originalOnResults = onResults;
          poseInstance.onResults((results) => {
            clearTimeout(timeout);
            poseInstance.onResults(originalOnResults);
            resolve();
          });
          
          // Initialize with a simple test
          setPose(poseInstance);
        });
        
        setIsInitialized(true);
        console.log('MediaPipe Pose initialized successfully');
      } catch (error) {
        console.error('Failed to load MediaPipe:', error);
        // Try fallback with different CDN
        await initializeMediaPipeFallback();
      }
    };

    const initializeMediaPipeFallback = async () => {
      try {
        console.log('Trying MediaPipe fallback...');
        
        // ลองใช้ CDN อื่น
        if (!window.Pose) {
          await loadScript('https://cdn.skypack.dev/@mediapipe/pose');
        }
        
        if (window.Pose) {
          const poseInstance = new window.Pose({
            locateFile: (file) => {
              return `https://cdn.skypack.dev/@mediapipe/pose/${file}`;
            }
          });

          poseInstance.setOptions({
            modelComplexity: 0, // ลดความซับซ้อนสำหรับ fallback
            smoothLandmarks: false,
            enableSegmentation: false,
            minDetectionConfidence: 0.3,
            minTrackingConfidence: 0.3
          });

          poseInstance.onResults(onResults);
          setPose(poseInstance);
          setIsInitialized(true);
          console.log('MediaPipe fallback initialized');
        } else {
          throw new Error('MediaPipe not available');
        }
      } catch (fallbackError) {
        console.error('MediaPipe fallback also failed:', fallbackError);
        // ใช้ setTimeout เพื่อ retry
        setTimeout(() => {
          console.log('Retrying MediaPipe initialization...');
          initializeMediaPipe();
        }, 2000);
      }
    };

    initializeMediaPipe();
  }, []);

  // Cleanup effect
  useEffect(() => {
    return () => {
      if (pose) {
        pose.close();
      }
    };
  }, [pose]);

  // Helper function to load scripts with better CORS handling
  const loadScript = (src) => {
    return new Promise((resolve, reject) => {
      // Check if script already exists
      const existingScript = document.querySelector(`script[src="${src}"]`);
      if (existingScript) {
        resolve();
        return;
      }
      
      const script = document.createElement('script');
      script.src = src;
      script.crossOrigin = 'anonymous'; // Add CORS attribute
      script.onload = resolve;
      script.onerror = (error) => {
        console.error('Script loading failed:', src, error);
        reject(error);
      };
      
      // Add timeout
      const timeout = setTimeout(() => {
        script.remove();
        reject(new Error(`Script loading timeout: ${src}`));
      }, 10000);
      
      script.onload = () => {
        clearTimeout(timeout);
        resolve();
      };
      
      document.head.appendChild(script);
    });
  };

  useEffect(() => {
    if (!isInitialized || !pose || !videoRef?.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    
    let isProcessing = false;
    let lastProcessTime = 0;
    const targetFPS = 15; // Limit to 15 FPS for better performance
    const frameInterval = 1000 / targetFPS;
    
    const processFrame = async () => {
      // Skip if already processing or not playing
      if (isProcessing || video.paused || video.ended || !isPlaying) {
        if (isPlaying && !video.paused && !video.ended) {
          requestAnimationFrame(processFrame);
        }
        return;
      }
      
      // Throttle frame processing
      const now = Date.now();
      if (now - lastProcessTime < frameInterval) {
        requestAnimationFrame(processFrame);
        return;
      }
      
      isProcessing = true;
      lastProcessTime = now;
      
      try {
        // Match canvas size to video element's display size exactly
        const rect = video.getBoundingClientRect();
        if (canvas.width !== rect.width || canvas.height !== rect.height) {
          canvas.width = rect.width;
          canvas.height = rect.height;
        }
        
        // Make sure video is ready and has valid dimensions
        if (video.readyState >= video.HAVE_CURRENT_DATA && 
            (video.videoWidth > 0 || video.srcObject)) {
          // Send frame to MediaPipe with timeout
          const sendPromise = pose.send({ image: video });
          const timeoutPromise = new Promise((_, reject) => 
            setTimeout(() => reject(new Error('MediaPipe processing timeout')), 1000)
          );
          
          await Promise.race([sendPromise, timeoutPromise]);
        }
      } catch (error) {
        console.error('Pose detection error:', error);
      } finally {
        isProcessing = false;
      }

      // Continue processing if still playing
      if (isPlaying && !video.paused && !video.ended) {
        requestAnimationFrame(processFrame);
      }
    };

    if (isPlaying) {
      // Start processing for camera stream or loaded video
      if (video.readyState >= 2 || video.srcObject) {
        console.log('Starting pose detection...');
        processFrame();
      } else {
        console.log('Waiting for video to load...');
        video.addEventListener('loadeddata', () => {
          console.log('Video loaded, starting pose detection...');
          processFrame();
        }, { once: true });
      }
    }

    // Clean up canvas when not playing
    if (!isPlaying) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }

    // Cleanup function
    return () => {
      isProcessing = false;
    };
  }, [isPlaying, isInitialized, pose]);

  const onResults = (results) => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    
    if (!canvas || !video) return;
    
    // Clear previous drawings
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    
    if (!results.poseLandmarks || results.poseLandmarks.length === 0) {
      return;
    }

    try {
      drawPoseMediaPipe(results.poseLandmarks, canvas, video);
      
      if (onKeypointsDetected) {
        // Always mirror the keypoints to match the CSS mirrored video display
        const mirroredLandmarks = results.poseLandmarks.map(landmark => ({
          ...landmark,
          x: 1 - landmark.x  // Mirror X coordinate to match video transform
        }));
        onKeypointsDetected(mirroredLandmarks);
      }
    } catch (error) {
      console.error('Drawing error:', error);
    }
  };

  const drawPoseMediaPipe = (landmarks, canvas, video) => {
    const ctx = canvas.getContext('2d');
    // Canvas is already cleared in onResults

    // Get actual video dimensions and display dimensions
    const videoRect = video.getBoundingClientRect();
    const videoWidth = video.videoWidth;
    const videoHeight = video.videoHeight;
    const displayWidth = videoRect.width;
    const displayHeight = videoRect.height;

    // Calculate aspect ratios
    const videoAspect = videoWidth / videoHeight;
    const displayAspect = displayWidth / displayHeight;

    // Calculate actual video display area within the video element
    let actualVideoWidth, actualVideoHeight, offsetX = 0, offsetY = 0;

    if (videoAspect > displayAspect) {
      // Video is wider - letterboxed top/bottom
      actualVideoWidth = displayWidth;
      actualVideoHeight = displayWidth / videoAspect;
      offsetY = (displayHeight - actualVideoHeight) / 2;
    } else {
      // Video is taller - letterboxed left/right
      actualVideoHeight = displayHeight;
      actualVideoWidth = displayHeight * videoAspect;
      offsetX = (displayWidth - actualVideoWidth) / 2;
    }

    // MediaPipe landmarks are normalized (0-1), scale to actual video area
    const scaleX = actualVideoWidth;
    const scaleY = actualVideoHeight;

    // Note: Video has CSS transform: scaleX(-1) to mirror the display
    // So we need to mirror the keypoints coordinates to match
    
    // Draw skeleton connections
    POSE_CONNECTIONS.forEach(([i, j]) => {
      const kp1 = landmarks[i];
      const kp2 = landmarks[j];
      
      if (kp1 && kp2 && kp1.visibility > 0.5 && kp2.visibility > 0.5) {
        // Always mirror X coordinates to match the CSS transform: scaleX(-1) on video
        const x1 = (1 - kp1.x) * scaleX + offsetX;
        const y1 = kp1.y * scaleY + offsetY;
        const x2 = (1 - kp2.x) * scaleX + offsetX;
        const y2 = kp2.y * scaleY + offsetY;
        
        ctx.beginPath();
        ctx.moveTo(x1, y1);
        ctx.lineTo(x2, y2);
        ctx.strokeStyle = '#00FF00';
        ctx.lineWidth = 2;
        ctx.stroke();
      }
    });

    // Draw keypoints with different colors
    landmarks.forEach((landmark, index) => {
      if (landmark && landmark.visibility > 0.5) {
        // Always mirror X coordinates to match the CSS transform: scaleX(-1) on video
        const x = (1 - landmark.x) * scaleX + offsetX;
        const y = landmark.y * scaleY + offsetY;
        
        ctx.beginPath();
        ctx.arc(x, y, 3, 0, 2 * Math.PI);
        
        // Different colors for different body parts
        if (index <= 10) {
          ctx.fillStyle = '#FF6B6B'; // Face and upper body
        } else if (index <= 16) {
          ctx.fillStyle = '#4ECDC4'; // Arms
        } else if (index <= 22) {
          ctx.fillStyle = '#45B7D1'; // Hands
        } else {
          ctx.fillStyle = '#96CEB4'; // Legs and body
        }
        
        ctx.fill();
        
        // Add white border for better visibility
        ctx.beginPath();
        ctx.arc(x, y, 3, 0, 2 * Math.PI);
        ctx.strokeStyle = '#FFFFFF';
        ctx.lineWidth = 1;
        ctx.stroke();
      }
    });
  };

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        zIndex: 10
      }}
    />
  );
};

