      if (response.ok) {
        const data = await response.json();
        const content = data.candidates[0].content.parts[0].text;
        
        // ลองแยก JSON จากข้อความ
        const jsonMatch = content.match(/\{[\s\S]*\}/);
        if (jsonMatch) {
