  // แปลง keypoints เป็นรูปแบบที่เข้าใจง่าย
  simplifyKeypoints(keypoints) {
    const getPoint = (jointName) => {
      const point = keypoints[jointName];
      return point && point.visibility > this.MIN_VISIBILITY ? 
        { x: point.x, y: point.y } : { x: 0, y: 0 };
    };

    return {
      leftShoulder: getPoint('left_shoulder'),
      rightShoulder: getPoint('right_shoulder'),
      leftElbow: getPoint('left_elbow'),
      rightElbow: getPoint('right_elbow'),
      leftWrist: getPoint('left_wrist'),
      rightWrist: getPoint('right_wrist'),
      leftHip: getPoint('left_hip'),
      rightHip: getPoint('right_hip'),
      leftKnee: getPoint('left_knee'),
      rightKnee: getPoint('right_knee'),
      leftAnkle: getPoint('left_ankle'),
      rightAnkle: getPoint('right_ankle')
    };
  }

  // เปรียบเทียบท่าด้วย OpenAI (backup)
  async analyzeWithOpenAI(trainerKeypoints, traineeKeypoints) {
    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
        },
        body: JSON.stringify({
          model: "gpt-3.5-turbo",
          messages: [{
            role: "system",
            content: "คุณเป็นผู้เชี่ยวชาญด้านการวิเคราะห์ท่าออกกำลังกาย ให้คะแนนความถูกต้องของท่าโดยเปรียบเทียบ keypoints"
          }, {
            role: "user",
            content: `วิเคราะห์ท่าออกกำลังกาย:
            Trainer keypoints: ${JSON.stringify(trainerKeypoints)}
            Trainee keypoints: ${JSON.stringify(traineeKeypoints)}
            
            ให้คะแนนเป็น JSON format:
            {
              "accuracy": 0-100,
              "feedback": "คำแนะนำภาษาไทย",
              "recommendations": ["คำแนะนำ1", "คำแนะนำ2"]
            }`
          }],
          max_tokens: 500,
          temperature: 0.3
        })
      });

      if (response.ok) {
        const data = await response.json();
        const aiResult = JSON.parse(data.choices[0].message.content);
        return aiResult;
      }
    } catch (error) {
      console.log('OpenAI API error:', error);
    }
    
    return null;
  }

  // เรียกใช้ AI โดยลองใช้ Gemini ก่อน แล้วค่อย fallback เป็น OpenAI
  async analyzeWithAI(trainerKeypoints, traineeKeypoints) {
    // ลอง Gemini ก่อน (ฟรี)
    if (process.env.REACT_APP_GEMINI_API_KEY && process.env.REACT_APP_GEMINI_API_KEY !== 'ใส่_GEMINI_API_KEY_ตรงนี้') {
      const geminiResult = await this.analyzeWithGemini(trainerKeypoints, traineeKeypoints);
      if (geminiResult) {
        return { ...geminiResult, aiProvider: 'Gemini' };
      }
    }

    // ถ้า Gemini ไม่ได้ ลอง OpenAI
    if (process.env.REACT_APP_OPENAI_API_KEY && process.env.REACT_APP_OPENAI_API_KEY !== 'ใส่_OPENAI_API_KEY_ตรงนี้') {
      const openaiResult = await this.analyzeWithOpenAI(trainerKeypoints, traineeKeypoints);
      if (openaiResult) {
        return { ...openaiResult, aiProvider: 'OpenAI' };
      }
    }

    // ไม่มี AI ไหนทำงาน
    return null;
  }

  // คำนวนความแม่นยำแบบดั้งเดิม
  calculateTraditionalAccuracy(trainerKeypoints, traineeKeypoints) {
    let totalScore = 0;
    let validJoints = 0;
    const jointComparisons = {};

    for (const joint of this.IMPORTANT_JOINTS) {
      const trainerJoint = trainerKeypoints[joint];
      const traineeJoint = traineeKeypoints[joint];

      if (trainerJoint && traineeJoint && 
          trainerJoint.visibility > this.MIN_VISIBILITY && 
          traineeJoint.visibility > this.MIN_VISIBILITY) {
        
        // คำนวนระยะห่างระหว่างจุด
        const distance = this.calculateDistance(trainerJoint, traineeJoint);
        
        // แปลงเป็นคะแนน (ยิ่งใกล้ยิ่งคะแนนสูง)
        const maxDistance = 0.3; // ระยะห่างสูงสุดที่ยอมรับได้
        const jointScore = Math.max(0, 100 - (distance / maxDistance) * 100);
        
        totalScore += jointScore;
        validJoints++;
        
        jointComparisons[joint] = {
          distance: distance,
          score: Math.round(jointScore),
          trainerPosition: { x: trainerJoint.x, y: trainerJoint.y },
          traineePosition: { x: traineeJoint.x, y: traineeJoint.y }
        };
      }
    }

    return {
      accuracy: validJoints > 0 ? Math.round(totalScore / validJoints) : 0,
      jointComparisons: jointComparisons,
      validJoints: validJoints
    };
  }

  async compareMultiDirectional(trainerPose, traineePose) {
    if (!trainerPose || !traineePose) {
      return { 
        accuracy: 0,
        confidence: 0,
        feedback: "กรุณาเริ่มทำท่าตามคลิป",
        jointComparisons: {},
        trainerDirection: "unknown",
        traineeDirection: "unknown",
        bodyPartScores: {},
        recommendations: []
      };
    }

    // คำนวนแบบดั้งเดิมก่อน
    const traditionalResult = this.calculateTraditionalAccuracy(trainerPose, traineePose);
    
    // เรียก AI มาวิเคราะห์เพิ่มเติม
    const aiResult = await this.analyzeWithAI(trainerPose, traineePose);
    
    // รวมผลลัพธ์ โดยให้น้ำหนัก AI มากกว่า
    let finalAccuracy = traditionalResult.accuracy;
    let feedback = "ปรับปรุงท่าให้ตรงมากขึ้น";
    let recommendations = ["ปรับท่าให้ตรงกับคลิปมากขึ้น"];
    let bodyPartScores = this.calculateBodyPartScores(traditionalResult.jointComparisons);

    if (aiResult) {
      // ให้น้ำหนัก AI 70% และ Traditional 30%
      finalAccuracy = Math.round((aiResult.accuracy * 0.7) + (traditionalResult.accuracy * 0.3));
      feedback = aiResult.feedback || feedback;
      recommendations = aiResult.recommendations || recommendations;
      
      // ใช้คะแนนแยกส่วนจาก AI ถ้ามี
      if (aiResult.detailedAnalysis) {
        bodyPartScores = {
          arms: aiResult.detailedAnalysis.armAccuracy || bodyPartScores.arms,
          legs: aiResult.detailedAnalysis.legAccuracy || bodyPartScores.legs,
          torso: aiResult.detailedAnalysis.torsoAccuracy || bodyPartScores.torso,
          overallPosture: aiResult.detailedAnalysis.overallPosture || finalAccuracy
        };
      }
    }

    return { 
      accuracy: finalAccuracy,
      confidence: traditionalResult.validJoints > 8 ? 90 : 70,
      feedback: feedback,
      jointComparisons: traditionalResult.jointComparisons,
      trainerDirection: "front",
      traineeDirection: "front",
      bodyPartScores: bodyPartScores,
      recommendations: recommendations,
      aiAnalyzed: !!aiResult,
      aiProvider: aiResult?.aiProvider || null,
      traditionalScore: traditionalResult.accuracy,
      aiScore: aiResult?.accuracy || null
    };
  }

  calculateBodyPartScores(jointComparisons) {
    const armJoints = ['left_shoulder', 'right_shoulder', 'left_elbow', 'right_elbow', 'left_wrist', 'right_wrist'];
    const legJoints = ['left_hip', 'right_hip', 'left_knee', 'right_knee', 'left_ankle', 'right_ankle'];
    const torsoJoints = ['nose', 'left_shoulder', 'right_shoulder', 'left_hip', 'right_hip'];

    const calculatePartScore = (joints) => {
      let total = 0;
      let count = 0;
      joints.forEach(joint => {
        if (jointComparisons[joint]) {
          total += jointComparisons[joint].score;
          count++;
        }
      });
      return count > 0 ? Math.round(total / count) : 0;
    };

    return {
      arms: calculatePartScore(armJoints),
      legs: calculatePartScore(legJoints),
      torso: calculatePartScore(torsoJoints)
    };
  }
}

